import React, { useState, useEffect } from 'react';
import api, { deleteData, postData, putData } from '../../api/endpoint'; // ✅ CORRIGÉ
import { FiActivity, FiSearch, FiPlus, FiEdit, FiTrash2, FiEye } from 'react-icons/fi';

const Consultations = () => {
  const [consultations, setConsultations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [selectedConsultation, setSelectedConsultation] = useState(null);
  const [formData, setFormData] = useState({
    dossierMedicalId: '',
    patientId: '',
    medecinId: '',
    motif: '',
    symptomes: [],
    diagnostic: '',
    recommandations: '',
    examensDemandes: [],
    constantes: {
      temperature: '',
      tension: '',
      poids: ''
    },
    statut: 'EN_COURS'
  });
  const [newSymptome, setNewSymptome] = useState('');
  const [newExamen, setNewExamen] = useState('');
  const [patients, setPatients] = useState([]);
  const [medecins, setMedecins] = useState([]);
  const [dossiers, setDossiers] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      setError(null);
      const [consultationsData, usersData, dossiersData] = await Promise.all([
        api.getData('/consultations'), // ✅ CORRIGÉ
        api.getData('/users'), 
        api.getData('/dossiers')
      ]);
      setConsultations(consultationsData);
      setPatients(usersData.filter(u => u.role === 'Patient'));
      setMedecins(usersData.filter(u => u.role === 'Medecin'));
      setDossiers(dossiersData);
    } catch (err) {
      console.error('Erreur lors du chargement:', err);
      setError('Impossible de charger les données.');
    } finally {
      setLoading(false);
    }
  };

  const handleView = (consultation) => {
    setSelectedConsultation(consultation);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette consultation ?')) return;
    try {
      await deleteData(`/consultations/${id}`); // ✅ ENDPOINT CORRIGÉ
      alert('Consultation supprimée avec succès');
      loadData();
    } catch (err) {
      alert(err.message || 'Erreur lors de la suppression');
    }
  };

  const handleOpenForm = (consultation = null) => {
    if (consultation) {
      setFormData({
        dossierMedicalId: consultation.dossierMedicalId || '',
        patientId: consultation.patientId?._id || consultation.patientId || '',
        medecinId: consultation.medecinId?._id || consultation.medecinId || '',
        motif: consultation.motif || '',
        symptomes: consultation.symptomes || [],
        diagnostic: consultation.diagnostic || '',
        recommandations: consultation.recommandations || '',
        examensDemandes: consultation.examensDemandes || [],
        constantes: consultation.constantes || { temperature: '', tension: '', poids: '' },
        statut: consultation.statut || 'EN_COURS'
      });
      setSelectedConsultation(consultation);
    } else {
      setFormData({
        dossierMedicalId: '',
        patientId: '',
        medecinId: '',
        motif: '',
        symptomes: [],
        diagnostic: '',
        recommandations: '',
        examensDemandes: [],
        constantes: { temperature: '', tension: '', poids: '' },
        statut: 'EN_COURS'
      });
      setSelectedConsultation(null);
    }
    setShowForm(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedConsultation) {
        await putData(`/consultations/${selectedConsultation._id}`, formData); // ✅ ENDPOINT CORRIGÉ
        alert('Consultation mise à jour avec succès');
      } else {
        await postData('/consultations', formData); // ✅ ENDPOINT CORRIGÉ
        alert('Consultation créée avec succès');
      }
      setShowForm(false);
      loadData();
    } catch (err) {
      alert(err.message || 'Erreur lors de la sauvegarde');
    }
  };

  // ✅ RESTE DU CODE IDENTIQUE (addSymptome, removeSymptome, etc.)
  const addSymptome = () => {
    if (newSymptome.trim()) {
      setFormData(prev => ({
        ...prev,
        symptomes: [...prev.symptomes, newSymptome.trim()]
      }));
      setNewSymptome('');
    }
  };

  const removeSymptome = (index) => {
    setFormData(prev => ({
      ...prev,
      symptomes: prev.symptomes.filter((_, i) => i !== index)
    }));
  };

  const addExamen = () => {
    if (newExamen.trim()) {
      setFormData(prev => ({
        ...prev,
        examensDemandes: [...prev.examensDemandes, newExamen.trim()]
      }));
      setNewExamen('');
    }
  };

  const removeExamen = (index) => {
    setFormData(prev => ({
      ...prev,
      examensDemandes: prev.examensDemandes.filter((_, i) => i !== index)
    }));
  };

  const filteredConsultations = consultations.filter(cons =>
    cons.motif?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    cons.patientId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    cons.medecinId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getStatusColor = (statut) => {
    switch (statut) {
      case 'TERMINEE': return 'bg-green-100 text-green-800';
      case 'EN_COURS': return 'bg-yellow-100 text-yellow-800';
      case 'ANNULEE': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return <div className="flex items-center justify-center h-64"><div>Chargement...</div></div>;
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center text-red-600">
          <h3>Erreur</h3>
          <p>{error}</p>
          <button onClick={loadData} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
            Réessayer
          </button>
        </div>
      </div>
    );
  }

  // ✅ TOUT LE RESTE DU JSX IDENTIQUE (tableau, modal, formulaire)
  // ... (code JSX identique à votre version originale)
  
  return (
    <div>
      {/* HEADER ET RECHERCHE IDENTIQUES */}
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Gestion des Consultations</h2>
          <p className="text-gray-600 mt-2">Gérez toutes les consultations médicales</p>
        </div>
        <button
          onClick={() => handleOpenForm()}
          className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-all font-medium flex items-center"
        >
          <FiPlus className="mr-2" /> Nouvelle consultation
        </button>
      </div>

      {/* BARRE DE RECHERCHE */}
      <div className="mb-6">
        <div className="relative">
          <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher une consultation..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      {/* TOUT LE TABLEAU ET MODALS IDENTIQUES À VOTRE VERSION */}
      {/* ... (votre JSX existant pour le tableau, modal, formulaire) */}
    </div>
  );
};

export default Consultations;
