import React, { useState, useEffect } from 'react';
import getData from '../../api/endpoint';
import { FiHeart, FiCalendar, FiUser, FiThermometer } from 'react-icons/fi';

const MesConsultations = () => {
  const [consultations, setConsultations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadConsultations();
  }, []);

  const loadConsultations = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      if (token) {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        const data = await getData('/consultations');
        const patientConsultations = data.filter(cons => cons.patientId === decoded.userId);
        setConsultations(patientConsultations);
      }
    } catch (err) {
      console.error('Erreur lors du chargement des consultations:', err);
      setError('Impossible de charger les consultations.');
      setConsultations([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (statut) => {
    switch (statut) {
      case 'TERMINEE': return 'bg-green-100 text-green-800';
      case 'EN_COURS': return 'bg-yellow-100 text-yellow-800';
      case 'ANNULEE': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-600">Chargement...</div>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Mes Consultations</h2>
        <p className="text-gray-600 mt-2">Historique de vos consultations médicales</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {consultations.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
          <FiHeart size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">Aucune consultation</h3>
          <p className="text-gray-600">Vous n'avez pas encore de consultations médicales enregistrées.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6">
          {consultations.map((consultation) => (
            <div key={consultation._id} className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100 hover:shadow-xl transition-all">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FiHeart className="text-red-500 mr-2" size={24} />
                  <h3 className="text-xl font-bold text-gray-900">{consultation.motif || 'Consultation médicale'}</h3>
                </div>
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(consultation.statut)}`}>
                  {consultation.statut || 'EN_COURS'}
                </span>
              </div>
              
              <div className="space-y-3 text-gray-600 mb-4">
                <div className="flex items-center">
                  <FiCalendar className="mr-2" size={16} />
                  <span>{new Date(consultation.dateConsultation || consultation.createdAt).toLocaleDateString('fr-FR', { 
                    weekday: 'long', 
                    year: 'numeric', 
                    month: 'long', 
                    day: 'numeric'
                  })}</span>
                </div>
                
                {consultation.medecinId && (
                  <div className="flex items-center">
                    <FiUser className="mr-2" size={16} />
                    <span>Médecin: {consultation.medecinId.fullName || 'Non spécifié'}</span>
                  </div>
                )}
              </div>

              {consultation.constantes && (
                <div className="mb-4 p-4 bg-gray-50 rounded-lg">
                  <h4 className="font-semibold text-gray-900 mb-2 flex items-center">
                    <FiThermometer className="mr-2" size={16} />
                    Constantes
                  </h4>
                  <div className="grid grid-cols-3 gap-4 text-sm">
                    {consultation.constantes.temperature && (
                      <div>
                        <span className="text-gray-600">Température:</span>
                        <span className="font-semibold ml-2">{consultation.constantes.temperature}°C</span>
                      </div>
                    )}
                    {consultation.constantes.tension && (
                      <div>
                        <span className="text-gray-600">Tension:</span>
                        <span className="font-semibold ml-2">{consultation.constantes.tension}</span>
                      </div>
                    )}
                    {consultation.constantes.poids && (
                      <div>
                        <span className="text-gray-600">Poids:</span>
                        <span className="font-semibold ml-2">{consultation.constantes.poids} kg</span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {consultation.symptomes && consultation.symptomes.length > 0 && (
                <div className="mb-4">
                  <h4 className="font-semibold text-gray-900 mb-2">Symptômes:</h4>
                  <ul className="flex flex-wrap gap-2">
                    {consultation.symptomes.map((symptome, index) => (
                      <li key={index} className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
                        {symptome}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {consultation.diagnostic && (
                <div className="mb-4 p-4 bg-blue-50 rounded-lg">
                  <h4 className="font-semibold text-gray-900 mb-2">Diagnostic:</h4>
                  <p className="text-gray-700">{consultation.diagnostic}</p>
                </div>
              )}

              {consultation.recommandations && (
                <div className="p-4 bg-green-50 rounded-lg">
                  <h4 className="font-semibold text-gray-900 mb-2">Recommandations:</h4>
                  <p className="text-gray-700">{consultation.recommandations}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesConsultations;
