import React, { useState, useEffect, useCallback } from 'react';
import { Link, Routes, Route, useLocation, useParams, useNavigate } from 'react-router-dom';
import api from '../../api/endpoint'; // Votre API endpoint

const MedecinDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState({
    patients: 0,
    dossiers: 0,
    rdvs: 0,
    consultations: 0
  });
  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);
  const [patientsList, setPatientsList] = useState([]);

  // Charger toutes les donnÃ©es dynamiquement
  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);

      // Profil mÃ©decin (API rÃ©elle)
      const profileRes = await api.getData('/medecin/profile');
      setProfile(profileRes);

      // Stats rÃ©elles
      const statsRes = await Promise.all([
        api.getData('/patients/medecin/count'),
        api.getData('/dossiers/medecin/count'),
        api.getData('/rendezvous/medecin/count'),
        api.getData('/consultations/medecin/count')
      ]);
      setStats({
        patients: statsRes[0] || 0,
        dossiers: statsRes[1] || 0,
        rdvs: statsRes[2] || 0,
        consultations: statsRes[3] || 0
      });

      // Liste patients pour formulaire
      const patientsRes = await api.getData('/patients/medecin?limit=50');
      setPatientsList(patientsRes || []);

      setLoading(false);
    } catch (error) {
      console.error('Erreur chargement dashboard:', error);
      // Fallback mock data
      setProfile({
        nom: 'NDIAYE', prenom: 'Moustapha', specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001', etablissement: 'CHU de Fann'
      });
      setStats({ patients: 24, dossiers: 18, rdvs: 5, consultations: 12 });
      setPatientsList([
        { _id: '1', fullName: 'Aminata DIOP', email: 'aminata@test.com' },
        { _id: '2', fullName: 'Fatou SOW', email: 'fatou@test.com' }
      ]);
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadDashboardData();
  }, [loadDashboardData]);

  const addNotification = (message, type = 'success') => {
    const notif = { id: Date.now(), message, type, date: new Date().toLocaleTimeString('fr-FR') };
    setNotifications(prev => [notif, ...prev.slice(0, 4)]);
    setTimeout(() => setNotifications(prev => prev.filter(n => n.id !== notif.id)), 5000);
  };

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <Notifications notifications={notifications} />
      
      <aside className="hidden lg:block lg:w-72 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white/90 backdrop-blur-xl shadow-2xl border-r border-blue-100">
        <ProfileCard profile={profile} />
        <NavigationMenu activePath={location.pathname} stats={stats} />
      </aside>

      <main className="lg:ml-72 p-4 lg:p-8 pb-24 lg:pb-8">
        <Routes>
          <Route path="/" element={<DashboardHome stats={stats} profile={profile} addNotification={addNotification} />} />
          <Route path="/patients" element={<PatientsPageStub stats={stats} />} />
          <Route path="/patients/:id" element={<PatientDetailStub />} />
          <Route path="/dossiers" element={<DossiersPageStub stats={stats} />} />
          <Route path="/dossier/:id" element={<DossierDetailStub />} />
          <Route path="/creer-dossier" element={<CreerDossierForm patients={patientsList} addNotification={addNotification} navigate={navigate} />} />
          <Route path="/consultations" element={<ConsultationsPageStub stats={stats} />} />
          <Route path="/rendezvous" element={<RendezVousPageStub stats={stats} />} />
          <Route path="/ordonnances" element={<OrdonnancesPageStub stats={stats} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>
    </div>
  );
};

// FORMULAIRE CRÃ‰ATION DOSSIER COMPLET âœ…
const CreerDossierForm = ({ patients, addNotification, navigate }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: { nom: '', telephone: '', lien: '' },
    statut: 'ACTIF'
  });
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
