import React, { useState, useEffect } from 'react';
import getData, { deleteData, postData, putData } from '../../api/endpoint';
import { FiActivity, FiSearch, FiPlus, FiEdit, FiTrash2, FiEye } from 'react-icons/fi';

const Consultations = () => {
  const [consultations, setConsultations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [selectedConsultation, setSelectedConsultation] = useState(null);
  const [formData, setFormData] = useState({
    dossierMedicalId: '',
    patientId: '',
    medecinId: '',
    motif: '',
    symptomes: [],
    diagnostic: '',
    recommandations: '',
    examensDemandes: [],
    constantes: {
      temperature: '',
      tension: '',
      poids: ''
    },
    statut: 'EN_COURS'
  });
  const [newSymptome, setNewSymptome] = useState('');
  const [newExamen, setNewExamen] = useState('');
  const [patients, setPatients] = useState([]);
  const [medecins, setMedecins] = useState([]);
  const [dossiers, setDossiers] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [consultationsData, usersData, dossiersData] = await Promise.all([
        getData('/consultations'),
        getData('/users'),
        getData('/dossiers')
      ]);
      setConsultations(consultationsData);
      setPatients(usersData.filter(u => u.role === 'Patient'));
      setMedecins(usersData.filter(u => u.role === 'Medecin'));
      setDossiers(dossiersData);
    } catch (err) {
      console.error('Erreur lors du chargement:', err);
      setError('Impossible de charger les données.');
    } finally {
      setLoading(false);
    }
  };

  const handleView = (consultation) => {
    setSelectedConsultation(consultation);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette consultation ?')) return;
    try {
      await deleteData(`/consultation/id?id=${id}`);
      alert('Consultation supprimée avec succès');
      loadData();
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const handleOpenForm = (consultation = null) => {
    if (consultation) {
      setFormData({
        dossierMedicalId: consultation.dossierMedicalId || '',
        patientId: consultation.patientId?._id || consultation.patientId || '',
        medecinId: consultation.medecinId?._id || consultation.medecinId || '',
        motif: consultation.motif || '',
        symptomes: consultation.symptomes || [],
        diagnostic: consultation.diagnostic || '',
        recommandations: consultation.recommandations || '',
        examensDemandes: consultation.examensDemandes || [],
        constantes: consultation.constantes || { temperature: '', tension: '', poids: '' },
        statut: consultation.statut || 'EN_COURS'
      });
      setSelectedConsultation(consultation);
    } else {
      setFormData({
        dossierMedicalId: '',
        patientId: '',
        medecinId: '',
        motif: '',
        symptomes: [],
        diagnostic: '',
        recommandations: '',
        examensDemandes: [],
        constantes: { temperature: '', tension: '', poids: '' },
        statut: 'EN_COURS'
      });
      setSelectedConsultation(null);
    }
    setShowForm(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedConsultation) {
        await putData(`/consultation/id?id=${selectedConsultation._id}`, formData);
        alert('Consultation mise à jour avec succès');
      } else {
        await postData('/new-consultation', formData);
        alert('Consultation créée avec succès');
      }
      setShowForm(false);
      loadData();
    } catch (err) {
      alert('Erreur lors de la sauvegarde');
    }
  };

  const addSymptome = () => {
    if (newSymptome.trim()) {
      setFormData(prev => ({
        ...prev,
        symptomes: [...prev.symptomes, newSymptome.trim()]
      }));
      setNewSymptome('');
    }
  };

  const removeSymptome = (index) => {
    setFormData(prev => ({
      ...prev,
      symptomes: prev.symptomes.filter((_, i) => i !== index)
    }));
  };

  const addExamen = () => {
    if (newExamen.trim()) {
      setFormData(prev => ({
        ...prev,
        examensDemandes: [...prev.examensDemandes, newExamen.trim()]
      }));
      setNewExamen('');
    }
  };

  const removeExamen = (index) => {
    setFormData(prev => ({
      ...prev,
      examensDemandes: prev.examensDemandes.filter((_, i) => i !== index)
    }));
  };

  const filteredConsultations = consultations.filter(cons =>
    cons.motif?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    cons.patientId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    cons.medecinId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getStatusColor = (statut) => {
    switch (statut) {
      case 'TERMINEE': return 'bg-green-100 text-green-800';
      case 'EN_COURS': return 'bg-yellow-100 text-yellow-800';
      case 'ANNULEE': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return <div className="flex items-center justify-center h-64"><div>Chargement...</div></div>;
  }

  return (
    <div>
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Gestion des Consultations</h2>
          <p className="text-gray-600 mt-2">Gérez toutes les consultations médicales</p>
        </div>
        <button
          onClick={() => handleOpenForm()}
          className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-all font-medium flex items-center"
        >
          <FiPlus className="mr-2" /> Nouvelle consultation
        </button>
      </div>

      {/* Barre de recherche */}
      <div className="mb-6">
        <div className="relative">
          <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher une consultation..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      {/* Tableau */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Patient</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Médecin</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Motif</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Statut</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {filteredConsultations.map((consultation) => (
              <tr key={consultation._id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {new Date(consultation.dateConsultation || consultation.createdAt).toLocaleDateString('fr-FR')}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {consultation.patientId?.fullName || consultation.patientId || 'N/A'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {consultation.medecinId?.fullName || consultation.medecinId || 'N/A'}
                </td>
                <td className="px-6 py-4 text-sm text-gray-900">{consultation.motif || 'N/A'}</td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(consultation.statut)}`}>
                    {consultation.statut || 'EN_COURS'}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <div className="flex gap-2">
                    <button onClick={() => handleView(consultation)} className="text-blue-600 hover:text-blue-900">
                      <FiEye size={18} />
                    </button>
                    <button onClick={() => handleOpenForm(consultation)} className="text-green-600 hover:text-green-900">
                      <FiEdit size={18} />
                    </button>
                    <button onClick={() => handleDelete(consultation._id)} className="text-red-600 hover:text-red-900">
                      <FiTrash2 size={18} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal de détails */}
      {showModal && selectedConsultation && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-2xl font-bold mb-4">Détails de la Consultation</h3>
            <div className="space-y-4">
              <div><strong>Date:</strong> {new Date(selectedConsultation.dateConsultation || selectedConsultation.createdAt).toLocaleString('fr-FR')}</div>
              <div><strong>Patient:</strong> {selectedConsultation.patientId?.fullName || selectedConsultation.patientId}</div>
              <div><strong>Médecin:</strong> {selectedConsultation.medecinId?.fullName || selectedConsultation.medecinId}</div>
              <div><strong>Motif:</strong> {selectedConsultation.motif}</div>
              {selectedConsultation.symptomes?.length > 0 && (
                <div><strong>Symptômes:</strong> {selectedConsultation.symptomes.join(', ')}</div>
              )}
              {selectedConsultation.diagnostic && (
                <div><strong>Diagnostic:</strong> {selectedConsultation.diagnostic}</div>
              )}
              {selectedConsultation.recommandations && (
                <div><strong>Recommandations:</strong> {selectedConsultation.recommandations}</div>
              )}
              {selectedConsultation.constantes && (
                <div>
                  <strong>Constantes:</strong>
                  <ul className="ml-4">
                    {selectedConsultation.constantes.temperature && <li>Température: {selectedConsultation.constantes.temperature}°C</li>}
                    {selectedConsultation.constantes.tension && <li>Tension: {selectedConsultation.constantes.tension}</li>}
                    {selectedConsultation.constantes.poids && <li>Poids: {selectedConsultation.constantes.poids} kg</li>}
                  </ul>
                </div>
              )}
            </div>
            <button onClick={() => setShowModal(false)} className="mt-6 bg-gray-500 text-white px-6 py-2 rounded-lg">Fermer</button>
          </div>
        </div>
      )}

      {/* Formulaire */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-3xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-2xl font-bold mb-4">{selectedConsultation ? 'Modifier' : 'Nouvelle'} Consultation</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Patient *</label>
                  <select
                    value={formData.patientId}
                    onChange={(e) => setFormData({ ...formData, patientId: e.target.value })}
                    required
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">Sélectionner</option>
                    {patients.map(p => <option key={p._id} value={p._id}>{p.fullName}</option>)}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Médecin *</label>
                  <select
                    value={formData.medecinId}
                    onChange={(e) => setFormData({ ...formData, medecinId: e.target.value })}
                    required
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">Sélectionner</option>
                    {medecins.map(m => <option key={m._id} value={m._id}>Dr. {m.fullName}</option>)}
                  </select>
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Dossier Médical ID</label>
                <input
                  type="text"
                  value={formData.dossierMedicalId}
                  onChange={(e) => setFormData({ ...formData, dossierMedicalId: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Motif *</label>
                <input
                  type="text"
                  value={formData.motif}
                  onChange={(e) => setFormData({ ...formData, motif: e.target.value })}
                  required
                  className="w-full px-4 py-2 border rounded-lg"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Symptômes</label>
                <div className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={newSymptome}
                    onChange={(e) => setNewSymptome(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addSymptome())}
                    className="flex-1 px-4 py-2 border rounded-lg"
                    placeholder="Ajouter un symptôme"
                  />
                  <button type="button" onClick={addSymptome} className="bg-blue-500 text-white px-4 py-2 rounded-lg">Ajouter</button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {formData.symptomes.map((s, i) => (
                    <span key={i} className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full flex items-center gap-2">
                      {s}
                      <button type="button" onClick={() => removeSymptome(i)} className="text-red-600">×</button>
                    </span>
                  ))}
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Diagnostic</label>
                <textarea
                  value={formData.diagnostic}
                  onChange={(e) => setFormData({ ...formData, diagnostic: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                  rows={3}
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Recommandations</label>
                <textarea
                  value={formData.recommandations}
                  onChange={(e) => setFormData({ ...formData, recommandations: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                  rows={3}
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Examens demandés</label>
                <div className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={newExamen}
                    onChange={(e) => setNewExamen(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addExamen())}
                    className="flex-1 px-4 py-2 border rounded-lg"
                    placeholder="Ajouter un examen"
                  />
                  <button type="button" onClick={addExamen} className="bg-blue-500 text-white px-4 py-2 rounded-lg">Ajouter</button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {formData.examensDemandes.map((e, i) => (
                    <span key={i} className="bg-green-100 text-green-800 px-3 py-1 rounded-full flex items-center gap-2">
                      {e}
                      <button type="button" onClick={() => removeExamen(i)} className="text-red-600">×</button>
                    </span>
                  ))}
                </div>
              </div>
              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Température (°C)</label>
                  <input
                    type="number"
                    value={formData.constantes.temperature}
                    onChange={(e) => setFormData({ ...formData, constantes: { ...formData.constantes, temperature: e.target.value } })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Tension</label>
                  <input
                    type="text"
                    value={formData.constantes.tension}
                    onChange={(e) => setFormData({ ...formData, constantes: { ...formData.constantes, tension: e.target.value } })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Poids (kg)</label>
                  <input
                    type="number"
                    value={formData.constantes.poids}
                    onChange={(e) => setFormData({ ...formData, constantes: { ...formData.constantes, poids: e.target.value } })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Statut</label>
                <select
                  value={formData.statut}
                  onChange={(e) => setFormData({ ...formData, statut: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                >
                  <option value="EN_COURS">En cours</option>
                  <option value="TERMINEE">Terminée</option>
                  <option value="ANNULEE">Annulée</option>
                </select>
              </div>
              <div className="flex gap-4">
                <button type="submit" className="flex-1 bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600">Enregistrer</button>
                <button type="button" onClick={() => setShowForm(false)} className="px-6 py-3 border rounded-lg">Annuler</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Consultations;
