import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

// ‚úÖ Instance Axios avec token
const api = axios.create({ baseURL: 'http://localhost:5000/api' });
api.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  config.headers['Content-Type'] = 'application/json';
  return config;
});

const CreateDossier = () => {
  const [formData, setFormData] = useState({ patientId: '', motif: '', description: '' });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [existingDossierId, setExistingDossierId] = useState(null);

  // ‚úÖ Animation CSS inject√©e
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes shake { 0%,100% { transform: translateX(0);} 25% {transform: translateX(-8px);} 75% {transform: translateX(8px);} }
      @keyframes pulse { 0%,100% { box-shadow: 0 0 20px rgba(239,68,68,0.5);} 50% { box-shadow: 0 0 40px rgba(239,68,68,1);} }
    `;
    document.head.appendChild(style);
  }, []);

  // ‚úÖ R√©cup√©rer la liste des patients
  const fetchPatients = useCallback(async () => {
    try {
      setLoadingPatients(true);
      const response = await api.get('/patients');
      setPatients(Array.isArray(response.data) ? response.data : []);
    } catch {
      setError('Impossible de charger les patients');
      setPatients([]);
    } finally {
      setLoadingPatients(false);
    }
  }, []);

  useEffect(() => { fetchPatients(); }, [fetchPatients]);

  // ‚úÖ S√©lectionner le patient choisi
  useEffect(() => {
    const patient = patients.find(p => p._id === formData.patientId);
    setSelectedPatient(patient || null);
  }, [formData.patientId, patients]);

  // ‚úÖ Gestion des changements de formulaire
  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setError('');
    setSuccess('');
    setExistingDossierId(null);
  }, []);

  // ‚úÖ Soumission du formulaire


const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    const token = localStorage.getItem('token');

    const response = await axios.post(
      'http://localhost:5000/api/new-dossier',
      {
        patientId,
        resumeMedical,
        groupeSanguin,
        antecedents,
        allergies,
        contactUrgence,
      },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );

    console.log('Dossier cr√©√© :', response.data);
    alert('‚úÖ Dossier cr√©√© avec succ√®s');

  } catch (error) {
    console.error('Erreur cr√©ation dossier :', error);

    if (error.response?.status === 401) {
      alert('üîê Non authentifi√©');
    } else if (error.response?.status === 403) {
      alert('‚õî Acc√®s refus√©');
    } else {
      alert('‚ùå Erreur serveur lors de la cr√©ation du dossier');
    }
  }
};

  return (
    <div className="create-dossier-container" style={{ padding: '30px', maxWidth: '800px', margin: '0 auto', fontFamily: '-apple-system, sans-serif' }}>
      <h1 style={{ color: '#1e293b', marginBottom: '30px', fontSize: '28px' }}>üìÅ Cr√©er Dossier M√©dical</h1>

      {/* SUCCESS */}
      {success && (
        <div className="success-box" style={{
          background: 'linear-gradient(135deg, #d1fae5 0%, #a7f3d0 100%)',
          color: '#065f46', padding: '24px', borderRadius: '16px',
          borderLeft: '6px solid #10b981', marginBottom: '24px', fontWeight: 600,
          fontSize: '16px', boxShadow: '0 10px 30px rgba(16,185,129,0.3)'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <span style={{ fontSize: '24px' }}>‚úÖ</span>
            <strong>{success}</strong>
            <button onClick={() => setSuccess('')} style={{ background: 'none', border: 'none', fontSize: '24px', cursor: 'pointer' }}>‚úï</button>
          </div>
        </div>
      )}

      {/* ERROR */}
      {error && (
        <div className="error-box" style={{
          position: 'sticky', top: 0, zIndex: 999,
          width: '100%', minHeight: '140px',
          background: '#fee2e2', border: '4px solid #ef4444',
          borderRadius: '20px', padding: '24px',
          marginBottom: '32px', fontSize: '18px', fontWeight: 'bold',
          animation: 'shake 0.8s infinite, pulse 1.5s infinite'
        }}>
          <div style={{ display: 'flex', gap: '16px', alignItems: 'flex-start' }}>
            <div style={{ fontSize: '36px', marginTop: '-4px' }}>üö®</div>
            <div style={{ flex: 1 }}>
              <div>{error}</div>
              {existingDossierId && (
                <div style={{ marginTop: '12px', padding: '16px', background: '#fef2f2', borderRadius: '12px', fontFamily: 'monospace', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <code style={{ fontSize: '20px', fontWeight: 'bold', color: '#dc2626' }}>{existingDossierId}</code>
                  {existingDossierId !== 'existant' && (
                    <button style={{
                      padding: '10px 20px', background: '#3b82f6', color: 'white',
                      border: 'none', borderRadius: '8px', fontSize: '14px', cursor: 'pointer'
                    }} onClick={() => window.open(`/dashboard/dossiers/${existingDossierId}`, '_blank')}>üëÅÔ∏è OUVRIR</button>
                  )}
                </div>
              )}
            </div>
            <button style={{
              width: '48px', height: '48px', borderRadius: '50%',
              background: '#dc2626', color: 'white', border: 'none',
              fontSize: '20px', cursor: 'pointer'
            }} onClick={() => { setError(''); setExistingDossierId(null); }}>‚úï</button>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Patient Select */}
        <div style={{ marginBottom: '24px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>üë§ Patient *</label>
          {loadingPatients ? (
            <div style={{ padding: '16px', background: '#f0fdfa', borderRadius: '10px', textAlign: 'center' }}>‚è≥ Chargement...</div>
          ) : patients.length === 0 ? (
            <div style={{ padding: '16px', background: '#fee2e2', borderRadius: '10px', textAlign: 'center' }}>
              ‚ùå Aucun patient
              <button type="button" onClick={fetchPatients} style={{ marginTop: '8px', padding: '6px 12px', background: '#3b82f6', color: 'white', border: 'none', borderRadius: '6px' }}>üîÑ Recharger</button>
            </div>
          ) : (
            <>
              <select name="patientId" value={formData.patientId} onChange={handleChange} required style={{ width: '100%', padding: '12px', borderRadius: '10px', fontSize: '16px', border: '2px solid #d1d5db' }}>
                <option value="">Choisir un patient ({patients.length})</option>
                {patients.map(p => <option key={p._id} value={p._id}>{p.nom?.toUpperCase()} {p.prenom ?? ''}</option>)}
              </select>
              {selectedPatient && <div style={{ marginTop: '8px', padding: '8px', background: '#d1fae5', borderRadius: '6px' }}>‚úÖ {selectedPatient.nom} {selectedPatient.prenom}</div>}
            </>
          )}
        </div>

        {/* Motif */}
        <div style={{ marginBottom: '16px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontWeight: 'bold' }}>üìã Motif *</label>
          <input name="motif" value={formData.motif} onChange={handleChange} required placeholder="Ex: palu, contr√¥le..." style={{ width: '100%', padding: '12px', borderRadius: '10px', border: '2px solid #d1d5db', fontSize: '16px' }}/>
        </div>

        {/* Description */}
        <div style={{ marginBottom: '24px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontWeight: 'bold' }}>üìù Description</label>
          <textarea name="description" value={formData.description} onChange={handleChange} rows={4} style={{ width: '100%', padding: '12px', borderRadius: '10px', border: '2px solid #d1d5db', fontSize: '16px', resize: 'vertical' }}/>
        </div>

        <button type="submit" disabled={loading || !formData.patientId || !formData.motif.trim()} style={{
          width: '100%', padding: '16px', background: loading ? '#9ca3af' : '#10b981', color: 'white', border: 'none', borderRadius: '12px', fontSize: '18px', fontWeight: 'bold', cursor: 'pointer'
        }}>
          {loading ? '‚è≥ Envoi...' : 'üöÄ Cr√©er Dossier'}
        </button>
      </form>
    </div>
  );
};

export default CreateDossier;
