import React, { useState, useEffect } from 'react';
import getData from '../../api/endpoint';
import { FiUsers, FiSearch } from 'react-icons/fi';
import { Link } from 'react-router-dom';

const MesPatients = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      setLoading(true);
      setError(null);
      const dossiers = await getData('/dossiers');
      const token = localStorage.getItem('token');
      if (token) {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        // Filtrer les dossiers où le médecin connecté est le médecin responsable
        const mesDossiers = dossiers.filter(d => d.medecinId === decoded.userId);
        // Extraire les IDs des patients
        const patientIds = [...new Set(mesDossiers.map(d => d.patientId))];
        // Récupérer les informations des patients
        const users = await getData('/users');
        const mesPatients = users.filter(u => patientIds.includes(u._id) && u.role === 'Patient');
        setPatients(mesPatients);
      }
    } catch (err) {
      console.error('Erreur lors du chargement des patients:', err);
      setError('Impossible de charger vos patients.');
      setPatients([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredPatients = patients.filter(patient =>
    patient.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-600">Chargement...</div>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Mes Patients</h2>
        <p className="text-gray-600 mt-2">Liste de vos patients</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {/* Barre de recherche */}
      <div className="mb-6">
        <div className="relative">
          <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher un patient..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>
      </div>

      {filteredPatients.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
          <FiUsers size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            {searchTerm ? 'Aucun patient trouvé' : 'Aucun patient'}
          </h3>
          <p className="text-gray-600">
            {searchTerm 
              ? 'Aucun patient ne correspond à votre recherche.' 
              : "Vous n'avez pas encore de patients assignés."}
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredPatients.map((patient) => (
            <div key={patient._id} className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100 hover:shadow-xl transition-all">
              <div className="flex items-center mb-4">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center mr-4">
                  <span className="text-white font-semibold text-lg">
                    {patient.fullName?.[0]?.toUpperCase() || patient.email?.[0]?.toUpperCase() || 'P'}
                  </span>
                </div>
                <div>
                  <h3 className="text-lg font-bold text-gray-900">{patient.fullName || 'Patient'}</h3>
                  <p className="text-sm text-gray-600">{patient.email}</p>
                </div>
              </div>
              
              <div className="space-y-2 text-sm text-gray-600 mb-4">
                {patient.date_of_birth && (
                  <p>Date de naissance: {new Date(patient.date_of_birth).toLocaleDateString('fr-FR')}</p>
                )}
                {patient.sexe && (
                  <p>Sexe: {patient.sexe === 'M' ? 'Masculin' : 'Féminin'}</p>
                )}
                <p>Statut: <span className={`font-semibold ${patient.statut === 'ACTIF' ? 'text-green-600' : 'text-red-600'}`}>
                  {patient.statut || 'ACTIF'}
                </span></p>
              </div>

              <Link
                to={`/medecin/dossiers?patientId=${patient._id}`}
                className="block w-full bg-blue-500 text-white text-center py-2 rounded-lg hover:bg-blue-600 transition-all font-medium"
              >
                Voir le dossier
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesPatients;
