import React, { useEffect, useState } from "react";
import { axiosInstance } from "../api/endpoint";

const MesDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchDossiers = async () => {
    try {
      setLoading(true);
      setError("");

      // Test : récupérer tous les dossiers (Admin ou pour debug)
      const response = await axiosInstance.get("/dossiers"); 
      // ⚠️ Si tu veux filtrer par médecin, utilise "/medecin/dossiers"

      console.log("Dossiers récupérés :", response.data);
      setDossiers(response.data);
    } catch (err) {
      console.error("Erreur récupération dossiers :", err);
      setError(err.response?.data?.message || "Erreur serveur");
    } finally {
      setLoading(false);
    }
  };


useEffect(() => {
  const fetchDossiers = async () => {
    try {
      console.log("Token décodé :", decodeToken(getAuthToken())); // debug
      const response = await axiosInstance.get("/medecin/dossiers");
      console.log("Dossiers récupérés :", response.data);
      setDossiers(response.data);
    } catch (err) {
      console.error("Erreur récupération dossiers :", err);
      alert(err.response?.data?.message || "Erreur serveur");
    }
  };

  fetchDossiers();
}, []);

  if (loading) return <p>Chargement des dossiers...</p>;
  if (error) return <p style={{ color: "red" }}>Erreur: {error}</p>;
  if (dossiers.length === 0) return <p>Aucun dossier trouvé.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Liste des Dossiers</h2>
      <table border="1" cellPadding="5" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>ID Dossier</th>
            <th>Patient</th>
            <th>Médecin</th>
            <th>Groupe Sanguin</th>
            <th>Résumé Médical</th>
          </tr>
        </thead>
        <tbody>
          {dossiers.map((dossier) => (
            <tr key={dossier._id}>
              <td>{dossier._id}</td>
              <td>{dossier.patientId?.fullName || "N/A"}</td>
              <td>{dossier.medecinId || "N/A"}</td>
              <td>{dossier.groupeSanguin || "N/A"}</td>
              <td>{dossier.resumeMedical || "N/A"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MesDossiers;
