import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../api/endpoint';
import { FiFileText, FiUser, FiSearch, FiEye } from 'react-icons/fi';

const MesDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadDossiers();
  }, []);

  const loadDossiers = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await api.getData('/dossiers/medecin');
      setDossiers(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Erreur dossiers:', err);
      setError(err.message || 'Impossible de charger les dossiers.');
      setDossiers([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredDossiers = dossiers.filter(dossier => {
    const patientName = dossier.patientId?.fullName || '';
    return patientName.toLowerCase().includes(searchTerm.toLowerCase());
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px] p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-lg text-gray-600">Chargement des dossiers...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* HEADER */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-12 gap-6">
        <div>
          <h1 className="text-4xl font-bold text-gray-900 flex items-center gap-4 mb-2">
            <FiFileText size={48} className="text-blue-600" />
            Mes Dossiers Médicaux
          </h1>
          <p className="text-xl text-gray-600">({filteredDossiers.length} dossiers trouvés)</p>
        </div>
        
        <div className="flex gap-4">
          <div className="relative flex-1 max-w-md">
            <FiSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Rechercher un patient..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-6 py-3 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
            />
          </div>
        </div>
      </div>

      {/* ERROR */}
      {error && (
        <div className="bg-red-50 border-2 border-red-200 text-red-800 px-6 py-4 rounded-2xl mb-8 shadow-lg">
          {error}
        </div>
      )}

      {/* TABLEAU DOSSIERS */}
      {filteredDossiers.length === 0 ? (
        <div className="text-center py-20 bg-white rounded-3xl shadow-lg">
          <FiFileText size={64} className="mx-auto mb-6 text-gray-400" />
          <h3 className="text-2xl font-bold text-gray-900 mb-2">Aucun dossier trouvé</h3>
          <p className="text-lg text-gray-600">
            {searchTerm ? 'Aucun dossier ne correspond à votre recherche.' : 'Vos dossiers apparaîtront ici.'}
          </p>
        </div>
      ) : (
        <div className="bg-white rounded-3xl shadow-2xl border border-gray-100 overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gradient-to-r from-gray-50 to-gray-100">
                <tr>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Patient</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Groupe Sanguin</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Statut</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Antécédents</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Allergies</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredDossiers.map((dossier) => (
                  <tr key={dossier._id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-8 py-6 font-semibold text-gray-900">
                      <div className="flex items-center gap-3">
                        <FiUser size={20} className="text-blue-600" />
                        <span>{dossier.patientId?.fullName || 'N/A'}</span>
                      </div>
                    </td>
                    <td className="px-8 py-6">
                      <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-semibold">
                        {dossier.groupeSanguin || 'N/A'}
                      </span>
                    </td>
                    <td className="px-8 py-6">
                      <span className={`px-4 py-2 rounded-full text-sm font-semibold ${
                        dossier.statut === 'ACTIF' 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-gray-100 text-gray-800'
                      }`}>
                        {dossier.statut}
                      </span>
                    </td>
                    <td className="px-8 py-6 text-gray-700">
                      {dossier.antecedents?.length || 0} antécédent(s)
                    </td>
                    <td className="px-8 py-6 text-gray-700">
                      <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                        dossier.allergies?.length > 0 
                          ? 'bg-red-100 text-red-800' 
                          : 'bg-gray-100 text-gray-600'
                      }`}>
                        {dossier.allergies?.length || 0} allergie(s)
                      </span>
                    </td>
                    <td className="px-8 py-6">
                      <Link
                        to={`/medecin/dossier/${dossier._id}`}
                        className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-all shadow-md"
                      >
                        <FiEye className="mr-2" size={16} />
                        Voir Dossier
                      </Link>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default MesDossiers;
