import React, { useEffect, useState } from "react";
import { axiosInstance } from "../api/endpoint";

const MesDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fonction pour récupérer les dossiers
  const fetchDossiers = async () => {
    try {
      setLoading(true);
      const res = await axiosInstance.get("/medecin/dossiers");
      setDossiers(res.data);
      setLoading(false);
    } catch (err) {
      console.error("Erreur récupération dossiers:", err);
      setError(err.response?.data?.message || "Erreur serveur");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDossiers();
  }, []);

  if (loading) return <p>Chargement des dossiers...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Mes Dossiers</h2>
      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead>
            <tr>
              <th>Patient</th>
              <th>Résumé Médical</th>
              <th>Groupe Sanguin</th>
              <th>Antécédents</th>
              <th>Allergies</th>
              <th>Contact Urgence</th>
              <th>Date de création</th>
            </tr>
          </thead>
          <tbody>
            {dossiers.map((dossier) => (
              <tr key={dossier._id}>
                <td>{dossier.patientId?.fullName || "N/A"}</td>
                <td>{dossier.resumeMedical}</td>
                <td>{dossier.groupeSanguin}</td>
                <td>
                  {dossier.antecedents?.length > 0
                    ? dossier.antecedents.map(a => a.libelle).join(", ")
                    : "-"}
                </td>
                <td>
                  {dossier.allergies?.length > 0
                    ? dossier.allergies.map(a => a.libelle).join(", ")
                    : "-"}
                </td>
                <td>
                  {dossier.contactUrgence
                    ? `${dossier.contactUrgence.nom} (${dossier.contactUrgence.telephone})`
                    : "-"}
                </td>
                <td>{new Date(dossier.createdAt).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default MesDossiers;
