import React, { useEffect, useState } from "react";
import { getData } from "../../api/endpoint";

const MyDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        const data = await getData("/patient/dossiers");
        setDossiers(data);
      } catch (err) {
        console.error("Erreur récupération dossiers :", err);
      } finally {
        setLoading(false);
      }
    };
    fetchDossiers();
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (!dossiers.length) return <div>Aucun dossier trouvé.</div>;

  return (
    <div>
      <h2>Mes dossiers</h2>
      <ul>
        {dossiers.map((d) => (
          <li key={d._id}>
            <strong>Médecin :</strong> {d.medecin.fullName} <br />
            <strong>Résumé médical :</strong> {d.resumeMedical} <br />
            <strong>Groupe sanguin :</strong> {d.groupeSanguin} <br />
            <strong>Antécédents :</strong> {d.antecedents?.map(a => a.libelle).join(", ")} <br />
            <strong>Allergies :</strong> {d.allergies?.map(a => a.libelle).join(", ")} <br />
            <strong>Contact urgence :</strong> {d.contactUrgence?.nom} - {d.contactUrgence?.telephone}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyDossiers;
