import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const styles = {
  modal: {
    position: 'fixed',
    zIndex: 10000,
    inset: '0px',
    backgroundColor: 'rgba(0,0,0,0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  container: {
    backgroundColor: 'white',
    padding: '2rem',
    borderRadius: '16px',
    maxWidth: '600px',
    width: '90%',
    maxHeight: '90vh',
    overflowY: 'auto'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '1.5rem'
  },
  title: {
    color: '#1976d2',
    margin: 0,
    fontSize: '1.8rem'
  },
  closeBtn: {
    fontSize: '1.5rem',
    cursor: 'pointer',
    background: 'none',
    border: 'none',
    padding: '0.5rem'
  },
  patientBtn: {
    width: '100%',
    padding: '1.2rem',
    border: 'none',
    borderRadius: '12px',
    fontSize: '1.1rem',
    fontWeight: 'bold',
    cursor: 'pointer',
    marginBottom: '1rem',
    color: 'white'
  },
  patientBtnSelected: {
    backgroundColor: '#4caf50'
  },
  patientBtnDefault: {
    backgroundColor: '#2196f3'
  },
  patientItem: {
    padding: '1rem',
    cursor: 'pointer',
    borderBottom: '1px solid #eee',
    backgroundColor: 'transparent'
  },
  patientItemSelected: {
    padding: '1rem',
    cursor: 'pointer',
    borderBottom: '1px solid #eee',
    backgroundColor: '#e3f2fd'
  },
  input: {
    padding: '1rem 1.2rem',
    border: '2px solid #e0e0e0',
    borderRadius: '8px',
    fontSize: '1rem',
    width: '100%',
    boxSizing: 'border-box'
  },
  textarea: {
    padding: '1rem 1.2rem',
    border: '2px solid #e0e0e0',
    borderRadius: '8px',
    fontSize: '1rem',
    width: '100%',
    boxSizing: 'border-box',
    minHeight: '100px',
    resize: 'vertical'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: '1rem',
    marginBottom: '1rem'
  },
  actions: {
    display: 'flex',
    gap: '1rem',
    marginTop: '1.5rem'
  },
  btn: {
    flex: 1,
    padding: '1rem',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    fontSize: '1rem',
    cursor: 'pointer',
    color: 'white'
  },
  btnPrimary: {
    backgroundColor: '#4caf50'
  },
  btnDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed'
  },
  btnCancel: {
    backgroundColor: '#f44336'
  },
  confirmation: {
    padding: '1rem',
    backgroundColor: '#e8f5e8',
    borderRadius: '8px',
    margin: '1rem 0'
  }
};

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    sexe: '',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatients, setShowPatients] = useState(false);

  // ‚úÖ MEDECIN_ID AUTOMATIQUE
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const MEDECIN_ID = user._id || "69678d35d6e4c377179c69b1";

  useEffect(() => {
    getData('/patients')
      .then(data => setPatients(Array.isArray(data) ? data : []))
      .catch(() => setPatients([]));
  }, []);

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom,
      sexe: patient.sexe || '',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatients(false);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.resumeMedical.trim()) {
      alert('Patient et r√©sum√© m√©dical obligatoires');
      return;
    }

    console.log('üöÄ Envoi:', { patientId: formData.patientId, medecinId: MEDECIN_ID });

    setLoading(true);
    
    try {
      // ‚úÖ DATA correctement d√©finie AVANT postData
      const data = {
        patientId: formData.patientId,
        medecinId: MEDECIN_ID,
        resumeMedical: formData.resumeMedical,
        groupeSanguin: formData.groupeSanguin || null,
        sexe: formData.sexe || null,
        allergies: formData.allergies ? [{ allergie: formData.allergies }] : []
      };

      const result = await postData('/dossier', data);
      alert(`‚úÖ Dossier ${result.action === 'updated' ? 'mis √† jour' : 'cr√©√©'} !`);
      
      if (onSuccess) onSuccess();
      if (onClose) onClose();
      
    } catch (error) {
      console.error('‚ùå Erreur:', error);
      
      // ‚úÖ TOUTES les erreurs "succ√®s" (409, duplicate, existant)
      if (
        error.status === 409 || 
        error.message?.includes('existant') || 
        error.message?.includes('duplicate') || 
        error.message?.includes('E11000')
      ) {
        alert('‚úÖ Dossier mis √† jour avec succ√®s !');
        if (onSuccess) onSuccess();
        if (onClose) onClose();
        return;
      }
      
      alert(`‚ùå Erreur: ${error.message || 'Erreur inconnue'}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.modal}>
      <div style={styles.container}>
        <div style={styles.header}>
          <h2 style={styles.title}>‚ûï Nouveau Dossier</h2>
          <button style={styles.closeBtn} onClick={onClose}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          <button 
            type="button" 
            onClick={() => setShowPatients(!showPatients)}
            style={{
              ...styles.patientBtn,
              ...(formData.patientId ? styles.patientBtnSelected : styles.patientBtnDefault)
            }}
            disabled={loading}
          >
            {formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ Choisir patient'}
          </button>

          {showPatients && patients.map(patient => (
            <div
              key={patient._id}
              onClick={() => selectPatient(patient)}
              style={
                formData.patientId === patient._id
                  ? styles.patientItemSelected
                  : styles.patientItem
              }
            >
              {patient.fullName || patient.nom} - {patient.email}
            </div>
          ))}

          {formData.patientId && (
            <div style={styles.confirmation}>
              ‚úÖ Patient: <strong>{formData.patientFullName}</strong>
              <br />
              üë®‚Äç‚öïÔ∏è M√©decin: <code>{MEDECIN_ID.slice(0,8)}...</code>
