// src/api/endpoint.js - VERSION ESLint 100% CORRIG√âE ‚úÖ
const BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:5000/api";

// =================== TOKEN MANAGEMENT ===================
const TOKEN_KEY = "token";
const getAuthToken = () => localStorage.getItem(TOKEN_KEY);
const setAuthToken = (token) => localStorage.setItem(TOKEN_KEY, token);
const removeAuthToken = () => localStorage.removeItem(TOKEN_KEY);

const decodeToken = (token) => {
  if (!token) return null;
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );
    return JSON.parse(jsonPayload);
  } catch {
    return null;
  }
};

const isTokenValid = (token) => {
  if (!token) return false;
  try {
    const decoded = decodeToken(token);
    if (!decoded || !decoded.exp) return false;
    return decoded.exp * 1000 > Date.now();
  } catch {
    return false;
  }
};

// =================== HELPERS ===================
const buildUrl = (endpoint) => {
  // ‚úÖ FIX SLASHES : √âvite double /api
  const cleanEndpoint = endpoint.startsWith('/') ? endpoint.slice(1) : endpoint;
  return `${BASE_URL}/${cleanEndpoint}`;
};

const getHeaders = (extraHeaders = {}) => {
  const token = getAuthToken();
  const headers = {
    'Content-Type': 'application/json',
    ...extraHeaders
  };
  
  if (token && isTokenValid(token)) {
    headers.Authorization = `Bearer ${token}`;
  }
  
  return headers;
};

// =================== GESTION DES ERREURS ===================
const handleApiError = async (response) => {
  const status = response.status;
  
  if (status === 401) {
    removeAuthToken();
    if (typeof window !== 'undefined' && window.location.pathname !== '/login') {
      window.location.href = '/login';
    }
    throw new Error('Session expir√©e. Redirection vers login...');
  }

  if (status === 403) {
    try {
      const errorText = await response.text();
      throw new Error(errorText || 'üîí Acc√®s refus√©');
    } catch {
      throw new Error('üîí Acc√®s refus√©');
    }
  }

  if (status === 404) {
    throw new Error('üìÅ Endpoint non trouv√©');
  }

  let errorData;
  try {
    errorData = await response.json();
  } catch {
    errorData = { message: `Erreur HTTP ${status}` };
  }

  console.error("üö® Erreur serveur:", { status, errorData });
  let errorMessage = errorData.message || `Erreur ${status}`;
  
  if (errorData.errorName === 'MongoServerError' && errorData.code === 11000) {
    errorMessage = '‚ö†Ô∏è Donn√©es en doublon';
  }
  
  if (errorData.validationErrors) {
    const errors = Object.values(errorData.validationErrors)
      .map(err => err.message)
      .join('; ');
    errorMessage = `Validation: ${errors}`;
  }
  
  throw new Error(errorMessage);
};

// =================== API METHODS ===================
const getData = async (endpoint) => {
  try {
    const url = buildUrl(endpoint);
    console.log('üåê GET:', url);
    
    const response = await fetch(url, { headers: getHeaders() });
    
    if (!response.ok) {
      throw await handleApiError(response);
    }
    
    const data = await response.json();
    console.log('‚úÖ Succ√®s:', data.length || 'OK');
    return data;
  } catch (error) {
    console.error("‚ùå GET failed:", error.message);
    throw error;
  }
};

const postData = async (endpoint, payload) => {
  try {
    const url = buildUrl(endpoint);
    console.log('üì§ POST:', url);
    
    const response = await fetch(url, {
      method: "POST",
      headers: getHeaders(),
      body: JSON.stringify(payload),
    });
    
    if (!response.ok) {
      throw await handleApiError(response);
    }
    
    return await response.json();
  } catch (error) {
    console.error("‚ùå POST failed:", error.message);
    throw error;
  }
};

const putData = async (endpoint, payload) => {
  try {
    const url = buildUrl(endpoint);
    const response = await fetch(url, {
      method: "PUT",
      headers: getHeaders(),
      body: JSON.stringify(payload),
    });
    
    if (!response.ok) {
      throw await handleApiError(response);
    }
    
    return await response.json();
  } catch (error) {
    console.error("‚ùå PUT failed:", error.message);
    throw error;
  }
};

const deleteData = async (endpoint) => {
  try {
    const url = buildUrl(endpoint);
    const response = await fetch(url, {
      method: "DELETE",
      headers: getHeaders(),
    });
    
    if (!response.ok) {
      throw await handleApiError(response);
    }
    
    return await response.json();
  } catch (error) {
    console.error("‚ùå DELETE failed:", error.message);
    throw error;
  }
};

// ‚úÖ UNE SEULE EXPORT - TOUT EST EXPORT√â ICI
export {
  TOKEN_KEY,
  getAuthToken,
  setAuthToken,
  removeAuthToken,
  decodeToken,
  isTokenValid,
  getData,
  postData,
  putData,
  deleteData
};

// Export par d√©faut
export default {
  getData,
  postData,
  putData,
  deleteData
};
