// src/pages/Composants/MyDossiers.jsx
import React, { useEffect, useState } from "react";
import { getData } from "../../api/endpoint";

const MyDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        const data = await getData("/patient/dossiers"); // API pour récupérer dossiers patient
        setDossiers(data);
      } catch (err) {
        console.error("Erreur chargement dossiers:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchDossiers();
  }, []);

  if (loading) return <p>Chargement des dossiers...</p>;
  if (dossiers.length === 0) return <p>Aucun dossier trouvé.</p>;

  return (
    <div>
      <h2>Mes Dossiers Médicaux</h2>
      <ul>
        {dossiers.map((dossier) => (
          <li key={dossier._id} style={{ marginBottom: "15px" }}>
            <strong>Résumé :</strong> {dossier.resumeMedical} <br />
            <strong>Groupe sanguin :</strong> {dossier.groupeSanguin}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyDossiers;
