              placeholder="Ex: 1cp 3x/jour" required />
          </div>
          <div>
            <label className="block text-lg font-semibold text-gray-700 mb-3">DurÃ©e</label>
            <input type="text" value={formData.duree} onChange={(e) => setFormData({...formData, duree: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500" 
              placeholder="Ex: 7 jours" required />
          </div>
        </div>
        <button type="submit" className="w-full bg-purple-600 text-white font-bold py-4 px-6 rounded-xl hover:bg-purple-700 focus:ring-4 focus:ring-purple-300 transition-all text-lg shadow-lg">
          âš•ï¸ Ã‰mettre l'ordonnance
        </button>
      </form>
    </div>
  );
};

