import React, { useState, useEffect, useCallback } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    patientEmail: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [showPatientsList, setShowPatientsList] = useState(false);
  const [error, setError] = useState('');

  // ‚úÖ CHARGEMENT PATIENTS R√âELS (avec fallback)
  const loadPatients = useCallback(async () => {
    setLoadingPatients(true);
    setError('');
    
    try {
      const data = await getData('/patients');
      const patientsList = Array.isArray(data) ? data : [];
      
      if (patientsList.length === 0) {
        setError('Aucun patient trouv√©. Cr√©ez-en d\'abord.');
      }
      
      setPatients(patientsList);
    } catch (err) {
      console.error('API patients √©chou√©e:', err);
      setError('Impossible de charger les patients');
      setPatients([]); // Pas de mock pour forcer vraies donn√©es
    } finally {
      setLoadingPatients(false);
    }
  }, []);

  useEffect(() => {
    loadPatients();
  }, [loadPatients]);

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id || patient.id || '',
      patientFullName: patient.fullName || patient.nom || patient.name || '',
      patientEmail: patient.email || '',
      sexe: patient.sexe || patient.gender || 'M',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatientsList(false);
    setError('');
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ ENVOI ULTRA-S√âCURIS√â (√©vite tous les crashes backend)
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId) {
      alert('‚ùå S√©lectionnez un patient');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // ‚úÖ FORMATAGE MAXIMUM S√âCURIT√â
      const safeData = {
        patientId: String(formData.patientId || ''),
        groupeSanguin: formData.groupeSanguin || null,
        resumeMedical: formData.resumeMedical || null,
        allergies: formData.allergies || null
      };

      // ‚úÖ SUPPRESSION valeurs undefined/null pour √©viter crash
      Object.keys(safeData).forEach(key => {
        if (safeData[key] === undefined || safeData[key] === null) {
          delete safeData[key];
        }
      });

      console.log('üì§ Envoi S√âCURIS√â:', safeData);

      // ‚úÖ TENTATIVE API R√âELLE
      const result = await postData('/dossier', safeData);
      console.log('‚úÖ API R√©ussie:', result);
      
      alert(`‚úÖ Dossier cr√©√© pour ${formData.patientFullName} !`);
      onSuccess();
      
    } catch (err) {
      console.error('‚ùå API √©chou√©e:', err);
      
      // ‚úÖ SAUVEGARDE LOCALE EN CAS D'√âCHEC API
      const dossierLocal = {
        _id: Date.now().toString(),
        patientId: formData.patientId,
        patientFullName: formData.patientFullName,
        groupeSanguin: formData.groupeSanguin,
        resumeMedical: formData.resumeMedical,
        allergies: formData.allergies,
        createdAt: new Date().toISOString(),
        status: 'local' // Marqu√© comme local
      };

      const dossiersLocaux = JSON.parse(localStorage.getItem('dossiersMedecin') || '[]');
      dossiersLocaux.unshift(dossierLocal); // Ajout en t√™te
      localStorage.setItem('dossiersMedecin', JSON.stringify(dossiersLocaux));

      console.log('üíæ Sauvegard√© localement:', dossierLocal);
      alert(`‚úÖ Dossier sauvegard√© pour ${formData.patientFullName} (localement)`);
      onSuccess();
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed', zIndex: 10000, top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center'
    }}>
      <div style={{
        background: 'white', padding: '2.5rem', borderRadius: '16px', maxWidth: '700px',
        width: '90%', maxHeight: '90vh', overflowY: 'auto', boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier</h2>
          <button onClick={onClose} style={{
            background: 'none', border: 'none', fontSize: '2rem', cursor: 'pointer', color: '#666'
          }}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* S√âLECTION PATIENT R√âEL */}
          <div style={{ marginBottom: '1.5rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientsList(!showPatientsList)}
              disabled={loading || loadingPatients}
              style={{
                width: '100%', padding: '1.2rem', 
                background: formData.patientId ? '#4caf50' : '#2196f3',
                color: 'white', border: 'none', borderRadius: '12px', 
                fontSize: '1.1rem', fontWeight: '600', cursor: 'pointer'
              }}
            >
              {loadingPatients ? '‚è≥ Chargement...' : 
               formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ Charger patients'}
            </button>
          </div>

          {/* LISTE PATIENTS R√âELS */}
          {showPatientsList && (
            <div style={{ 
              maxHeight: '300px', overflowY: 'auto', 
              border: '2px solid #e0e0e0', borderRadius: '12px', marginBottom: '1.5rem',
              background: '#f8f9fa'
            }}>
              {loadingPatients ? (
                <div style={{ padding: '2rem', textAlign: 'center' }}>
                  ‚è≥ Connexion base de donn√©es...
                </div>
              ) : patients.length === 0 ? (
                <div style={{ padding: '2rem', textAlign: 'center', color: '#666' }}>
                  {error || 'Aucun patient en base de donn√©es'}
                </div>
              ) : (
                patients.map(patient => (
                  <div 
                    key={patient._id || patient.id}
                    onClick={() => selectPatient(patient)}
                    style={{
                      padding: '1.2rem 1.5rem', cursor: 'pointer', borderBottom: '1px solid #eee',
                      display: 'flex', alignItems: 'center', gap: '1rem',
                      background: formData.patientId === (patient._id || patient.id) ? '#e3f2fd' : 'transparent'
                    }}
                  >
                    <div style={{ fontWeight: 'bold', fontSize: '1.1rem' }}>
                      {patient.fullName || patient.nom || patient.name}
                    </div>
                    <div style={{ color: '#666', flex: 1 }}>
                      {patient.email}
                    </div>
                  </div>
                ))
              )}
            </div>
          )}

          {/* CONFIRMATION */}
          {formData.patientId && (
            <div style={{ 
              padding: '1.5rem', background: '#e8f5e8', borderRadius: '12px', marginBottom: '2rem',
              borderLeft: '5px solid #4caf50'
            }}>
              ‚úÖ <strong>{formData.patientFullName}</strong> ({formData.patientEmail})
            </div>
          )}

          {/* CHAMPS M√âDICAUX */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem', marginBottom: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600' }}>
                ü©∏ Groupe sanguin
              </label>
              <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleInputChange} 
                style={inputStyle} required>
                <option value="">Choisir...</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600' }}>‚öïÔ∏è Sexe</label>
              <input value={formData.sexe === 'M' ? 'Masculin' : 'F√©minin'} readOnly style={readOnlyStyle} />
            </div>
          </div>

          <div style={{ marginBottom: '2rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600' }}>
              üìù R√©sum√© m√©dical *
            </label>
            <textarea name="resumeMedical" value={formData.resumeMedical} onChange={handleInputChange} 
              rows={3} required style={textAreaStyle} 
              placeholder="Diagnostic, ant√©c√©dents, traitements..." />
          </div>

          {/* BOUTONS */}
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={!formData.patientId || loading} style={{
              flex: 1, padding: '1.3rem', 
              background: formData.patientId ? '#4caf50' : '#ccc',
              color: 'white', border: 'none', borderRadius: '12px', fontSize: '1.1rem',
              fontWeight: 'bold', cursor: 'pointer'
            }}>
              {loading ? '‚è≥ Enregistrement...' : 'üíæ Cr√©er Dossier'}
            </button>
            <button type="button" onClick={onClose} style={{
              flex: 1, padding: '1.3rem', background: '#f44336', color: 'white',
              border: 'none', borderRadius: '12px', fontSize: '1.1rem', cursor: 'pointer'
            }}>
              ‚ùå Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  padding: '1rem 1.2rem', border: '2px solid #e0e0e0', borderRadius: '8px',
  fontSize: '1rem', width: '100%', fontFamily: 'inherit'
};
const readOnlyStyle = { ...inputStyle, background: '#f5f5f5' };
const textAreaStyle = { ...inputStyle, resize: 'vertical', minHeight: '100px' };

export default CreateDossierModal;
