import React, { useState, useEffect, useCallback } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    patientEmail: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [showPatientsList, setShowPatientsList] = useState(false);
  const [error, setError] = useState('');

  // ‚úÖ ID M√âDECIN FIXE (remplacez par le v√¥tre depuis MongoDB)
  const MEDECIN_ID = "66f4b1234567890abcdef456"; // ‚Üê COPIEZ VOTRE ID ICI !

  // Chargement patients r√©els
  const loadPatients = useCallback(async () => {
    try {
      setLoadingPatients(true);
      setError('');
      const data = await getData('/patients');
      setPatients(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Patients error:', err);
      setError('Impossible de charger les patients');
      setPatients([]);
    } finally {
      setLoadingPatients(false);
    }
  }, []);

  useEffect(() => {
    loadPatients();
  }, [loadPatients]);

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom || patient.name || '',
      patientEmail: patient.email || '',
      sexe: patient.sexe || 'M',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatientsList(false);
    setError('');
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ handleSubmit CORRIG√â - FORMAT EXACT BACKEND
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId) {
      alert('‚ùå S√©lectionnez un patient');
      return;
    }

    if (!MEDECIN_ID || MEDECIN_ID === "66f4b1234567890abcdef456") {
      alert('‚ùå Configurez votre MEDECIN_ID dans le code');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // ‚úÖ PAYLOAD EXACT pour votre backend
      const payload = {
        patientId: formData.patientId,
        medecinId: MEDECIN_ID,  // ‚Üê √áA R√âSOUT TOUT !
        resumeMedical: formData.resumeMedical || '',
        groupeSanguin: formData.groupeSanguin || '',
        antecedents: [],
        allergies: formData.allergies ? [{ allergie: formData.allergies }] : [],
        statut: 'ACTIF'
      };

      console.log('üì§ PAYLOAD ENVOY√â:', payload);
      
      const result = await postData('/dossier', payload);
      console.log('‚úÖ SUCC√àS:', result);
      
      alert(`‚úÖ Dossier cr√©√© pour ${formData.patientFullName} !`);
      onSuccess();
      
    } catch (err) {
      console.error('‚ùå ERREUR API:', err);
      
      // ‚úÖ Fallback localStorage
      const dossierLocal = {
        _id: `local_${Date.now()}`,
        patientId: formData.patientId,
        patientFullName: formData.patientFullName,
        medecinId: MEDECIN_ID,
        ...formData,
        createdAt: new Date().toISOString()
      };

      const dossiersLocaux = JSON.parse(localStorage.getItem('dossiersMedecin') || '[]');
      dossiersLocaux.unshift(dossierLocal);
      localStorage.setItem('dossiersMedecin', JSON.stringify(dossiersLocaux.slice(0, 100))); // Max 100

      console.log('üíæ Sauvegard√© localement:', dossierLocal);
      alert(`‚úÖ Dossier sauvegard√© localement pour ${formData.patientFullName}`);
      onSuccess();
    } finally {
      setLoading(false);
    }
  };

  const inputStyle = {
    padding: '1rem 1.2rem',
    border: '2px solid #e0e0e0',
    borderRadius: '8px',
    fontSize: '1rem',
    width: '100%',
    fontFamily: 'inherit'
  };

  const readOnlyStyle = { ...inputStyle, background: '#f5f5f5' };
  const textAreaStyle = { ...inputStyle, resize: 'vertical', minHeight: '100px' };

  return (
    <div style={{
      position: 'fixed', zIndex: 10000, inset: 0,
      background: 'rgba(0,0,0,0.6)',
      display: 'flex', alignItems: 'center', justifyContent: 'center',
      backdropFilter: 'blur(4px)'
    }}>
      <div style={{
        background: 'white',
        padding: '2.5rem',
        borderRadius: '20px',
        maxWidth: '700px',
        width: '90%',
        maxHeight: '90vh',
        overflowY: 'auto',
        boxShadow: '0 25px 70px rgba(0,0,0,0.4)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>
            ‚ûï Nouveau Dossier M√©dical
          </h2>
          <button 
            onClick={onClose}
            style={{
              background: 'none', border: 'none', fontSize: '2.2rem',
              cursor: 'pointer', color: '#666', width: '40px', height: '40px',
              borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center',
              transition: 'all 0.2s'
            }}
            onMouseEnter={(e) => e.target.style.background = '#f0f0f0'}
            onMouseLeave={(e) => e.target.style.background = 'none'}
          >
            √ó
          </button>
        </div>

        {error && (
          <div style={{
            padding: '1rem', background: '#ffebee', border: '1px solid #f44336',
            borderRadius: '8px', marginBottom: '1.5rem', color: '#c62828'
          }}>
            ‚ö†Ô∏è {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {/* S√âLECTION PATIENT */}
          <div style={{ marginBottom: '1.5rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientsList(!showPatientsList)}
              disabled={loading || loadingPatients}
              style={{
                width: '100%', padding: '1.2rem',
                background: formData.patientId ? '#4caf50' : '#2196f3',
                color: 'white', border: 'none', borderRadius: '12px',
                fontSize: '1.1rem', fontWeight: '600', cursor: 'pointer',
                opacity: loading || loadingPatients ? 0.7 : 1
              }}
            >
              {loadingPatients ? '‚è≥ Chargement patients...' :
               formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ S√©lectionner un patient'}
            </button>
          </div>

          {/* LISTE PATIENTS */}
          {showPatientsList && (
            <div style={{ 
              maxHeight: '300px', overflowY: 'auto',
              border: '2px solid #e0e0e0', borderRadius: '12px',
              marginBottom: '1.5rem', background: '#f8f9fa'
            }}>
              {loadingPatients ? (
                <div style={{ padding: '2rem', textAlign: 'center', color: '#666' }}>
                  üîÑ Connexion base de donn√©es...
                </div>
              ) : patients.length === 0 ? (
                <div style={{ padding: '2rem', textAlign: 'center', color: '#666' }}>
                  Aucun patient trouv√©
                </div>
              ) : (
                patients.map((patient) => (
                  <div
                    key={patient._id}
                    onClick={() => selectPatient(patient)}
                    style={{
                      padding: '1.2rem 1.5rem',
                      cursor: 'pointer',
                      borderBottom: '1px solid #eee',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '1rem',
                      background: formData.patientId === patient._id ? '#e3f2fd' : 'transparent',
                      transition: 'background 0.2s'
                    }}
                  >
                    <strong style={{ fontSize: '1.1rem' }}>
                      {patient.fullName || patient.nom || patient.name}
                    </strong>
                    <span style={{ color: '#666', flex: 1 }}>
                      {patient.email}
                    </span>
                    <span style={{ 
                      padding: '0.2rem 0.6rem', 
                      background: '#4caf50', 
                      color: 'white', 
                      borderRadius: '12px', 
                      fontSize: '0.8rem' 
                    }}>
                      {patient.sexe === 'M' ? 'üë®' : 'üë©'}
                    </span>
                  </div>
                ))
              )}
            </div>
          )}

          {/* CONFIRMATION PATIENT */}
          {formData.patientId && (
            <div style={{
              padding: '1.5rem',
              background: 'linear-gradient(135deg, #e8f5e8, #c8e6c9)',
              borderRadius: '12px',
              marginBottom: '2rem',
              borderLeft: '5px solid #4caf50'
            }}>
              ‚úÖ <strong>{formData.patientFullName}</strong><br/>
              <small style={{ color: '#2e7d32' }}>{formData.patientEmail}</small>
            </div>
          )}

          {/* CHAMPS M√âDICAUX */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem', marginBottom: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
                ü©∏ Groupe sanguin
              </label>
              <select 
                name="groupeSanguin" 
                value={formData.groupeSanguin} 
                onChange={handleInputChange}
                style={inputStyle}
                required
              >
                <option value="">Choisir...</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
                ‚öïÔ∏è Sexe patient
              </label>
              <input 
                value={formData.sexe === 'M' ? 'Masculin' : 'F√©minin'} 
                readOnly 
                style={readOnlyStyle} 
              />
            </div>
          </div>

          <div style={{ marginBottom: '2rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
              üìù R√©sum√© m√©dical *
            </label>
            <textarea 
              name="resumeMedical" 
              value={formData.resumeMedical} 
              onChange={handleInputChange}
              rows={4}
              required
              style={textAreaStyle}
              placeholder="Diagnostic principal, ant√©c√©dents importants, traitements en cours..."
            />
          </div>

          {/* ALLERGIES */}
          <div style={{ marginBottom: '2rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#d32f2f' }}>
              ‚ö†Ô∏è Allergies (optionnel)
            </label>
            <input 
              name="allergies" 
              value={formData.allergies} 
              onChange={handleInputChange}
              style={inputStyle}
              placeholder="P√©nicilline, arachides, etc..."
            />
          </div>

          {/* ACTIONS */}
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button 
              type="submit" 
              disabled={!formData.patientId || !formData.resumeMedical || loading}
              style={{
                flex: 1, padding: '1.3rem',
                background: (formData.patientId && formData.resumeMedical && !loading) ? 
                  'linear-gradient(135deg, #4caf50, #45a049)' : '#ccc',
                color: 'white', border: 'none', borderRadius: '12px',
                fontSize: '1.1rem', fontWeight: 'bold', cursor: 'pointer',
                opacity: loading ? 0.7 : 1
              }}
            >
              {loading ? '‚è≥ Cr√©ation en cours...' : 'üíæ Cr√©er le Dossier'}
            </button>
            <button 
              type="button" 
              onClick={onClose}
              style={{
                flex: 1, padding: '1.3rem',
                background: 'linear-gradient(135deg, #f44336, #d32f2f)',
                color: 'white', border: 'none', borderRadius: '12px',
                fontSize: '1.1rem', fontWeight: 'bold', cursor: 'pointer'
              }}
              disabled={loading}
            >
              ‚ùå Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateDossierModal;
