import React, { useEffect, useState } from "react";
import axios from "../api/endpoint";

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Récupère le token depuis localStorage (après login)
  const token = localStorage.getItem("token");

  // Config Axios avec token
  const axiosConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  // Récupérer profil patient
  const fetchUser = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users/me", axiosConfig);
      setUser(res.data);
    } catch (err) {
      console.error("Erreur récupération infos patient:", err);
      setError(err.response?.data?.message || "Erreur serveur");
    }
  };

  // Récupérer dossiers du patient
  const fetchDossiers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/patient/dossiers", axiosConfig);
      setDossiers(res.data);
    } catch (err) {
      console.error("Erreur récupération dossiers:", err);
      setError(err.response?.data?.message || "Erreur serveur");
    }
  };

  // Initialisation
  useEffect(() => {
    const init = async () => {
      setLoading(true);
      await fetchUser();
      await fetchDossiers();
      setLoading(false);
    };
    init();
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Bienvenue, {user?.fullName}</h1>
      <p>Email : {user?.email}</p>
      <p>Rôle : {user?.role}</p>

      <h2>Mes Dossiers Médicaux</h2>
      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Motif</th>
              <th>Diagnostic</th>
              <th>Date</th>
              <th>Médecin</th>
            </tr>
          </thead>
          <tbody>
            {dossiers.map((d) => (
              <tr key={d._id}>
                <td>{d.motif}</td>
                <td>{d.diagnostic}</td>
                <td>{new Date(d.date).toLocaleDateString()}</td>
                <td>{d.medecinName || "Médecin inconnu"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PatientDashboard;
