import React, { useState, useEffect } from 'react';
import getData from '../../api/endpoint';
import { FiFileText, FiHeart, FiAlertCircle, FiUser } from 'react-icons/fi';

const MonDossier = () => {
  const [dossier, setDossier] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDossier();
  }, []);

  const loadDossier = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      if (token) {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        const dossiers = await getData('/dossiers');
        const patientDossier = dossiers.find(d => d.patientId === decoded.userId);
        setDossier(patientDossier);
      }
    } catch (err) {
      console.error('Erreur lors du chargement du dossier:', err);
      setError('Impossible de charger votre dossier médical.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-600">Chargement...</div>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Mon Dossier Médical</h2>
        <p className="text-gray-600 mt-2">Consultez votre dossier médical complet</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {!dossier ? (
        <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
          <FiFileText size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">Aucun dossier médical</h3>
          <p className="text-gray-600">Votre dossier médical n'a pas encore été créé. Contactez votre médecin.</p>
        </div>
      ) : (
        <div className="space-y-6">
          {/* Informations générales */}
          <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
            <h3 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
              <FiUser className="mr-2" size={24} />
              Informations Générales
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <p className="text-sm text-gray-600">Groupe Sanguin</p>
                <p className="text-lg font-semibold text-gray-900">{dossier.groupeSanguin || 'Non spécifié'}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Statut</p>
                <p className="text-lg font-semibold text-gray-900">{dossier.statut || 'ACTIF'}</p>
              </div>
            </div>
          </div>

          {/* Résumé médical */}
          {dossier.resumeMedical && (
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
              <h3 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
                <FiFileText className="mr-2" size={24} />
                Résumé Médical
              </h3>
              <p className="text-gray-700">{dossier.resumeMedical}</p>
            </div>
          )}

          {/* Antécédents */}
          {dossier.antecedents && dossier.antecedents.length > 0 && (
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
              <h3 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
                <FiAlertCircle className="mr-2" size={24} />
                Antécédents Médicaux
              </h3>
              <ul className="space-y-2">
                {dossier.antecedents.map((antecedent, index) => (
                  <li key={index} className="text-gray-700 flex items-center">
                    <span className="w-2 h-2 bg-blue-500 rounded-full mr-3"></span>
                    {antecedent.antecedent}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Allergies */}
          {dossier.allergies && dossier.allergies.length > 0 && (
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
              <h3 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
                <FiHeart className="mr-2" size={24} />
                Allergies
              </h3>
              <ul className="space-y-2">
                {dossier.allergies.map((allergie, index) => (
                  <li key={index} className="text-gray-700 flex items-center">
                    <span className="w-2 h-2 bg-red-500 rounded-full mr-3"></span>
                    {allergie.allergie}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Contact d'urgence */}
          {dossier.contactUrgence && (
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
              <h3 className="text-xl font-bold text-gray-900 mb-4">Contact d'Urgence</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <p className="text-sm text-gray-600">Nom</p>
                  <p className="text-lg font-semibold text-gray-900">{dossier.contactUrgence.nom || 'Non spécifié'}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Téléphone</p>
                  <p className="text-lg font-semibold text-gray-900">{dossier.contactUrgence.telephone || 'Non spécifié'}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Lien</p>
                  <p className="text-lg font-semibold text-gray-900">{dossier.contactUrgence.lien || 'Non spécifié'}</p>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MonDossier;
