import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation, useParams } from 'react-router-dom';

const MedecinDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [patients, setPatients] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);
  const [ordonnances, setOrdonnances] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState({ open: false, type: null, patientId: null });

  useEffect(() => {
    // Mock data
    setTimeout(() => {
      setProfile({
        nom: 'NDIAYE', prenom: 'Moustapha', email: 'moustapha.ndiaye@govsanitaire.sn',
        telephone: '+221 78 987 6543', specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001', etablissement: 'CHU de Fann', statut: 'ValidÃ©'
      });
      
      const mockPatients = [
        { id: '1', nom: 'DIOP', prenom: 'Aminata', age: 35, sexe: 'F', telephone: '+221 78 123 4567', 
          email: 'aminata.diop@email.com', groupeSanguin: 'O+', allergies: 'Aucune', derniereConsult: '08/02/2026' },
        { id: '2', nom: 'SOW', prenom: 'Fatou', age: 42, sexe: 'F', telephone: '+221 77 456 7890', 
          email: 'fatou.sow@email.com', groupeSanguin: 'A+', allergies: 'PÃ©nicilline', derniereConsult: '07/02/2026' }
      ];
      
      setPatients(mockPatients);
      setLoading(false);
    }, 1000);
  }, []);

  const createDossier = (patientId, data) => {
    const nouveauDossier = { id: Date.now().toString(), patientId, ...data, dateCreation: new Date().toLocaleDateString('fr-FR'), statut: 'Actif' };
    setDossiers(prev => [...prev, nouveauDossier]);
    setNotifications(prev => [...prev, {
      id: Date.now(),
      message: `Nouveau dossier crÃ©Ã© pour ${patients.find(p => p.id === patientId)?.prenom} ${patients.find(p => p.id === patientId)?.nom}`,
      type: 'success',
      date: new Date().toLocaleDateString('fr-FR')
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  const createOrdonnance = (patientId, data) => {
    const nouvelleOrdonnance = { id: Date.now().toString(), patientId, ...data, dateCreation: new Date().toLocaleDateString('fr-FR'), statut: 'Active' };
    setOrdonnances(prev => [...prev, nouvelleOrdonnance]);
    setNotifications(prev => [...prev, {
      id: Date.now(),
      message: `Ordonnance crÃ©Ã©e pour ${patients.find(p => p.id === patientId)?.prenom} ${patients.find(p => p.id === patientId)?.nom}`,
      type: 'success',
      date: new Date().toLocaleDateString('fr-FR')
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  if (loading) return <LoadingScreen />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      {/* âœ… NotificationsMedecin DÃ‰FINI ICI */}
      <NotificationsMedecin notifications={notifications} />
      
      <aside className="hidden lg:block lg:w-64 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white shadow-lg border-r border-gray-200">
        <ProfileMedecin profile={profile} />
        <NavigationMedecin activePath={location.pathname} patientCount={patients.length} />
      </aside>

      <main className="lg:ml-64 p-6 lg:p-8 pb-20">
        <Routes>
          <Route path="/" element={<DashboardMedecin profile={profile} patients={patients} />} />
          <Route path="/patients" element={<PatientsPage patients={patients} setShowModal={setShowModal} />} />
          <Route path="/patients/:id" element={<PatientDetail patients={patients} dossiers={dossiers} ordonnances={ordonnances} setShowModal={setShowModal} />} />
          <Route path="/ordonnances" element={<OrdonnancesPage ordonnances={ordonnances} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>

      {/* Modals */}
      {showModal.open && (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4" onClick={() => setShowModal({ open: false, type: null, patientId: null })}>
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
            {showModal.type === 'dossier' && <DossierForm patientId={showModal.patientId} onSubmit={createDossier} patients={patients} />}
            {showModal.type === 'ordonnance' && <OrdonnanceForm patientId={showModal.patientId} onSubmit={createOrdonnance} patients={patients} />}
          </div>
        </div>
      )}
    </div>
  );
};

// âœ… TOUS LES COMPOSANTS DÃ‰FINIS
const NotificationsMedecin = ({ notifications }) => {
  if (!notifications.length) return null;
  return (
    <div className="fixed top-20 right-4 z-50 space-y-2 max-w-sm">
      {notifications.slice(0, 2).map(notif => (
        <div key={notif.id} className="bg-blue-500 text-white p-4 rounded-xl shadow-lg border-l-4 border-blue-600 animate-pulse">
          <h4 className="font-bold text-lg mb-1">{notif.message}</h4>
          <p className="text-xs opacity-75">{notif.date}</p>
        </div>
      ))}
    </div>
  );
};

const DossierForm = ({ patientId, onSubmit, patients }) => {
  const patient = patients.find(p => p.id === patientId);
  const [formData, setFormData] = useState({ antecedents: '', diagnostics: '', notes: '', analyses: '', traitements: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(patientId, formData);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-2xl font-bold text-gray-900">ğŸ“„ CrÃ©er dossier mÃ©dical</h2>
        <button onClick={() => window.history.back()} className="text-xl hover:text-red-500">âœ•</button>
      </div>
      <div className="bg-blue-50 p-6 rounded-xl mb-8">
        <h3 className="font-bold text-lg text-blue-800 mb-2">Patient: {patient?.prenom} {patient?.nom}</h3>
        <p className="text-sm text-blue-700">{patient?.age} ans</p>
      </div>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div><label className="block text-lg font-semibold text-gray-700 mb-3">AntÃ©cÃ©dents</label>
          <textarea rows="3" value={formData.antecedents} onChange={(e) => setFormData({...formData, antecedents: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" required /></div>
        <div className="grid md:grid-cols-2 gap-6">
          <div><label className="block text-lg font-semibold text-gray-700 mb-3">Diagnostic</label>
            <input type="text" value={formData.diagnostics} onChange={(e) => setFormData({...formData, diagnostics: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" required /></div>
          <div><label className="block text-lg font-semibold text-gray-700 mb-3">Examens</label>
            <input type="text" value={formData.analyses} onChange={(e) => setFormData({...formData, analyses: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" /></div>
        </div>
        <div><label className="block text-lg font-semibold text-gray-700 mb-3">Notes</label>
          <textarea rows="4" value={formData.notes} onChange={(e) => setFormData({...formData, notes: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" /></div>
        <button type="submit" className="w-full bg-green-600 text-white font-bold py-4 px-6 rounded-xl hover:bg-green-700 shadow-lg">
          ğŸ’¾ CrÃ©er dossier
        </button>
      </form>
    </div>
  );
};

const OrdonnanceForm = ({ patientId, onSubmit, patients }) => {
  const patient = patients.find(p => p.id === patientId);
  const [formData, setFormData] = useState({ medicament: '', dosage: '', duree: '', instructions: '', renouvelable: false });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(patientId, formData);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-2xl font-bold text-gray-900">ğŸ’Š Nouvelle ordonnance</h2>
        <button onClick={() => window.history.back()} className="text-xl hover:text-red-500">âœ•</button>
      </div>
      <div className="bg-green-50 p-6 rounded-xl mb-8">
        <h3 className="font-bold text-lg text-green-800 mb-2">Patient: {patient?.prenom} {patient?.nom}</h3>
      </div>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div><label className="block text-lg font-semibold text-gray-700 mb-3">MÃ©dicament</label>
          <input type="text" value={formData.medicament} onChange={(e) => setFormData({...formData, medicament: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" required /></div>
        <div className="grid md:grid-cols-2 gap-6">
          <div><label className="block text-lg font-semibold text-gray-700 mb-3">Dosage</label>
            <input type="text" value={formData.dosage} onChange={(e) => setFormData({...formData, dosage: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" required /></div>
          <div><label className="block text-lg font-semibold text-gray-700 mb-3">DurÃ©e</label>
            <input type="text" value={formData.duree} onChange={(e) => setFormData({...formData, duree: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500" required /></div>
        </div>
        <button type="submit" className="w-full bg-purple-600 text-white font-bold py-4 px-6 rounded-xl hover:bg-purple-700 shadow-lg">
          âš•ï¸ Ã‰mettre ordonnance
        </button>
      </form>
    </div>
  );
};

const ProfileMedecin = ({ profile }) => (
  <div className="p-6 border-b border-gray-200">
    <div className="w-20 h-20 bg-blue-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 font-bold text-xl shadow-md">ğŸ‘¨â€âš•ï¸</div>
    <h2 className="text-xl font-bold text-gray-900 text-center mb-2">Dr. {profile?.prenom} {profile?.nom}</h2>
    <div className="text-center text-sm space-y-1 bg-blue-50 p-3 rounded-lg border border-blue-200">
      <p className="font-semibold text-blue-800">{profile?.specialite}</p>
      <p className="text-xs">{profile?.etablissement}</p>
      <span className="px-3 py-1 rounded-full text-xs font-bold bg-green-100 text-green-800">{profile?.statut}</span>
    </div>
  </div>
);

const NavigationMedecin = ({ activePath, patientCount }) => (
  <nav className="p-4 space-y-2 mt-4">
    <NavLink href="/medecin" icon="ğŸ“Š" label="Dashboard" active={activePath === '/medecin'} color="blue" />
    <NavLink href="/medecin/patients" icon="ğŸ‘¥" label={`Patients (${patientCount})`} active={activePath === '/medecin/patients'} color="green" />
    <NavLink href="/medecin/ordonnances" icon="ğŸ’Š" label="Ordonnances" active={activePath === '/medecin/ordonnances'} color="orange" />
  </nav>
);

const NavLink = ({ href, icon, label, active, color }) => (
  <Link to={href} className={`flex items-center p-4 rounded-xl font-semibold transition-all hover:shadow-md ${
    active ? `bg-${color}-100 text-${color}-800 border-l-4 border-${color}-500 shadow-md` : 'text-gray-700 hover:bg-gray-50'
  }`}>
    <span className={`mr-4 text-xl ${active ? `text-${color}-600` : 'text-gray-500'}`}>{icon}</span>
    <span className="truncate">{label}</span>
  </Link>
);

const DashboardMedecin = ({ profile, patients }) => (
  <div className="max-w-7xl mx-auto space-y-8">
    <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
      <h1 className="text-4xl font-bold text-gray-900 mb-2">Bonjour Dr. {profile?.prenom}</h1>
      <p className="text-xl text-blue-800">{profile?.specialite}</p>
    </div>
    <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard icon="ğŸ‘¥" title={patients.length} label="Patients" color="blue" />
      <StatCard icon="ğŸ“„" title={dossiers.length} label="Dossiers" color="green" />
      <StatCard icon="ğŸ’Š" title={ordonnances.length} label="Ordonnances" color="purple" />
    </div>
  </div>
);

const PatientsPage = ({ patients, setShowModal }) => (
  <div className="max-w-6xl mx-auto space-y-8">
    <div className="flex items-center justify-between p-8 bg-white rounded-2xl shadow-lg border border-gray-200">
      <h2 className="text-3xl font-bold text-gray-900">ğŸ‘¥ Mes patients ({patients.length})</h2>
    </div>
    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
      {patients.map(patient => (
        <PatientCard key={patient.id} patient={patient} link={`/medecin/patients/${patient.id}`} />
      ))}
    </div>
  </div>
);

const PatientDetail = ({ patients, dossiers, ordonnances, setShowModal }) => {
  const { id } = useParams();
  const patient = patients.find(p => p.id === id);
  if (!patient) return <NotFoundPage />;
  
  return (
    <div className="max-w-6xl mx-auto space-y-8">
      <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
        <div className="flex items-start gap-6">
          <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-full flex items-center justify-center font-bold text-2xl flex-shrink-0">
            {patient.sexe === 'M' ? 'ğŸ‘¨' : 'ğŸ‘©'}
          </div>
          <div className="flex-1">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">{patient.prenom} {patient.nom}</h1>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-600">
              <div><span className="font-semibold">Ã‚ge:</span> {patient.age} ans</div>
              <div><span className="font-semibold">Tel:</span> {patient.telephone}</div>
              <div><span className="font-semibold">Email:</span> {patient.email}</div>
            </div>
          </div>
          <div className="flex gap-3">
            <button onClick={() => setShowModal({ open: true, type: 'dossier', patientId: patient.id })} 
              className="bg-blue-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-blue-700 shadow-lg">ğŸ“„ Dossier</button>
            <button onClick={() => setShowModal({ open: true, type: 'ordonnance', patientId: patient.id })} 
              className="bg-purple-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-purple-700 shadow-lg">ğŸ’Š Ordonnance</button>
          </div>
        </div>
      </div>
      <div className="grid lg:grid-cols-2 gap-8">
        <DossierResume dossiers={dossiers.filter(d => d.patientId === id)} />
        <OrdonnancesResume ordonnances={ordonnances.filter(o => o.patientId === id)} />
      </div>
    </div>
  );
};

const PatientCard = ({ patient, link }) => (
  <Link to={link} className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200 hover:shadow-xl hover:-translate-y-1 transition-all">
    <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-full flex items-center justify-center font-bold text-xl mx-auto mb-4">
      {patient.sexe === 'M' ? 'ğŸ‘¨' : 'ğŸ‘©'}
    </div>
    <h3 className="text-xl font-bold text-gray-900 text-center mb-2">{patient.prenom} {patient.nom}</h3>
    <p className="text-center text-sm text-gray-600">{patient.age} ans</p>
  </Link>
);

const StatCard = ({ icon, title, label, color }) => (
  <div className={`bg-white p-6 rounded-2xl shadow-md border-l-4 border-${color}-500 hover:shadow-lg transition-all`}>
    <div className="text-3xl mb-4">{icon}</div>
    <p className="text-3xl font-bold text-gray-900 mb-2">{title}</p>
    <p className="text-lg font-semibold text-gray-600">{label}</p>
  </div>
);

const DossierResume = ({ dossiers }) => (
  <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
    <h3 className="text-2xl font-bold text-gray-900 mb-6">ğŸ“‹ Dossiers mÃ©dicaux</h3>
    {dossiers.length ? (
      dossiers.map(d => (
        <div key={d.id} className="p-4 bg-blue-50 rounded-xl mb-4">
          <p className="font-semibold">{d.diagnostics}</p>
          <p className="text-sm text-gray-600">{d.dateCreation}</p>
        </div>
      ))
    ) : (
      <p className="text-gray-500">Aucun dossier</p>
    )}
  </div>
);

const OrdonnancesResume = ({ ordonnances }) => (
  <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200">
    <h3 className="text-xl font-bold text-gray-900 mb-4">ğŸ’Š Ordonnances</h3>
    {ordonnances.length ? (
      ordonnances.map(o => (
        <div key={o.id} className="p-3 bg-purple-50 rounded-lg mb-2 text-sm">
          {o.medicament} - {o.statut}
        </div>
      ))
    ) : (
      <p className="text-gray-500 text-sm">Aucune ordonnance</p>
    )}
  </div>
);

const OrdonnancesPage = ({ ordonnances }) => (
  <div className="max-w-6xl mx-auto p-12">
    <h1 className="text-4xl font-bold text-gray-900 text-center mb-8">ğŸ’Š Toutes les ordonnances</h1>
    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
      {ordonnances.map(o => (
        <div key={o.id} className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200">
          <h3 className="font-bold text-lg">{o.medicament}</h3>
          <p className="text-sm text-gray-600">{o.dosage} - {o.duree}</p>
          <span className="inline-block mt-2 px-3 py-1 bg-green-100 text-green-800 text-xs rounded-full">{o.statut}</span>
        </div>
      ))}
    </div>
  </div>
);

const LoadingScreen = () => (
  <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 flex items-center justify-center p-8">
    <div className="text-center">
      <div className="w-20 h-20 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mx-auto mb-8"></div>
      <h1 className="text-3xl font-bold text-gray-900">Chargement...</h1>
    </div>
  </div>
);

const NotFoundPage = () => (
  <div className="max-w-2xl mx-auto p-20 text-center">
    <h1 className="text-5xl font-bold text-gray-400 mb-8">404</h1>
    <Link to="/medecin" className="bg-blue-600 text-white font-bold px-12 py-4 rounded-xl hover:bg-blue-700 inline-block">
      Retour Dashboard
    </Link>
  </div>
);

export default MedecinDashboard;
