import React, { useState } from 'react';

const PatientDashboard = () => {
  const [activeTab, setActiveTab] = useState('rdv');
  const [ordonnances, setOrdonnances] = useState([
    { id: 1, date: '08/02/2026', medecin: 'Dr Ndiaye', statut: 'actif' },
    { id: 2, date: '05/02/2026', medecin: 'Dr Diop', statut: 'expir√©e' }
  ]);

  const stats = { 
    rdvs: 3, 
    ordonnances: 12, 
    medecins: 2,
    prochains: 'Demain 14h00'
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: '#f8fafc',
      padding: '24px',
      fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        
        {/* HEADER PATIENT */}
        <div style={{
          background: 'white',
          borderRadius: '16px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
          border: '1px solid #e2e8f0',
          padding: '32px',
          marginBottom: '32px'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '24px' }}>
            <div>
              <h1 style={{
                fontSize: '36px',
                fontWeight: '700',
                color: '#3b82f6',
                margin: '0 0 8px 0'
              }}>
                üë§ Mon Espace Patient
