import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation, useParams } from 'react-router-dom';

const MedecinDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [patients, setPatients] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);
  const [ordonnances, setOrdonnances] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState({ open: false, type: null, patientId: null });

  useEffect(() => {
    setTimeout(() => {
      setProfile({
        nom: 'NDIAYE', prenom: 'Moustapha', email: 'moustapha.ndiaye@govsanitaire.sn',
        telephone: '+221 78 987 6543', specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001', etablissement: 'CHU de Fann', statut: 'ValidÃ©'
      });
      
      const mockPatients = [
        { id: '1', nom: 'DIOP', prenom: 'Aminata', age: 35, sexe: 'F', telephone: '+221 78 123 4567', 
          email: 'aminata.diop@email.com', groupeSanguin: 'O+', allergies: 'Aucune', derniereConsult: '08/02/2026' },
        { id: '2', nom: 'SOW', prenom: 'Fatou', age: 42, sexe: 'F', telephone: '+221 77 456 7890', 
          email: 'fatou.sow@email.com', groupeSanguin: 'A+', allergies: 'PÃ©nicilline', derniereConsult: '07/02/2026' }
      ];
      
      setPatients(mockPatients);
      setRendezVous([
        { id: '1', patientId: '1', patient: 'Aminata DIOP', date: '24/02/2026 10:00', statut: 'ConfirmÃ©' },
        { id: '2', patientId: '2', patient: 'Fatou SOW', date: '24/02/2026 14:00', statut: 'ConfirmÃ©' }
      ]);
      
      setDossiers([
        { patientId: '1', antecedents: 'HTA lÃ©gÃ¨re', diagnostics: 'Palpitations', notes: 'Suivi rÃ©gulier', analyses: 'ECG normal' },
        { patientId: '2', antecedents: 'DiabÃ¨te type 2', diagnostics: 'Hypertension', notes: 'Bonne observance', analyses: 'Bilan sanguin OK' }
      ]);
      
      setLoading(false);
    }, 1000);
  }, []);

  const createDossier = (patientId, data) => {
    const nouveauDossier = {
      id: Date.now().toString(),
      patientId,
      ...data,
      dateCreation: new Date().toLocaleDateString('fr-FR'),
      statut: 'Actif'
    };
    setDossiers(prev => [...prev, nouveauDossier]);
    setNotifications(prev => [...prev, {
      id: Date.now(),
      message: `Nouveau dossier crÃ©Ã© pour ${patients.find(p => p.id === patientId)?.prenom} ${patients.find(p => p.id === patientId)?.nom}`,
      type: 'success',
      date: new Date().toLocaleDateString('fr-FR')
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  const createOrdonnance = (patientId, data) => {
    const nouvelleOrdonnance = {
      id: Date.now().toString(),
      patientId,
      ...data,
      dateCreation: new Date().toLocaleDateString('fr-FR'),
      statut: 'Active'
    };
    setOrdonnances(prev => [...prev, nouvelleOrdonnance]);
    setNotifications(prev => [...prev, {
      id: Date.now(),
      message: `Ordonnance crÃ©Ã©e pour ${patients.find(p => p.id === patientId)?.prenom} ${patients.find(p => p.id === patientId)?.nom}`,
      type: 'success',
      date: new Date().toLocaleDateString('fr-FR')
    }]);
    setShowModal({ open: false, type: null, patientId: null });
  };

  if (loading) return <LoadingScreen />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <NotificationsMedecin notifications={notifications} />
      
      <aside className="hidden lg:block lg:w-64 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white shadow-lg border-r border-gray-200">
        <ProfileMedecin profile={profile} />
        <NavigationMedecin activePath={location.pathname} patientCount={patients.length} />
      </aside>

      <main className="lg:ml-64 p-6 lg:p-8 pb-20">
        <Routes>
          <Route path="/" element={<DashboardMedecin profile={profile} patients={patients} rdv={rendezVous} dossiers={dossiers} />} />
          <Route path="/patients" element={<PatientsPage patients={patients} setShowModal={setShowModal} />} />
          <Route path="/patients/:id" element={<PatientDetail patients={patients} dossiers={dossiers} ordonnances={ordonnances} setShowModal={setShowModal} />} />
          <Route path="/ordonnances" element={<OrdonnancesPage ordonnances={ordonnances} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>

      {/* ðŸ”¥ MODALS DYNAMIQUES */}
      {showModal.open && (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4" onClick={() => setShowModal({ open: false, type: null, patientId: null })}>
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
            {showModal.type === 'dossier' && (
              <DossierForm patientId={showModal.patientId} onSubmit={createDossier} patients={patients} />
            )}
            {showModal.type === 'ordonnance' && (
              <OrdonnanceForm patientId={showModal.patientId} onSubmit={createOrdonnance} patients={patients} />
            )}
          </div>
        </div>
      )}
    </div>
  );
};

// ðŸ”¥ FORMULAIRE CRÃ‰ATION DOSSIER
const DossierForm = ({ patientId, onSubmit, patients }) => {
  const patient = patients.find(p => p.id === patientId);
  const [formData, setFormData] = useState({
    antecedents: '', diagnostics: '', notes: '', analyses: '', traitements: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(patientId, formData);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-2xl font-bold text-gray-900">ðŸ“„ CrÃ©er dossier mÃ©dical</h2>
        <button onClick={() => window.location.href = '/medecin/patients'} className="text-xl hover:text-red-500">âœ•</button>
      </div>
      
      <div className="bg-blue-50 p-6 rounded-xl mb-8">
        <h3 className="font-bold text-lg text-blue-800 mb-2">Patient: {patient?.prenom} {patient?.nom}</h3>
        <p className="text-sm text-blue-700">{patient?.age} ans â€¢ {patient?.telephone}</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-lg font-semibold text-gray-700 mb-3">AntÃ©cÃ©dents mÃ©dicaux</label>
          <textarea rows="3" value={formData.antecedents} onChange={(e) => setFormData({...formData, antecedents: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-vertical" 
            placeholder="Ex: HTA, DiabÃ¨te, Chirurgie..." required />
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <label className="block text-lg font-semibold text-gray-700 mb-3">Diagnostic actuel</label>
            <input type="text" value={formData.diagnostics} onChange={(e) => setFormData({...formData, diagnostics: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500" 
              placeholder="Ex: Palpitations, Infection..." required />
          </div>
          <div>
            <label className="block text-lg font-semibold text-gray-700 mb-3">Examens/Analyses</label>
            <input type="text" value={formData.analyses} onChange={(e) => setFormData({...formData, analyses: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500" 
              placeholder="Ex: ECG, Bilan sanguin..." />
          </div>
        </div>

        <div>
          <label className="block text-lg font-semibold text-gray-700 mb-3">Notes & recommandations</label>
          <textarea rows="4" value={formData.notes} onChange={(e) => setFormData({...formData, notes: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-vertical" 
            placeholder="Observations, suivi, conseils..." />
        </div>

        <div>
          <label className="block text-lg font-semibold text-gray-700 mb-3">Traitements en cours</label>
          <textarea rows="3" value={formData.traitements} onChange={(e) => setFormData({...formData, traitements: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-vertical" 
            placeholder="MÃ©dicaments actuels, posologie..." />
        </div>

        <button type="submit" className="w-full bg-green-600 text-white font-bold py-4 px-6 rounded-xl hover:bg-green-700 focus:ring-4 focus:ring-green-300 transition-all text-lg shadow-lg">
          ðŸ’¾ CrÃ©er le dossier mÃ©dical
        </button>
      </form>
    </div>
  );
};

// ðŸ”¥ FORMULAIRE ORDONNANCE
const OrdonnanceForm = ({ patientId, onSubmit, patients }) => {
  const patient = patients.find(p => p.id === patientId);
  const [formData, setFormData] = useState({
    medicament: '', dosage: '', duree: '', instructions: '', renouvelable: false
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(patientId, formData);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-2xl font-bold text-gray-900">ðŸ’Š Nouvelle ordonnance</h2>
        <button onClick={() => window.location.href = '/medecin/patients'} className="text-xl hover:text-red-500">âœ•</button>
      </div>
      
      <div className="bg-green-50 p-6 rounded-xl mb-8">
        <h3 className="font-bold text-lg text-green-800 mb-2">Patient: {patient?.prenom} {patient?.nom}</h3>
        <p className="text-sm text-green-700">{patient?.age} ans â€¢ {patient?.sexe}</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-lg font-semibold text-gray-700 mb-3">MÃ©dicament</label>
          <input type="text" value={formData.medicament} onChange={(e) => setFormData({...formData, medicament: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500" 
            placeholder="Ex: ParacÃ©tamol 500mg" required />
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <label className="block text-lg font-semibold text-gray-700 mb-3">Dosage</label>
            <input type="text" value={formData.dosage} onChange={(e) => setFormData({...formData, dosage: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500" 
              placeholder="Ex: 1cp 3x/jour" required />
          </div>
          <div>
            <label className="block text-lg font-semibold text-gray-700 mb-3">DurÃ©e</label>
            <input type="text" value={formData.duree} onChange={(e) => setFormData({...formData, duree: e.target.value})}
              className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500" 
              placeholder="Ex: 7 jours" required />
          </div>
        </div>

        <div>
          <label className="block text-lg font-semibold text-gray-700 mb-3">Instructions</label>
          <textarea rows="3" value={formData.instructions} onChange={(e) => setFormData({...formData, instructions: e.target.value})}
            className="w-full p-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-vertical" 
            placeholder="Ã€ prendre aprÃ¨s les repas, etc..." />
        </div>

        <label className="flex items-center space-x-3">
          <input type="checkbox" checked={formData.renouvelable} onChange={(e) => setFormData({...formData, renouvelable: e.target.checked})}
            className="w-5 h-5 text-blue-600 rounded focus:ring-blue-500" />
          <span className="text-lg font-semibold text-gray-700">Renouvelable</span>
        </label>

        <button type="submit" className="w-full bg-purple-600 text-white font-bold py-4 px-6 rounded-xl hover:bg-purple-700 focus:ring-4 focus:ring-purple-300 transition-all text-lg shadow-lg">
          âš•ï¸ Ã‰mettre l'ordonnance
        </button>
      </form>
    </div>
  );
};

// ðŸ”¥ PatientDetail avec boutons actions
const PatientDetail = ({ patients, dossiers, ordonnances, setShowModal }) => {
  const { id } = useParams();
  const patient = patients.find(p => p.id === id);
  const patientDossier = dossiers.find(d => d.patientId === id);
  const patientOrdonnances = ordonnances.filter(o => o.patientId === id);

  if (!patient) return <NotFoundPage />;

  return (
    <div className="max-w-6xl mx-auto space-y-8">
      {/* Header Patient */}
      <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
        <div className="flex items-start gap-6">
          <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-full flex items-center justify-center font-bold text-2xl flex-shrink-0">
            {patient.sexe === 'M' ? 'ðŸ‘¨' : 'ðŸ‘©'}
          </div>
          <div className="flex-1">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">{patient.prenom} {patient.nom}</h1>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-600">
              <div><span className="font-semibold">Ã‚ge:</span> {patient.age} ans</div>
              <div><span className="font-semibold">Tel:</span> {patient.telephone}</div>
              <div><span className="font-semibold">Email:</span> {patient.email}</div>
              <div><span className="font-semibold">Groupe:</span> {patient.groupeSanguin}</div>
            </div>
          </div>
          <div className="flex gap-3">
            <button onClick={() => setShowModal({ open: true, type: 'dossier', patientId: patient.id })} 
              className="bg-blue-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-blue-700 transition-all shadow-lg">
              ðŸ“„ Dossier
            </button>
            <button onClick={() => setShowModal({ open: true, type: 'ordonnance', patientId: patient.id })} 
              className="bg-purple-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-purple-700 transition-all shadow-lg">
              ðŸ’Š Ordonnance
            </button>
          </div>
        </div>
      </div>

      {/* RÃ©sumÃ© */}
      <div className="grid lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2">
          <DossierResume dossier={patientDossier} />
        </div>
        <OrdonnancesResume ordonnances={patientOrdonnances.slice(0, 3)} />
      </div>
    </div>
  );
};

// Autres composants (ProfileMedecin, NavigationMedecin, etc.) restent identiques Ã  la version prÃ©cÃ©dente
const ProfileMedecin = ({ profile }) => (
  <div className="p-6 border-b border-gray-200">
    <div className="w-20 h-20 bg-blue-600 text-white rounded-full flex items-center justify-center mx-auto mb-4 font-bold text-xl shadow-md">
      ðŸ‘¨â€âš•ï¸
    </div>
    <h2 className="text-xl font-bold text-gray-900 text-center mb-2">
      Dr. {profile?.prenom} {profile?.nom}
    </h2>
    <div className="text-center text-sm space-y-1 bg-blue-50 p-3 rounded-lg border border-blue-200">
      <p className="font-semibold text-blue-800">{profile?.specialite}</p>
      <p className="text-xs text-gray-600">{profile?.etablissement}</p>
      <p className="text-xs font-mono bg-white px-2 py-1 rounded text-blue-700">#{profile?.numeroOrdre}</p>
      <span className="px-3 py-1 rounded-full text-xs font-bold bg-green-100 text-green-800">{profile?.statut}</span>
    </div>
  </div>
);

const NavigationMedecin = ({ activePath, patientCount }) => (
  <nav className="p-4 space-y-2 mt-4">
    <NavLink href="/medecin" icon="ðŸ“Š" label="Dashboard" active={activePath === '/medecin'} color="blue" />
    <NavLink href="/medecin/patients" icon="ðŸ‘¥" label={`Patients (${patientCount})`} active={activePath === '/medecin/patients'} color="green" />
    <NavLink href="/medecin/ordonnances" icon="ðŸ’Š" label="Ordonnances" active={activePath === '/medecin/ordonnances'} color="orange" />
  </nav>
);

const NavLink = ({ href, icon, label, active, color }) => (
  <Link to={href} className={`flex items-center p-4 rounded-xl font-semibold transition-all hover:shadow-md ${
    active ? `bg-${color}-100 text-${color}-800 border-l-4 border-${color}-500 shadow-md` : 'text-gray-700 hover:bg-gray-50'
  }`}>
    <span className={`mr-4 text-xl ${active ? `text-${color}-600` : 'text-gray-500'}`}>{icon}</span>
    <span className="truncate">{label}</span>
  </Link>
);

// Mock composants restants
const DashboardMedecin = ({ profile, patients, rdv }) => (
  <div className="max-w-7xl mx-auto space-y-8">
    <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
      <h1 className="text-4xl font-bold text-gray-900 mb-2">Bonjour Dr. {profile?.prenom}</h1>
      <p className="text-xl text-blue-800">{profile?.specialite}</p>
    </div>
    <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard icon="ðŸ‘¥" title={patients.length} label="Patients" color="blue" />
      <StatCard icon="ðŸ“…" title={rdv.length} label="RDV" color="green" />
    </div>
  </div>
);

const StatCard = ({ icon, title, label, color }) => (
  <div className={`bg-white p-6 rounded-2xl shadow-md border-l-4 border-${color}-500 hover:shadow-lg transition-all`}>
    <div className="text-3xl mb-4">{icon}</div>
    <p className="text-3xl font-bold text-gray-900 mb-2">{title}</p>
    <p className="text-lg font-semibold text-gray-600">{label}</p>
  </div>
);

const DossierResume = ({ dossier }) => (
  <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
    <h3 className="text-2xl font-bold text-gray-900 mb-6">ðŸ“‹ RÃ©sumÃ© mÃ©dical</h3>
    {dossier ? (
      <div className="space-y-4">
        <div><strong>AntÃ©cÃ©dents:</strong> {dossier.antecedents}</div>
        <div><strong>Diagnostic:</strong> {dossier.diagnostics}</div>
        <div><strong>Notes:</strong> {dossier.notes}</div>
      </div>
    ) : (
      <p className="text-gray-500">Aucun dossier crÃ©Ã©</p>
    )}
  </div>
);

const OrdonnancesResume = ({ ordonnances }) => (
  <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-200">
    <h3 className="text-xl font-bold text-gray-900 mb-4">ðŸ’Š Ordonnances rÃ©centes</h3>
    {ordonnances.length ? (
      ordonnances.map(o => (
        <div key={o.id} className="p-3 bg-purple-50 rounded-lg mb-2 text-sm">
          {o.medicament} - {o.statut}
        </div>
      ))
    ) : (
      <p className="text-gray-500 text-sm">Aucune ordonnance</p>
    )}
