      <hr />

      <ul>
        <li>üìÅ Mes dossiers m√©dicaux</li>
        <li>üìÖ Mes rendez-vous</li>
        <li>üíä Mes ordonnances</li>
        <li>‚öôÔ∏è Mon profil</li>
      </ul>
    </div>
  );
};

export default PatientDashboard;
