// Dans loadDossiers()
const loadDossiers = async () => {
  try {
    setLoading(true);
    // ✅ SOLUTION : paramètre OU tous les dossiers
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const dossiers = await getData(`/dossier/mes-dossiers?medecinId=${user._id}`);
    setDossiers(dossiers);
  } catch (error) {
    console.error('API dossiers:', error);
    // Fallback : tous les dossiers
    const fallback = await getData('/dossier/public');
    setDossiers(fallback);
  } finally {
    setLoading(false);
  }
};
