import React, { useState, useEffect } from 'react';
import api, { deleteData } from '../../api/endpoint'; // ✅ CORRIGÉ
import { FiFileText, FiSearch, FiTrash2, FiEye } from 'react-icons/fi';

const Medecins = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [medecins, setMedecins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [services, setServices] = useState([]);

  useEffect(() => {
    loadMedecins();
    loadServices();
  }, []);

  const loadMedecins = async () => {
    try {
      setLoading(true);
      setError(null);
      const users = await api.getData('/users'); // ✅ CORRIGÉ
      const medecinsList = users.filter(user => user.role === 'Medecin');
      setMedecins(medecinsList);
    } catch (err) {
      console.error('Erreur chargement médecins:', err);
      setError(err.message || 'Impossible de charger les médecins.');
      setMedecins([]);
    } finally {
      setLoading(false);
    }
  };

  const loadServices = async () => {
    try {
      const servicesData = await api.getData('/services'); // ✅ CORRIGÉ
      setServices(servicesData);
    } catch (err) {
      console.error('Erreur services:', err);
      setServices([]);
    }
  };

  const getServiceName = (serviceId) => {
    if (!serviceId || !services.length) return 'N/A';
    const service = services.find(s => s._id === serviceId);
    return service?.nom || 'N/A';
  };

  const handleDelete = async (medecinId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce médecin ?')) return;
    try {
      await deleteData(`/users/${medecinId}`); // ✅ ENDPOINT STANDARD
      loadMedecins();
    } catch (err) {
      alert(err.message || 'Erreur suppression');
    }
  };

  const filteredMedecins = medecins.filter(medecin =>
    medecin.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    medecin.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return <div className="flex items-center justify-center h-64"><div>Chargement...</div></div>;
  }

  // ✅ JSX IDENTIQUE (EXCELLENT)
 // ✅ TEMPLATE CORRIGÉ POUR CHAQUE PAGE
const NomPage = () => {
  // ... VOS useState() + useEffect() ✅

  if (loading) return <div className="flex items-center justify-center h-64 p-6"><div className="text-lg">Chargement...</div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* VOTRE TITRE */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Votre Titre</h1>
        <p className="text-gray-600 mt-2">Description</p>
      </div>

      {/* VOTRE TABLEAU / FORMULAIRE */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        {/* CONTENU */}
      </div>
    </div>
  );
};



};

export default Medecins;
