import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken,
  getData
} from '../api/endpoint';
import {
  FiUser, 
  FiCalendar, 
  FiFileText, 
  FiHeart,
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX
} from 'react-icons/fi';

// Import des pages patient
import MesRendezVous from './patient/MesRendezVous';
import MonDossier from './patient/MonDossier';
import MesOrdonnances from './patient/MesOrdonnances';
import MesConsultations from './patient/MesConsultations';

// Composant Dashboard principal (page d'accueil patient)
const DashboardHome = ({ user }) => {
  const [stats, setStats] = useState({
    rendezVous: 0,
    ordonnances: 0,
    consultations: 0,
    dossier: null
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      if (token) {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        const userId = decoded.userId;

        // Charger les donnÃ©es en parallÃ¨le
        const [rdvData, ordonnancesData, consultationsData, dossiersData] = await Promise.all([
          getData('/rendezvous').catch(() => []),
          getData('/ordonnances').catch(() => []),
          getData('/consultations').catch(() => []),
          getData('/dossiers').catch(() => [])
        ]);

        const patientRdv = rdvData.filter(rdv => rdv.patientId?._id === userId || rdv.patientId === userId);
        const patientOrdonnances = ordonnancesData.filter(ord => ord.patientId?._id === userId || ord.patientId === userId);
        const patientConsultations = consultationsData.filter(cons => cons.patientId?._id === userId || cons.patientId === userId);
        const patientDossier = dossiersData.find(d => d.patientId?._id === userId || d.patientId === userId);

        setStats({
          rendezVous: patientRdv.length,
          ordonnances: patientOrdonnances.length,
          consultations: patientConsultations.length,
          dossier: patientDossier
        });
      }
    } catch (err) {
      console.error('Erreur lors du chargement des statistiques:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Mon Tableau de Bord</h2>
        <p className="text-gray-600 mt-2">Bienvenue, {user?.fullName || user?.email || 'Patient'}</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <Link to="/patient/rendez-vous" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Mes Rendez-vous</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.rendezVous}
              </p>
              <p className="text-sm text-blue-600 mt-1 font-medium">Voir tous</p>
            </div>
            <FiCalendar size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/patient/dossier" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Mon Dossier</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.dossier ? 'âœ“' : '-'}
              </p>
              <p className="text-sm text-green-600 mt-1 font-medium">Consulter</p>
            </div>
            <FiFileText size={32} className="text-green-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/patient/ordonnances" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Ordonnances</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.ordonnances}
              </p>
              <p className="text-sm text-purple-600 mt-1 font-medium">Historique</p>
            </div>
            <FiFileText size={32} className="text-purple-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/patient/consultations" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Consultations</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.consultations}
              </p>
              <p className="text-sm text-red-600 mt-1 font-medium">Voir toutes</p>
            </div>
            <FiHeart size={32} className="text-red-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100 mb-10">
        <h3 className="text-2xl font-bold text-gray-900 mb-6">Actions rapides</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Link to="/patient/rendez-vous" className="block w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white p-4 rounded-xl hover:shadow-lg hover:scale-105 transition-all text-center font-medium">
            ðŸ“… Prendre un rendez-vous
          </Link>
          <Link to="/patient/dossier" className="block w-full border-2 border-blue-100 bg-blue-50 text-blue-700 p-4 rounded-xl hover:bg-blue-100 hover:border-blue-200 hover:shadow-md transition-all text-center font-medium">
            ðŸ“‹ Consulter mon dossier
          </Link>
        </div>
      </div>

      {/* Informations importantes */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl shadow-lg p-6 border border-blue-100">
          <h3 className="text-xl font-bold text-gray-900 mb-3 flex items-center">
            <FiHeart className="mr-2 text-red-500" />
            SantÃ© & Bien-Ãªtre
          </h3>
          <p className="text-gray-700 mb-4">
            AccÃ©dez Ã  votre dossier mÃ©dical complet, consultez vos ordonnances et suivez votre historique mÃ©dical.
          </p>
          <Link to="/patient/dossier" className="text-blue-600 font-medium hover:underline">
            En savoir plus â†’
          </Link>
        </div>
        <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl shadow-lg p-6 border border-green-100">
          <h3 className="text-xl font-bold text-gray-900 mb-3 flex items-center">
            <FiCalendar className="mr-2 text-green-500" />
            Rendez-vous
          </h3>
          <p className="text-gray-700 mb-4">
            Planifiez vos rendez-vous mÃ©dicaux en ligne et recevez des notifications pour ne rien manquer.
          </p>
          <Link to="/patient/rendez-vous" className="text-green-600 font-medium hover:underline">
            Prendre RDV â†’
          </Link>
        </div>
      </div>
    </>
  );
};

// Composant Layout Patient qui accepte des children
export const PatientLayoutWrapper = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Patient') {
      navigate('/');
      return;
    }
    
    setUser(decoded);
  }, [navigate]);

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/patient', exact: true },
    { name: 'Mes Rendez-vous', icon: FiCalendar, href: '/patient/rendez-vous' },
    { name: 'Mon Dossier', icon: FiFileText, href: '/patient/dossier' },
    { name: 'Mes Ordonnances', icon: FiFileText, href: '/patient/ordonnances' },
    { name: 'Mes Consultations', icon: FiHeart, href: '/patient/consultations' },
    { name: 'ParamÃ¨tres', icon: FiSettings, href: '/patient/settings' }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 lg:hidden" 
          onClick={() => setSidebarOpen(false)}
        >
          <div className="absolute inset-0 bg-black bg-opacity-25" />
        </div>
      )}
      
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 lg:static lg:inset-0 transition-transform duration-200 ease-in-out`}>
        <div className="flex items-center justify-between h-16 px-6 border-b border-gray-200">
          <h1 className="text-xl font-bold text-gray-900">Espace Patient</h1>
          <button 
            onClick={() => setSidebarOpen(false)} 
            className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
          >
            <FiX size={20} />
          </button>
        </div>
        
        <nav className="mt-8 px-4 space-y-2">
          {navigation.map((item) => {
            const isActive = item.exact 
              ? location.pathname === item.href 
              : location.pathname.startsWith(item.href);
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center px-4 py-3 rounded-lg transition-all group ${
                  isActive 
                    ? 'bg-blue-100 text-blue-700 font-semibold' 
                    : 'text-gray-700 hover:bg-gray-100 hover:text-blue-600'
                }`}
              >
                <item.icon size={20} className="mr-3 flex-shrink-0" />
                <span>{item.name}</span>
              </Link>
            );
          })}
        </nav>

        <div className="absolute bottom-6 left-6 w-56">
          <button
            onClick={logout}
            className="flex items-center w-full px-4 py-3 text-red-600 hover:bg-red-50 rounded-lg transition-all group"
          >
            <FiLogOut size={20} className="mr-3 group-hover:-translate-x-1 transition-transform" />
            DÃ©connexion
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-64">
        {/* Topbar */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="flex items-center justify-between h-16 px-6">
            <button
              className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
              onClick={() => setSidebarOpen(true)}
            >
              <FiMenu size={20} />
            </button>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-500 rounded-lg relative">
                <FiBell size={20} />
                <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></span>
              </button>
              
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center">
                  <span className="text-white font-semibold text-sm">
                    {user?.fullName?.[0]?.toUpperCase() || user?.email?.[0]?.toUpperCase() || 'P'}
                  </span>
                </div>
                <span className="font-medium text-gray-700 hidden md:block">
                  {user?.fullName || user?.email || 'Patient'}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Content avec children */}
        <main style={{ padding: '20px', minHeight: 'calc(100vh - 64px)' }}>
          {children}
        </main>
      </div>
    </div>
  );
};

// Export du layout pour utilisation dans App.js
export { DashboardHome };
export default PatientLayoutWrapper;
