import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { postData } from '../../api/endpoint';
import { FiFileText, FiUser, FiAlertCircle, FiHeart, FiCheckCircle } from 'react-icons/fi';

const CreerDossier = () => {
  const navigate = useNavigate();
  const [patients, setPatients] = useState([]);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  
  const [formData, setFormData] = useState({
    patientId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: { nom: '', telephone: '', lien: '' },
    statut: 'ACTIF'
  });
  
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      setLoadingPatients(true);
      const patientsData = await api.getData('/users?role=Patient');
      setPatients(patientsData.filter(p => !p.dossierMedicalId));
    } catch (err) {
      console.error('Erreur chargement patients:', err);
      setError('Erreur lors du chargement des patients');
      setPatients([]);
    } finally {
      setLoadingPatients(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setError(null);
    
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        contactUrgence: { ...prev.contactUrgence, [field]: value }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const addAntecedent = () => {
    if (newAntecedent.trim()) {
      setFormData(prev => ({
        ...prev,
        antecedents: [...prev.antecedents, { antecedent: newAntecedent.trim() }]
      }));
      setNewAntecedent('');
    }
  };

  const removeAntecedent = (index) => {
    setFormData(prev => ({
      ...prev,
      antecedents: prev.antecedents.filter((_, i) => i !== index)
    }));
  };

  const addAllergie = () => {
    if (newAllergie.trim()) {
      setFormData(prev => ({
        ...prev,
        allergies: [...prev.allergies, { allergie: newAllergie.trim() }]
      }));
      setNewAllergie('');
    }
  };

  const removeAllergie = (index) => {
    setFormData(prev => ({
      ...prev,
      allergies: prev.allergies.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);

    if (!formData.patientId) {
      setError('Veuillez s√©lectionner un patient.');
      return;
    }

    try {
      setLoading(true);
      
      const dataToSend = {
        patientId: formData.patientId,
        resumeMedical: formData.resumeMedical || '',
        groupeSanguin: formData.groupeSanguin || '',
        antecedents: formData.antecedents || [],
        allergies: formData.allergies || [],
        contactUrgence: Object.values(formData.contactUrgence).some(v => v) 
          ? formData.contactUrgence 
          : undefined,
        statut: 'ACTIF'
      };

      await postData('/dossiers', dataToSend);
      setSuccess(true);
      setTimeout(() => navigate('/medecin/dossiers'), 1500);
      
    } catch (err) {
      console.error('Erreur cr√©ation dossier:', err);
      if (err.message.includes('duplicate') || err.message.includes('existe')) {
        setError('‚ö†Ô∏è Un dossier existe d√©j√† pour ce patient.');
      } else {
        setError(err.message || 'Erreur cr√©ation dossier');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <div className="mb-12">
        <h1 className="text-4xl font-bold text-gray-900 flex items-center gap-4 mb-2">
          <FiFileText size={48} className="text-blue-600" />
          Cr√©er Dossier M√©dical
        </h1>
        <p className="text-xl text-gray-600">Nouveau dossier patient complet</p>
      </div>

      {error && (
        <div className="bg-red-50 border-2 border-red-200 text-red-800 px-6 py-4 rounded-2xl mb-8 shadow-lg">
          {error}
        </div>
      )}
      
      {success && (
        <div className="bg-green-50 border-2 border-green-200 text-green-800 px-6 py-4 rounded-2xl mb-8 shadow-lg animate-pulse">
          ‚úÖ Dossier cr√©√© avec succ√®s !
        </div>
      )}

      <form onSubmit={handleSubmit} className="bg-white rounded-3xl shadow-2xl p-8 border border-gray-100">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-4">Patient *</label>
            <select
              name="patientId"
              value={formData.patientId}
              onChange={handleChange}
              disabled={loadingPatients}
              className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 disabled:bg-gray-50"
              required
            >
              <option value="">S√©lectionner un patient</option>
              {patients.map(patient => (
                <option key={patient._id} value={patient._id}>
                  {patient.fullName} - {patient.email}
                </option>
              ))}
            </select>
            {loadingPatients && (
              <p className="text-sm text-gray-500 mt-2">Chargement des patients...</p>
            )}
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-3">Groupe Sanguin</label>
            <select 
              name="groupeSanguin" 
              value={formData.groupeSanguin} 
              onChange={handleChange}
              className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100"
            >
              <option value="">Non sp√©cifi√©</option>
              <option value="A+">A+</option><option value="A-">A-</option>
              <option value="B+">B+</option><option value="B-">B-</option>
              <option value="AB+">AB+</option><option value="AB-">AB-</option>
              <option value="O+">O+</option><option value="O-">O-</option>
            </select>
          </div>
        </div>

        <div className="mb-8">
          <label className="block text-sm font-semibold text-gray-700 mb-4">R√©sum√© M√©dical</label>
          <textarea
            name="resumeMedical"
            value={formData.resumeMedical}
            onChange={handleChange}
            rows="4"
            className="w-full p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 resize-vertical"
            placeholder="Ant√©c√©dents principaux, traitements actuels..."
          />
        </div>

        {/* ANTECEDENTS */}
        <div className="mb-8">
          <label className="block text-lg font-bold text-gray-900 mb-4 flex items-center gap-2">
            <FiHeart className="text-red-500" />
            Ant√©c√©dents M√©dicaux
          </label>
          <div className="flex gap-4 mb-4">
            <input
              value={newAntecedent}
              onChange={(e) => setNewAntecedent(e.target.value)}
              className="flex-1 p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100"
              placeholder="Diab√®te, hypertension..."
            />
            <button
              type="button"
              onClick={addAntecedent}
              className="px-8 py-4 bg-blue-600 text-white rounded-2xl font-semibold hover:bg-blue-700 shadow-lg"
            >
              Ajouter
            </button>
          </div>
          <div className="space-y-3">
            {formData.antecedents.map((ant, index) => (
              <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-2xl border">
                <span className="font-medium">{ant.antecedent}</span>
                <button
                  type="button"
                  onClick={() => removeAntecedent(index)}
                  className="text-red-500 hover:text-red-700 text-xl font-bold"
                >
                  √ó
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* ALLERGIES */}
        <div className="mb-8">
          <label className="block text-lg font-bold text-gray-900 mb-4 flex items-center gap-2">
            <FiAlertCircle className="text-orange-500" />
            Allergies
          </label>
          <div className="flex gap-4 mb-4">
            <input
              value={newAllergie}
              onChange={(e) => setNewAllergie(e.target.value)}
              className="flex-1 p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100"
              placeholder="P√©nicilline, arachides..."
            />
            <button
              type="button"
              onClick={addAllergie}
              className="px-8 py-4 bg-orange-600 text-white rounded-2xl font-semibold hover:bg-orange-700 shadow-lg"
            >
              Ajouter
            </button>
          </div>
          <div className="space-y-3">
            {formData.allergies.map((all, index) => (
              <div key={index} className="flex items-center justify-between p-4 bg-red-50 border-2 border-red-200 rounded-2xl">
                <span className="font-semibold text-red-800">{all.allergie}</span>
                <button
                  type="button"
                  onClick={() => removeAllergie(index)}
                  className="text-red-600 hover:text-red-800 text-xl font-bold"
                >
                  √ó
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* CONTACT URGENCE */}
        <div className="mb-12">
          <label className="block text-lg font-bold text-gray-900 mb-6 flex items-center gap-2">
            <FiUser className="text-purple-600" />
            Contact d'Urgence
          </label>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <input
              name="contactUrgence.nom"
              value={formData.contactUrgence.nom}
              onChange={handleChange}
              placeholder="Nom complet"
              className="p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-purple-100"
            />
            <input
              name="contactUrgence.telephone"
              value={formData.contactUrgence.telephone}
              onChange={handleChange}
              placeholder="T√©l√©phone"
              className="p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-purple-100"
            />
            <input
              name="contactUrgence.lien"
              value={formData.contactUrgence.lien}
              onChange={handleChange}
              placeholder="Lien (P√®re, M√®re...)"
              className="p-4 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-purple-100"
            />
          </div>
        </div>

        <div className="flex flex-col sm:flex-row gap-4 pt-8">
          <button
            type="submit"
            disabled={loading || loadingPatients}
            className="flex-1 bg-gradient-to-r from-blue-600 to-blue-700 text-white py-5 px-8 rounded-3xl font-bold text-xl shadow-2xl hover:shadow-3xl hover:scale-[1.02] transition-all disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Cr√©ation en cours...' : 'üíæ Cr√©er le Dossier'}
          </button>
          <button
            type="button"
            onClick={() => navigate('/medecin/dossiers')}
            className="px-12 py-5 border-2 border-gray-300 text-gray-700 rounded-3xl font-bold hover:bg-gray-50 hover:shadow-xl transition-all"
          >
            Annuler
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreerDossier;
