import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
// ‚úÖ PAS D'IMPORT API - TOUT INLINE

const MedecinDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({ patients: 12, consultations: 8, rendezVous: 3, dossiers: 12 });
  const [loading, setLoading] = useState(true);

  // ‚úÖ MOCK DATA (pas d'API)
  useEffect(() => {
    setTimeout(() => {
      setUser({ nom: 'Dr. Moustapha Ndiaye', specialite: 'Cardiologue' });
      setLoading(false);
    }, 1000);
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: 'üìä', href: '/medecin' },
    { name: 'Patients', icon: 'üë•', href: '/medecin/patients' },
    { name: 'Consultations', icon: 'ü©∫', href: '/medecin/consultations' },
    { name: 'Rendez-vous', icon: 'üìÖ', href: '/medecin/rendez-vous' },
    { name: 'Dossiers', icon: 'üìÅ', href: '/medecin/dossiers' }
  ];

  // ‚úÖ DASHBOARD HOME INLINE
  const DashboardHome = () => (
    <div className="space-y-8 p-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-xl">
              <span className="text-2xl">üë•</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Patients</p>
              <p className="text-2xl font-bold text-blue-600">{stats.patients}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-xl">
              <span className="text-2xl">ü©∫</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Consultations</p>
              <p className="text-2xl font-bold text-green-600">{stats.consultations}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-purple-100 rounded-xl">
              <span className="text-2xl">üìÖ</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">RDV</p>
              <p className="text-2xl font-bold text-purple-600">{stats.rendezVous}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border">
          <div className="flex items-center">
            <div className="p-3 bg-yellow-100 rounded-xl">
              <span className="text-2xl">üìÅ</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Dossiers</p>
              <p className="text-2xl font-bold text-yellow-600">{stats.dossiers}</p>
            </div>
          </div>
        </div>
      </div>
      
      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-8 rounded-xl border">
        <h3 className="text-2xl font-bold text-gray-900 mb-2">
          Bienvenue Dr. {user?.nom}
        </h3>
        <p className="text-gray-600 text-lg">Votre tableau de bord est op√©rationnel</p>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p>Chargement du tableau de bord m√©decin...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className={`fixed inset-0 z-40 lg:static bg-black/10 lg:bg-transparent ${sidebarOpen ? 'block' : 'hidden lg:block'}`}>
        <div className="lg:w-64 h-screen bg-white shadow-lg">
          <div className="p-6 border-b">
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              MedSync Pro
            </h1>
          </div>
          <nav className="p-4 space-y-2">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center p-3 rounded-xl w-full ${
                  location.pathname === item.href
                    ? 'bg-blue-100 text-blue-800 border-r-4 border-blue-500 shadow-md'
                    : 'text-gray-700 hover:bg-gray-100 hover:shadow-sm'
                }`}
              >
                <span className="mr-3 text-xl">{item.icon}</span>
                <span className="font-medium">{item.name}</span>
              </Link>
            ))}
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-64">
        <header className="bg-white shadow-sm border-b p-4">
          <div className="flex items-center justify-between">
            <button 
              onClick={() => setSidebarOpen(!sidebarOpen)} 
              className="lg:hidden p-2 rounded-lg hover:bg-gray-100"
            >
              <span className="text-xl">‚ò∞</span>
            </button>
            <button onClick={logout} className="flex items-center p-2 text-red-600 hover:bg-red-50 rounded-lg">
              <span className="mr-2">üö™</span> D√©connexion
            </button>
          </div>
        </header>

        <main className="p-6">
          <Routes>
            <Route path="/" element={<DashboardHome />} />
            <Route path="/patients" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-3xl font-bold">Mes Patients</h1></div>} />
            <Route path="/consultations" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-3xl font-bold">Consultations</h1></div>} />
            <Route path="/rendez-vous" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-3xl font-bold">Rendez-vous</h1></div>} />
            <Route path="/dossiers" element={<div className="p-8 bg-white rounded-xl shadow-sm"><h1 className="text-3xl font-bold">Dossiers</h1></div>} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

