import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const api = axios.create({ baseURL: 'http://localhost:5000/api' });
api.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  config.headers['Content-Type'] = 'application/json';
  return config;
});

const CreateDossier = () => {
  const [formData, setFormData] = useState({
    patientId: '', motif: '', description: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [existingDossierId, setExistingDossierId] = useState(null);

  // Animation CSS
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes shake { 0%, 100% { transform: translateX(0); } 25% { transform: translateX(-10px); } 75% { transform: translateX(10px); } }
      @keyframes pulse { 0%, 100% { box-shadow: 0 0 20px rgba(239,68,68,0.5); } 50% { box-shadow: 0 0 40px rgba(239,68,68,1); } }
    `;
    document.head.appendChild(style);
  }, []);

  const fetchPatients = useCallback(async () => {
    try {
      setLoadingPatients(true);
      const response = await api.get('/patients');
      setPatients(Array.isArray(response.data) ? response.data : []);
    } catch (err) {
      setError('Impossible de charger les patients');
      setPatients([]);
    } finally {
      setLoadingPatients(false);
    }
  }, []);

  useEffect(() => { fetchPatients(); }, [fetchPatients]);
  useEffect(() => {
    const patient = patients.find(p => p._id === formData.patientId);
    setSelectedPatient(patient || null);
  }, [formData.patientId, patients]);

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setError(''); setSuccess(''); setExistingDossierId(null);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId) { setError('üë§ S√©lectionnez un patient'); return; }
    if (!formData.motif.trim()) { setError('üìã Motif obligatoire'); return; }

    try {
      setLoading(true); setError(''); setExistingDossierId(null);
      const response = await api.post('/dossiers', formData);
      
      setSuccess(`‚úÖ Dossier cr√©√© pour ${selectedPatient?.nom || 'patient'}! ID: ${response.data.dossierId}`);
      setFormData(prev => ({ ...prev, motif: '', description: '' }));
      
    } catch (error) {
      console.error('üí• ERREUR R√âELLE:', error.response?.data);
      
      if (error.response?.status === 409) {
        const { message, dossierId } = error.response.data;
        console.log('üî• 409 CAPTUR√â:', { message, dossierId });
        
        const idFinal = dossierId || 'ID non disponible';
        setExistingDossierId(idFinal);
        
        setError(`üö® ${message}\nüìã Dossier existant d√©tect√©`);
      } else {
        setError(`‚ùå Erreur: ${error.response?.status || 'Inconnue'}`);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '30px', maxWidth: '800px', margin: '0 auto', fontFamily: '-apple-system, sans-serif' }}>
      <h1 style={{ color: '#1e293b', marginBottom: '30px', fontSize: '28px' }}>üìÅ Cr√©er Dossier M√©dical</h1>

      {/* üî• SUCCESS NOTIFICATION */}
      {success && (
        <div style={{
          background: 'linear-gradient(135deg, #d1fae5 0%, #a7f3d0 100%)',
          color: '#065f46', padding: '24px', borderRadius: '16px',
          borderLeft: '6px solid #10b981', marginBottom: '24px',
          fontWeight: 600, fontSize: '16px', boxShadow: '0 10px 30px rgba(16,185,129,0.3)'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <span style={{ fontSize: '24px' }}>‚úÖ</span>
            <strong>{success}</strong>
            <button onClick={() => setSuccess('')} style={{ background: 'none', border: 'none', fontSize: '24px', cursor: 'pointer' }}>‚úï</button>
          </div>
        </div>
      )}

      {/* üî• ERROR NOTIFICATION - VERSION NUCLEAIRE */}
      {error && (
        <div style={{
          width: '100%', background: '#fee2e2', color: '#991b1b',
          padding: '32px', borderRadius: '20px', border: '5px solid #ef4444',
          marginBottom: '32px', fontSize: '18px', fontWeight: 'bold',
          boxShadow: '0 0 50px rgba(239,68,68,0.8)', position: 'relative',
          animation: 'shake 0.5s ease-in-out, pulse 1s infinite',
          zIndex: 10000
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
            <div>
              üö® <strong>{error}</strong>
              {existingDossierId && existingDossierId !== 'ID non disponible' && (
                <div style={{ marginTop: '16px', padding: '16px', background: '#fef2f2', borderRadius: '12px', fontSize: '16px' }}>
                  üìã <strong>ID existant:</strong> <code>{existingDossierId.slice(-6)}</code>
                  <br/>
                  <button 
                    onClick={() => window.open(`/dashboard/dossiers/${existingDossierId}`, '_blank')}
                    style={{ marginTop: '12px', padding: '8px 16px', background: '#3b82f6', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer' }}
                  >
                    üëÅÔ∏è Voir dossier
                  </button>
                </div>
              )}
            </div>
            <button 
              onClick={() => {setError(''); setExistingDossierId(null);}}
              style={{
                background: '#dc2626', color: 'white', border: 'none', 
                borderRadius: '50%', width: '44px', height: '44px', 
                fontSize: '20px', cursor: 'pointer', fontWeight: 'bold'
              }}
            >
              ‚úï
            </button>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* PATIENT SELECT */}
        <div style={{ marginBottom: '28px' }}>
          <label style={{ display: 'block', marginBottom: '12px', fontWeight: 'bold', fontSize: '16px', color: '#374151' }}>
            üë§ Patient <span style={{ color: '#ef4444' }}>*</span>
          </label>
          {loadingPatients ? (
            <div style={{ padding: '24px', textAlign: 'center', color: '#6b7280', background: '#f8fafc', borderRadius: '10px' }}>‚è≥ Chargement...</div>
          ) : patients.length === 0 ? (
            <div style={{ padding: '24px', color: '#dc2626', textAlign: 'center', background: '#fef2f2', borderRadius: '10px' }}>
              ‚ùå Aucun patient
              <button type="button" onClick={fetchPatients} style={{ marginTop: '12px', padding: '8px 16px', background: '#3b82f6', color: 'white', border: 'none', borderRadius: '6px' }}>üîÑ Recharger</button>
            </div>
          ) : (
            <>
              <select name="patientId" value={formData.patientId} onChange={handleChange} required style={{
                width: '100%', padding: '16px', border: `2px solid ${formData.patientId ? '#10b981' : '#d1d5db'}`, borderRadius: '12px', fontSize: '16px'
              }}>
                <option value="">Choisir un patient ({patients.length})</option>
                {patients.map(patient => (
                  <option key={patient._id} value={patient._id}>
                    {patient.nom?.toUpperCase()} {patient.prenom ?? ''} {patient.telephone && `‚Ä¢ ${patient.telephone}`}
                  </option>
                ))}
              </select>
              {selectedPatient && (
                <div style={{ marginTop: '12px', padding: '12px', background: '#d1fae5', borderRadius: '8px', fontSize: '14px' }}>
                  ‚úÖ {selectedPatient.nom} {selectedPatient.prenom}
                </div>
              )}
            </>
          )}
        </div>

        {/* MOTIF */}
        <div style={{ marginBottom: '24px' }}>
          <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold', color: '#374151', fontSize: '16px' }}>
            üìã Motif <span style={{ color: '#ef4444' }}>*</span>
          </label>
          <input name="motif" value={formData.motif} onChange={handleChange} placeholder="Ex: palu, contr√¥le..." required style={{
            width: '100%', padding: '16px', border: `2px solid ${formData.motif.trim() ? '#10b981' : '#d1d5db'}`, borderRadius: '12px', fontSize: '16px'
          }}/>
        </div>

        {/* DESCRIPTION */}
        <div style={{ marginBottom: '32px' }}>
          <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold', color: '#374151' }}>üìù Description</label>
          <textarea name="description" value={formData.description} onChange={handleChange} rows={4} style={{
            width: '100%', padding: '16px', border: '2px solid #d1d5db', borderRadius: '12px', fontSize: '16px', resize: 'vertical'
          }}/>
        </div>

        <button type="submit" disabled={loading || !formData.patientId || !formData.motif.trim()} style={{
          width: '100%', padding: '20px', background: loading ? '#9ca3af' : '#10b981', color: 'white', 
          border: 'none', borderRadius: '12px', fontSize: '18px', fontWeight: 'bold', cursor: 'pointer'
        }}>
          {loading ? '‚è≥ Envoi...' : 'üöÄ Cr√©er Dossier'}
        </button>
      </form>

      {/* DEBUG */}
      {process.env.NODE_ENV === 'development' && (
        <details style={{ marginTop: '32px', padding: '20px', background: '#f8fafc', borderRadius: '12px' }}>
          <summary style={{ cursor: 'pointer', fontWeight: 'bold' }}>üîç Debug</summary>
          <pre style={{ fontSize: '13px', marginTop: '16px' }}>
{JSON.stringify({ patientsCount: patients.length, selectedPatient: selectedPatient?._id, existingDossierId, formData, error }, null, 2)}
          </pre>
        </details>
      )}
    </div>
  );
};

export default CreateDossier;
