// src/pages/PatientDashboard.js
import React, { useEffect, useState } from "react";
import api from "../api/endpoint"; // utilise ton export par défaut

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Récupérer profil patient
  const fetchUser = async () => {
    try {
      const data = await api.getData("/users/me"); // utilise getData()
      setUser(data);
    } catch (err) {
      console.error("Erreur récupération infos patient:", err);
      setError(err.message || "Erreur serveur");
    }
  };

  // Récupérer dossiers du patient
  const fetchDossiers = async () => {
    try {
      const data = await api.getData("/patient/dossiers"); // utilise getData()
      setDossiers(data);
    } catch (err) {
      console.error("Erreur récupération dossiers:", err);
      setError(err.message || "Erreur serveur");
    }
  };

  // Initialisation
  useEffect(() => {
    const init = async () => {
      setLoading(true);
      await fetchUser();
      await fetchDossiers();
      setLoading(false);
    };
    init();
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Bienvenue, {user?.fullName}</h1>
      <p>Email : {user?.email}</p>
      <p>Rôle : {user?.role}</p>

      <h2>Mes Dossiers Médicaux</h2>
      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <table
          border="1"
          cellPadding="10"
          style={{ borderCollapse: "collapse", width: "100%" }}
        >
          <thead>
            <tr>
              <th>Motif</th>
              <th>Diagnostic</th>
              <th>Date</th>
              <th>Médecin</th>
            </tr>
          </thead>
          <tbody>
            {dossiers.map((d) => (
              <tr key={d._id}>
                <td>{d.motif}</td>
                <td>{d.diagnostic}</td>
                <td>{new Date(d.date).toLocaleDateString()}</td>
                <td>{d.medecinName || "Médecin inconnu"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PatientDashboard;
