import React, { useState, useEffect } from "react";
import { axiosInstance } from "../api/endpoint";

const MesDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Récupérer les dossiers du médecin connecté
  const fetchDossiers = async () => {
    try {
      setLoading(true);
      const res = await axiosInstance.get("/dossiers/medecin");
      setDossiers(res.data);
    } catch (err) {
      console.error("Erreur récupération dossiers :", err);
      alert(err.response?.data?.message || "❌ Erreur serveur");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDossiers();
  }, []);

  // Activer / désactiver un dossier
  const toggleStatut = async (id, currentStatut) => {
    try {
      const newStatut = currentStatut === "ACTIF" ? "ARCHIVE" : "ACTIF";
      await axiosInstance.put(`/dossiers/admin/${id}`, { statut: newStatut });
      setDossiers((prev) =>
        prev.map((d) =>
          d._id === id ? { ...d, statut: newStatut } : d
        )
      );
    } catch (err) {
      console.error("Erreur changement statut :", err);
      alert(err.response?.data?.message || "❌ Erreur serveur");
    }
  };

  if (loading) return <p>Chargement des dossiers...</p>;

  if (!dossiers.length) return <p>Aucun dossier trouvé.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Mes Dossiers Patients</h2>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Patient</th>
            <th>Résumé</th>
            <th>Groupe Sanguin</th>
            <th>Statut</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {dossiers.map((dossier) => (
            <tr key={dossier._id}>
              <td>{dossier.patientId?.fullName || "N/A"}</td>
              <td>{dossier.resumeMedical}</td>
              <td>{dossier.groupeSanguin}</td>
              <td>{dossier.statut}</td>
              <td>
                <button
                  onClick={() => toggleStatut(dossier._id, dossier.statut)}
                >
                  {dossier.statut === "ACTIF" ? "Désactiver" : "Activer"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MesDossiers;
