import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken 
} from '../api/endpoint';
import {
  FiUsers, 
  FiActivity, 
  FiCalendar, 
  FiDollarSign, 
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX,
  FiFileText
} from 'react-icons/fi';

import Patients from './admin/Patients';
import Medecins from './admin/Medecins';
import Services from './admin/Services';
import Rdv from './admin/Rdv';
import Finances from './admin/Finances';
import Moderateurs from './admin/Moderateurs';

import {
  LineChart,
  Line,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

/* ---------------- DASHBOARD HOME ---------------- */

const DashboardHome = ({ stats, recentActivity }) => {

  const tendanceData = [
    { mois: 'Août', patients: 1100, medecins: 85, rdv: 420 },
    { mois: 'Sept', patients: 1150, medecins: 86, rdv: 430 },
    { mois: 'Oct', patients: 1180, medecins: 87, rdv: 440 },
    { mois: 'Nov', patients: 1200, medecins: 88, rdv: 450 },
    { mois: 'Déc', patients: 1220, medecins: 88, rdv: 455 },
    { mois: 'Jan', patients: 1247, medecins: 89, rdv: 456 },
  ];

  const revenusData = [
    { mois: 'Août', revenus: 2100000 },
    { mois: 'Sept', revenus: 2200000 },
    { mois: 'Oct', revenus: 2300000 },
    { mois: 'Nov', revenus: 2350000 },
    { mois: 'Déc', revenus: 2400000 },
    { mois: 'Jan', revenus: 2450000 },
  ];

  return (
    <>
      <div className="mb-10">
        <h2 className="text-4xl font-bold tracking-tight text-gray-900">
          Dashboard Administrateur
        </h2>
        <p className="text-gray-500 mt-3 text-lg">
          Supervision complète de la plateforme my virtuActive
        </p>
      </div>

      {/* STATS */}
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-8 mb-12">
        {[
          { label: 'Patients', value: stats.totalPatients, icon: FiUsers, color: 'blue' },
          { label: 'Médecins', value: stats.totalMedecins, icon: FiUsers, color: 'emerald' },
          { label: 'Rendez-vous', value: stats.totalRdv, icon: FiCalendar, color: 'violet' },
          { label: 'Revenus', value: `${stats.revenue.toLocaleString()} CFA`, icon: FiDollarSign, color: 'amber' },
        ].map((item, i) => (
          <div
            key={i}
            className="bg-white/80 backdrop-blur p-8 rounded-2xl shadow-lg hover:shadow-2xl border border-gray-100 transition-all hover:-translate-y-1 group"
          >
            <div className="flex justify-between items-center">
              <div>
                <p className="text-sm text-gray-500 font-medium">{item.label}</p>
                <p className="text-3xl font-bold text-gray-900 mt-2">{item.value}</p>
              </div>
              <item.icon
                size={34}
                className={`text-${item.color}-600 bg-${item.color}-50 p-3 rounded-xl group-hover:scale-110 transition`}
              />
            </div>
          </div>
        ))}
      </div>

      {/* GRAPHIQUES */}
      <div className="grid grid-cols-1 xl:grid-cols-2 gap-10 mb-12">
        <div className="bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold text-gray-900 mb-6">
            Activité générale (6 mois)
          </h3>
          <ResponsiveContainer width="100%" height={320}>
            <AreaChart data={tendanceData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis stroke="#6b7280" />
              <Tooltip contentStyle={{ borderRadius: 12 }} />
              <Legend />
              <Area type="monotone" dataKey="patients" stroke="#3b82f6" fill="#dbeafe" />
              <Area type="monotone" dataKey="medecins" stroke="#10b981" fill="#d1fae5" />
              <Area type="monotone" dataKey="rdv" stroke="#8b5cf6" fill="#ede9fe" />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold text-gray-900 mb-6">
            Revenus générés
          </h3>
          <ResponsiveContainer width="100%" height={320}>
            <LineChart data={revenusData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="mois" />
              <YAxis tickFormatter={(v) => `${v / 1000000}M`} />
              <Tooltip />
              <Legend />
              <Line
                type="monotone"
                dataKey="revenus"
                stroke="#f59e0b"
                strokeWidth={3}
                dot={{ r: 6 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* ACTIVITÉ */}
      <div className="grid grid-cols-1 xl:grid-cols-3 gap-10">
        <div className="xl:col-span-2 bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold mb-6">Activité récente</h3>
          <div className="space-y-4">
            {recentActivity.map((a) => (
              <div
                key={a.id}
                className="flex items-center p-4 bg-gray-50 rounded-xl hover:bg-gray-100 transition"
              >
                <div className="w-12 h-12 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-xl flex items-center justify-center text-white font-bold">
                  ✓
                </div>
                <div className="ml-4 flex-1">
                  <p className="font-medium text-gray-900">{a.action}</p>
                  <p className="text-sm text-gray-500">{a.user}</p>
                </div>
                <span className="text-xs text-gray-400">{a.time}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold mb-6">Actions rapides</h3>
          <div className="space-y-4">
            <Link className="block bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-4 rounded-xl text-center font-medium hover:scale-105 transition" to="/admin/utilisateurs">
              Nouvel utilisateur
            </Link>
            <Link className="block bg-gradient-to-r from-emerald-600 to-green-600 text-white p-4 rounded-xl text-center font-medium hover:scale-105 transition" to="/admin/services">
              Gérer services
            </Link>
          </div>
        </div>
      </div>
    </>
  );
};

/* ---------------- ADMIN DASHBOARD ---------------- */

const AdminDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);

  const [stats] = useState({
    totalPatients: 1247,
    totalMedecins: 89,
    totalRdv: 456,
    revenue: 2450000
  });

  const [recentActivity, setRecentActivity] = useState([]);

  useEffect(() => {
    const token = getAuthToken();
    if (!token) return navigate('/login');

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Admin') return navigate('/dashboard');

    setUser(decoded);
    setRecentActivity([
      { id: 1, action: 'Nouveau patient inscrit', user: 'Mamadou Diop', time: '2 min' },
      { id: 2, action: 'Médecin validé', user: 'Dr Awa Ndiaye', time: '15 min' },
      { id: 3, action: 'RDV confirmé', user: 'Service Pédiatrie', time: '1h' }
    ]);
  }, [navigate]);

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="lg:ml-64">
        <header className="sticky top-0 z-40 bg-white/80 backdrop-blur border-b border-gray-100">
          <div className="h-16 px-6 flex items-center justify-between">
            <button className="lg:hidden" onClick={() => setSidebarOpen(true)}>
              <FiMenu size={22} />
            </button>

            <div className="flex items-center gap-4">
              <FiBell className="text-gray-500" />
              <div className="w-9 h-9 rounded-full bg-gradient-to-r from-blue-600 to-indigo-600 text-white flex items-center justify-center font-bold">
                {user?.email?.[0]?.toUpperCase() || 'A'}
              </div>
            </div>
          </div>
        </header>

        <main className="p-8">
          <Routes>
            <Route path="/" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
            <Route path="/patients" element={<Patients />} />
            <Route path="/medecins" element={<Medecins />} />
            <Route path="/rdv" element={<Rdv />} />
            <Route path="/services" element={<Services />} />
            <Route path="/finances" element={<Finances />} />
            <Route path="/moderateurs" element={<Moderateurs />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
