import React, { useState, useEffect, useCallback } from 'react';
import { getData, postData } from '../api/endpoint';

const DoctorDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('dossiers');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [formData, setFormData] = useState({
    patientFullName: '',
    patientEmail: '',
    dateOfBirth: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });

  const loadDossiers = useCallback(async () => {
    try {
      setLoading(true);
      // Mock data pour tester imm√©diatement
      const mockDossiers = [
        {
          _id: '1',
          patientId: {
            fullName: 'Marie Dubois',
            email: 'marie.dubois@clinique.fr',
            date_of_birth: '1990-05-12',
            sexe: 'F'
          },
          resumeMedical: 'Diab√®te type 2 contr√¥l√©',
          groupeSanguin: 'A+'
        }
      ];
      setDossiers(mockDossiers);
    } catch (err) {
      console.error('Erreur dossiers:', err);
      setDossiers([]);
    } finally {
      setLoading(false);
    }
  }, []);

  const loadPatients = useCallback(async () => {
    try {
      // Mock patients
      const mockPatients = [
        { _id: '1', fullName: 'Marie Dubois', email: 'marie@exemple.com' },
        { _id: '2', fullName: 'Jean Martin', email: 'jean@exemple.com' }
      ];
      setPatients(mockPatients);
    } catch (err) {
      console.error('Erreur patients:', err);
      setPatients([]);
    }
  }, []);

  useEffect(() => {
    loadDossiers();
    loadPatients();
  }, [loadDossiers, loadPatients]);

  const handleCreateDossier = async (e) => {
    e.preventDefault();
    try {
      console.log('Nouveau dossier:', formData);
      // await postData('/dossier', formData);
      alert('‚úÖ Dossier cr√©√© avec succ√®s !');
      setShowCreateModal(false);
      setFormData({
        patientFullName: '',
        patientEmail: '',
        dateOfBirth: '',
        sexe: 'M',
        groupeSanguin: '',
        resumeMedical: '',
        allergies: ''
      });
      loadDossiers();
    } catch (err) {
      alert('‚ùå Erreur: ' + err.message);
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '1400px', margin: '0 auto' }}>
      <header style={{ marginBottom: '2rem', textAlign: 'center' }}>
        <h1 style={{ color: '#1976d2', marginBottom: '0.5rem' }}>üè• Espace M√©decin</h1>
        <p>Bienvenue dans votre tableau de bord</p>
      </header>

      {/* Navigation onglets */}
      <div style={{ marginBottom: '2rem', display: 'flex', gap: '1rem' }}>
        <button 
          onClick={() => setActiveTab('dossiers')}
          style={{
            flex: 1, padding: '1rem', 
            background: activeTab === 'dossiers' ? '#1976d2' : '#f5f5f5',
            color: activeTab === 'dossiers' ? 'white' : '#333',
            border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer'
          }}
        >
          üìÅ Dossiers ({dossiers.length})
        </button>
        <button 
          onClick={() => setActiveTab('patients')}
          style={{
            flex: 1, padding: '1rem', 
            background: activeTab === 'patients' ? '#1976d2' : '#f5f5f5',
            color: activeTab === 'patients' ? 'white' : '#333',
            border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer'
          }}
        >
          üë• Patients ({patients.length})
        </button>
      </div>

      {/* Bouton Cr√©er Dossier */}
      <div style={{ marginBottom: '2rem' }}>
        <button 
          onClick={() => setShowCreateModal(true)}
          style={{
            padding: '1.2rem 2.5rem',
            background: 'linear-gradient(45deg, #4caf50, #45a049)',
            color: 'white', fontSize: '1.2rem', fontWeight: 'bold',
            border: 'none', borderRadius: '12px', cursor: 'pointer',
            boxShadow: '0 6px 20px rgba(76,175,80,0.4)',
            transition: 'all 0.3s'
          }}
          onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
          onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
        >
          ‚ûï Cr√©er Nouveau Dossier
        </button>
      </div>

      {/* Contenu des onglets */}
      {activeTab === 'dossiers' && (
        <DossiersTable dossiers={dossiers} loading={loading} />
      )}
      
      {activeTab === 'patients' && (
        <PatientsTable patients={patients} />
      )}

      {/* Modal Cr√©ation Dossier */}
      {showCreateModal && (
        <CreateDossierModal 
          formData={formData}
          setFormData={setFormData}
          onClose={() => setShowCreateModal(false)}
          onSubmit={handleCreateDossier}
        />
      )}
    </div>
  );
};

// Composant Tableau Dossiers (INT√âGR√â)
const DossiersTable = ({ dossiers, loading }) => (
  <div style={{ background: 'white', borderRadius: '12px', boxShadow: '0 8px 32px rgba(0,0,0,0.1)', overflow: 'hidden' }}>
    {loading ? (
      <div style={{ padding: '4rem', textAlign: 'center', color: '#666' }}>
        <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>‚è≥</div>
        Chargement des dossiers...
      </div>
    ) : dossiers.length === 0 ? (
      <div style={{ padding: '4rem', textAlign: 'center', color: '#999' }}>
        <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìÇ</div>
        <h3>Aucun dossier trouv√©</h3>
        <p>Cr√©ez le premier dossier patient en cliquant sur le bouton ci-dessus</p>
      </div>
    ) : (
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: 'linear-gradient(45deg, #1976d2, #1565c0)' }}>
            <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Patient</th>
            <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Sexe</th>
            <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Naissance</th>
            <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Groupe Sanguin</th>
            <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>R√©sum√© M√©dical</th>
          </tr>
        </thead>
        <tbody>
          {dossiers.map(dossier => (
            <tr key={dossier._id} style={{ borderBottom: '1px solid #eee' }}>
              <td style={{ padding: '1.5rem 1rem' }}>
                <div style={{ fontWeight: 'bold', color: '#1976d2' }}>
                  {dossier.patientId?.fullName || 'N/A'}
                </div>
                <div style={{ fontSize: '0.9rem', color: '#666' }}>
                  {dossier.patientId?.email || ''}
                </div>
              </td>
              <td style={{ padding: '1.5rem 1rem', fontWeight: '500' }}>
                {dossier.patientId?.sexe || 'N/A'}
              </td>
              <td style={{ padding: '1.5rem 1rem' }}>
                {dossier.patientId?.date_of_birth ? 
                  new Date(dossier.patientId.date_of_birth).toLocaleDateString('fr-FR') : 'N/A'}
              </td>
              <td style={{ padding: '1.5rem 1rem', fontWeight: 'bold' }}>
                {dossier.groupeSanguin || 'N/A'}
              </td>
              <td style={{ padding: '1.5rem 1rem', maxWidth: '400px' }}>
                <div>{dossier.resumeMedical || 'Aucune info'}</div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    )}
  </div>
);

// Composant Tableau Patients (INT√âGR√â)
const PatientsTable = ({ patients }) => (
  <div style={{ background: 'white', borderRadius: '12px', boxShadow: '0 8px 32px rgba(0,0,0,0.1)', overflow: 'hidden' }}>
    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
      <thead>
        <tr style={{ background: 'linear-gradient(45deg, #ff9800, #f57c00)' }}>
          <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Nom</th>
          <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Email</th>
          <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>T√©l√©phone</th>
          <th style={{ padding: '1.5rem 1rem', color: 'white', textAlign: 'left' }}>Cr√©√© le</th>
        </tr>
      </thead>
      <tbody>
        {patients.map(patient => (
          <tr key={patient._id} style={{ borderBottom: '1px solid #eee' }}>
            <td style={{ padding: '1.5rem 1rem', fontWeight: 'bold' }}>{patient.fullName}</td>
            <td style={{ padding: '1.5rem 1rem' }}>{patient.email}</td>
            <td style={{ padding: '1.5rem 1rem' }}>{patient.telephone || 'N/A'}</td>
            <td style={{ padding: '1.5rem 1rem', color: '#666' }}>01/02/2026</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

// Modal Cr√©ation Dossier (INT√âGR√â)
const CreateDossierModal = ({ formData, setFormData, onClose, onSubmit }) => {
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center',
      zIndex: 10000
    }}>
      <div style={{
        background: 'white', padding: '2.5rem', borderRadius: '16px', maxWidth: '700px',
        width: '90%', maxHeight: '90vh', overflowY: 'auto', boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier Patient</h2>
          <button onClick={onClose} style={{
            background: 'none', border: 'none', fontSize: '2rem', cursor: 'pointer', color: '#666'
          }}>√ó</button>
        </div>

        <form onSubmit={onSubmit} style={{ display: 'grid', gap: '1.5rem' }}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <input
              name="patientFullName"
              placeholder="Nom complet *"
              value={formData.patientFullName}
              onChange={handleChange}
              required
              style={inputStyle}
            />
            <input
              name="patientEmail"
              type="email"
              placeholder="Email *"
              value={formData.patientEmail}
              onChange={handleChange}
              required
              style={inputStyle}
            />
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <input
              name="dateOfBirth"
              type="date"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
              style={inputStyle}
            />
            <select name="sexe" value={formData.sexe} onChange={handleChange} style={inputStyle}>
              <option value="M">Masculin</option>
              <option value="F">F√©minin</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>Groupe sanguin</label>
            <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleChange} style={inputStyle}>
              <option value="">S√©lectionner...</option>
              <option value="A+">A+</option><option value="A-">A-</option>
              <option value="B+">B+</option><option value="B-">B-</option>
              <option value="AB+">AB+</option><option value="AB-">AB-</option>
              <option value="O+">O+</option><option value="O-">O-</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>R√©sum√© m√©dical</label>
            <textarea
              name="resumeMedical"
              placeholder="Ant√©c√©dents, diagnostics actuels..."
              value={formData.resumeMedical}
              onChange={handleChange}
              rows={4}
              style={{ ...inputStyle, width: '100%', resize: 'vertical' }}
            />
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>Allergies</label>
            <textarea
              name="allergies"
              placeholder="Aucun connu ou liste des allergies..."
              value={formData.allergies}
              onChange={handleChange}
              rows={2}
              style={{ ...inputStyle, width: '100%', resize: 'vertical' }}
            />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" style={submitButtonStyle}>üíæ Cr√©er Dossier</button>
            <button type="button" onClick={onClose} style={cancelButtonStyle}>‚ùå Annuler</button>
          </div>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  padding: '1rem 1.2rem', border: '2px solid #e0e0e0', borderRadius: '8px',
  fontSize: '1rem', transition: 'border-color 0.3s', width: '100%'
};

const submitButtonStyle = {
  flex: 1, padding: '1.2rem', background: 'linear-gradient(45deg, #4caf50, #45a049)',
  color: 'white', border: 'none', borderRadius: '8px', fontSize: '1.1rem',
  fontWeight: 'bold', cursor: 'pointer', boxShadow: '0 4px 15px rgba(76,175,80,0.4)'
};

const cancelButtonStyle = {
  flex: 1, padding: '1.2rem', background: '#f44336', color: 'white',
  border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer'
};

export default DoctorDashboard;
