import React, { useState } from 'react';

const MedecinDashboard = () => {
  const [activeTab, setActiveTab] = useState('ordonnances');

  const [patients, setPatients] = useState([
    { id: 1, name: 'Diop Aminata', age: 32, lastVisit: '08/02/2026', status: 'actif' },
    { id: 2, name: 'Ndiaye Fatou', age: 45, lastVisit: '07/02/2026', status: 'actif' },
    { id: 3, name: 'Sow Mamadou', age: 28, lastVisit: '06/02/2026', status: 'en attente' }
  ]);

  const [ordonnance, setOrdonnance] = useState({
    patient: '',
    medicaments: [
      { nom: 'Parac√©tamol 1g', dose: '1cp', frequence: '3x/jour', duree: '5 jours' },
      { nom: 'Amoxicilline 500mg', dose: '1cp', frequence: '2x/jour', duree: '7 jours' }
    ]
  });

  const addMedicament = () => {
    setOrdonnance({
      ...ordonnance,
      medicaments: [...ordonnance.medicaments, { nom: '', dose: '', frequence: '', duree: '' }]
    });
  };

  const updateMedicament = (index, field, value) => {
    const newMeds = [...ordonnance.medicaments];
    newMeds[index][field] = value;
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const removeMedicament = (index) => {
    const newMeds = ordonnance.medicaments.filter((_, i) => i !== index);
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const saveOrdonnance = () => {
    if (!ordonnance.patient) {
      alert('‚ùå Veuillez s√©lectionner un patient !');
      return;
    }

    alert(
      '‚úÖ ORDONNANCE G√âN√âR√âE !\n\n' +
        `Patient: ${ordonnance.patient}\n\n` +
        ordonnance.medicaments
          .map(
            (m, i) =>
              `${i + 1}. ${m.nom || '---'}\n   ${m.dose} - ${m.frequence} - ${m.duree}`
          )
          .join('\n')
    );
  };

  const stats = {
    patients: patients.length,
    ordonnances: 47,
    rdvsToday: 8,
    revenus: 1250000
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg,#10b981,#047857)', padding: 20 }}>
      <div style={{ maxWidth: 1400, margin: '0 auto', color: 'white' }}>

        {/* NAV */}
        <div style={{ display: 'flex', gap: 10, marginBottom: 30 }}>
          <TabButton label="üíä Ordonnances" active={activeTab === 'ordonnances'} onClick={() => setActiveTab('ordonnances')} />
          <TabButton label={`üë• Patients (${stats.patients})`} active={activeTab === 'patients'} onClick={() => setActiveTab('patients')} />
          <TabButton label="üìÖ RDV" active={activeTab === 'rdv'} onClick={() => setActiveTab('rdv')} />
          <TabButton label="üìä Stats" active={activeTab === 'stats'} onClick={() => setActiveTab('stats')} />
        </div>

        {activeTab === 'ordonnances' && (
          <OrdonnancesSection
            ordonnance={ordonnance}
            patients={patients}
            setOrdonnance={setOrdonnance}
            addMedicament={addMedicament}
            updateMedicament={updateMedicament}
            removeMedicament={removeMedicament}
            saveOrdonnance={saveOrdonnance}
          />
        )}

        {activeTab === 'patients' && <PatientsSection patients={patients} />}
        {activeTab === 'rdv' && <RdvSection />}
        {activeTab === 'stats' && <StatsSection />}
      </div>
    </div>
  );
};

const OrdonnancesSection = ({
  ordonnance,
  patients,
  setOrdonnance,
  addMedicament,
  updateMedicament,
  removeMedicament,
  saveOrdonnance
}) => (
  <div>
    <h2>üíä Nouvelle Ordonnance</h2>

    <select
      value={ordonnance.patient}
      onChange={(e) => setOrdonnance({ ...ordonnance, patient: e.target.value })}
    >
      <option value="">S√©lectionner un patient</option>
      {patients.map((p) => (
        <option key={p.id} value={`${p.name} - ${p.age} ans`}>
          {p.name} - {p.age} ans
        </option>
      ))}
    </select>

    {ordonnance.medicaments.map((med, index) => (
      <MedicamentRow
        key={index}
        medicament={med}
        index={index}
        onUpdate={updateMedicament}
        onRemove={removeMedicament}
      />
    ))}

    <button onClick={addMedicament}>‚ûï Ajouter</button>
    <button onClick={saveOrdonnance}>‚úÖ Finaliser</button>
  </div>
);

const MedicamentRow = ({ medicament, index, onUpdate, onRemove }) => (
  <div style={{ display: 'flex', gap: 10 }}>
    <input value={medicament.nom} onChange={(e) => onUpdate(index, 'nom', e.target.value)} />
    <input value={medicament.dose} onChange={(e) => onUpdate(index, 'dose', e.target.value)} />
    <input value={medicament.frequence} onChange={(e) => onUpdate(index, 'frequence', e.target.value)} />
    <input value={medicament.duree} onChange={(e) => onUpdate(index, 'duree', e.target.value)} />
    <button onClick={() => onRemove(index)}>üóëÔ∏è</button>
  </div>
);

const TabButton = ({ label, active, onClick }) => (
  <button onClick={onClick} style={{ fontWeight: active ? 'bold' : 'normal' }}>
    {label}
  </button>
);

const PatientsSection = ({ patients }) => (
  <div>
    <h2>üë• Patients</h2>
    {patients.map((p) => (
      <div key={p.id}>
        {p.name} ‚Äî {p.age} ans ‚Äî {p.status}
      </div>
    ))}
  </div>
);

const RdvSection = () => <h2>üìÖ RDV</h2>;
const StatsSection = () => <h2>üìä Stats</h2>;

export default MedecinDashboard;
