import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    sexe: '',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  
  // ‚úÖ VOTRE ID M√âDECIN (OBLIGATOIRE)
  const MEDECIN_ID = "PASTE_VOTRE_ID_MONGODB_ICI"; 
  
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatients, setShowPatients] = useState(false);

  useEffect(() => {
    getData('/patients')
      .then(data => setPatients(Array.isArray(data) ? data : []))
      .catch(() => setPatients([]));
  }, []);

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom,
      sexe: patient.sexe || '',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatients(false);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.resumeMedical) {
      alert('Patient et r√©sum√© obligatoires');
      return;
    }

    setLoading(true);
    
    try {
      // ‚úÖ FORMAT SIMPLE - AUCUN undefined
      const data = {
        patientId: formData.patientId,
        medecinId: MEDECIN_ID,
        resumeMedical: formData.resumeMedical,
        groupeSanguin: formData.groupeSanguin || null,
        sexe: formData.sexe,
        allergies: formData.allergies ? [{ allergie: formData.allergies }] : []
      };

      console.log('üì§ Envoi:', data);
      const result = await postData('/dossier', data);
      console.log('‚úÖ Succ√®s:', result);
      
      alert('‚úÖ Dossier cr√©√© !');
      onSuccess();
      
    } catch (error) {
      console.error('‚ùå Erreur:', error);
      
      // Local fallback
      localStorage.setItem('dossiersMedecin', JSON.stringify([{
        _id: Date.now().toString(),
        ...formData,
        medecinId: MEDECIN_ID
      }]));
      
      alert('‚úÖ Sauvegard√© localement');
      onSuccess();
    } finally {
      setLoading(false);
    }
  };

  const style = {
    input: 'padding:1rem;border:2px solid #ddd;border-radius:8px;width:100%;font-size:1rem',
    btn: 'padding:1rem;background:#4caf50;color:white;border:none;border-radius:8px;cursor:pointer;font-weight:bold'
  };

  return (
    <div style={{position:'fixed',zIndex:10000,inset:0,background:'rgba(0,0,0,0.5)',display:'flex',alignItems:'center',justifyContent:'center'}}>
      <div style={{background:'white',padding:'2rem',borderRadius:'16px',maxWidth:'600px',width:'90%'}}>
        <div style={{display:'flex',justifyContent:'space-between',marginBottom:'1.5rem'}}>
          <h2 style={{color:'#1976d2',margin:0}}>‚ûï Nouveau Dossier</h2>
          <button onClick={onClose} style={{fontSize:'1.5rem',cursor:'pointer'}}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* PATIENT */}
          <button 
            type="button" 
            onClick={() => setShowPatients(!showPatients)}
            style={{...style.btn, width:'100%', background: formData.patientId ? '#4caf50' : '#2196f3'}}
          >
            {formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ Choisir patient'}
          </button>

          {showPatients && patients.map(p => (
            <div 
              key={p._id} 
              onClick={() => selectPatient(p)}
              style={{padding:'1rem',cursor:'pointer',borderBottom:'1px solid #eee',background: formData.patientId===p._id?'#e3f2fd':''}}
            >
              {p.fullName || p.nom} - {p.email}
            </div>
          ))}

          {/* FORMULAIRE */}
          {formData.patientId && (
            <>
              <div style={{padding:'1rem',background:'#e8f5e8',borderRadius:'8px',margin:'1rem 0'}}>
                ‚úÖ {formData.patientFullName}
              </div>

              <div style={{display:'grid',gridTemplateColumns:'1fr 1fr',gap:'1rem',marginBottom:'1rem'}}>
                <div>
                  <label>ü©∏ Groupe sanguin</label>
                  <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleChange} style={style.input}>
                    <option value="">Choisir...</option>
                    <option value="A+">A+</option><option value="A-">A-</option>
                    <option value="B+">B+</option><option value="B-">B-</option>
                    <option value="AB+">AB+</option><option value="AB-">AB-</option>
                    <option value="O+">O+</option><option value="O-">O-</option>
                  </select>
                </div>
                <div>
                  <label>‚öïÔ∏è Sexe</label>
                  <select name="sexe" value={formData.sexe} onChange={handleChange} style={style.input} required>
                    <option value="">Choisir...</option>
                    <option value="M">M üë®</option>
                    <option value="F">F üë©</option>
                  </select>
                </div>
              </div>

              <div style={{marginBottom:'1rem'}}>
                <label>üìù R√©sum√© m√©dical *</label>
                <textarea 
                  name="resumeMedical" 
                  value={formData.resumeMedical} 
                  onChange={handleChange}
                  rows="3" 
                  style={{...style.input, minHeight:'100px'}} 
                  required 
                  placeholder="Diagnostic, traitements..."
                />
              </div>

              <div style={{display:'flex',gap:'1rem'}}>
                <button type="submit" disabled={loading} style={{...style.btn, flex:1, background: loading?'#ccc':'#4caf50'}}>
                  {loading ? '‚è≥ Cr√©ation...' : 'üíæ Cr√©er'}
                </button>
                <button type="button" onClick={onClose} style={{...style.btn, flex:1, background:'#f44336'}}>‚ùå Annuler</button>
              </div>
            </>
          )}
        </form>
      </div>
    </div>
  );
};

export default CreateDossierModal;
