import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import api, { getAuthToken, decodeToken, removeAuthToken } from '../../api/endpoint';
import { 
  FiUsers, FiActivity, FiCalendar, FiDollarSign, FiBarChart, FiBell, FiSettings, FiLogOut, FiMenu, FiX, FiFileText
} from 'react-icons/fi';

// ✅ Imports des pages admin (toutes vos pages corrigées)
import Patients from './admin/Patients';
import Medecins from './admin/Medecins';
import Services from './admin/Services';
import Rdv from './admin/Rdv';
import Finances from './admin/Finances';
import Moderateurs from './admin/Moderateurs';
import Journal from './admin/Journal';
import Ordonnances from './admin/Ordonnances';
import Consultations from './admin/Consultations';

// ✅ Recharts pour graphiques
import { LineChart, Line, AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const DashboardHome = ({ stats, recentActivity }) => {
  const data = [
    { name: 'Jan', value: 400 },
    { name: 'Fév', value: 300 },
    { name: 'Mar', value: 500 },
    { name: 'Avr', value: 450 },
    { name: 'Mai', value: 600 }
  ];

  return (
    <div className="space-y-8">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 hover:shadow-md transition">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-xl">
              <FiUsers className="h-6 w-6 text-blue-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Patients</p>
              <p className="text-2xl font-bold text-gray-900">{stats.totalPatients}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 hover:shadow-md transition">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-xl">
              <FiUsers className="h-6 w-6 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Médecins</p>
              <p className="text-2xl font-bold text-gray-900">{stats.totalMedecins}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 hover:shadow-md transition">
          <div className="flex items-center">
            <div className="p-3 bg-purple-100 rounded-xl">
              <FiCalendar className="h-6 w-6 text-purple-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Rdv</p>
              <p className="text-2xl font-bold text-gray-900">{stats.totalRdv}</p>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 hover:shadow-md transition">
          <div className="flex items-center">
            <div className="p-3 bg-yellow-100 rounded-xl">
              <FiDollarSign className="h-6 w-6 text-yellow-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Revenus</p>
              <p className="text-2xl font-bold text-gray-900">{stats.revenue?.toLocaleString()} FCFA</p>
            </div>
          </div>
        </div>
      </div>

      {/* Graphiques + Activité */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 bg-white rounded-xl shadow-sm border border-gray-100 p-6">
          <h3 className="text-lg font-semibold mb-4">Évolution des RDV</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="value" stroke="#3B82F6" />
            </LineChart>
          </ResponsiveContainer>
        </div>
        <div className="bg-white rounded-xl shadow-sm border border-gray-100 p-6">
          <h3 className="font-semibold mb-4">Activité récente</h3>
          <div className="space-y-3">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="flex items-center p-3 bg-gray-50 rounded-lg">
                <div className="w-2 h-2 bg-green-400 rounded-full mr-3"></div>
                <div>
                  <p className="text-sm font-medium text-gray-900">{activity.action}</p>
                  <p className="text-xs text-gray-500">{activity.user} • {activity.time}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const AdminDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({
    totalPatients: 0, totalMedecins: 0, totalRdv: 0, revenue: 0
  });
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const init = async () => {
      const token = getAuthToken();
      if (!token) {
        navigate('/login');
        return;
      }

      const decoded = decodeToken(token);
      if (decoded?.role !== 'Admin') {
        navigate(decoded?.role === 'Medecin' ? '/medecin' : '/dashboard');
        return;
      }

      setUser(decoded);
      await loadAdminData();
      setLoading(false);
    };

    init();
  }, [navigate]);

  const loadAdminData = async () => {
    try {
      const [patientsData, usersData, rdvData] = await Promise.all([
        api.getData('/users?role=Patient'),
        api.getData('/users?role=Medecin'),
        api.getData('/rendezvous')
      ]);

      setStats({
        totalPatients: Array.isArray(patientsData) ? patientsData.length : 0,
        totalMedecins: Array.isArray(usersData) ? usersData.length : 0,
        totalRdv: Array.isArray(rdvData) ? rdvData.length : 0,
        revenue: 2450000
      });

      setRecentActivity([
        { id: 1, action: 'Nouveau patient inscrit', user: 'Mamadou Diop', time: '2min' },
        { id: 2, action: 'Médecin ajouté', user: 'Dr. Awa Ndiaye', time: '15min' },
        { id: 3, action: 'RDV confirmé', user: 'Service Pédiatrie', time: '1h' }
      ]);
    } catch (err) {
      console.error('Erreur admin data:', err);
    }
  };

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/admin', exact: true },
    { name: 'Patients', icon: FiUsers, href: '/admin/patients' },
    { name: 'Médecins', icon: FiUsers, href: '/admin/medecins' },
    { name: 'Consultations', icon: FiActivity, href: '/admin/consultations' },
    { name: 'Ordonnances', icon: FiFileText, href: '/admin/ordonnances' },
    { name: 'Rendez-vous', icon: FiCalendar, href: '/admin/rdv' },
    { name: 'Services', icon: FiActivity, href: '/admin/services' },
    { name: 'Journal', icon: FiFileText, href: '/admin/journal' },
    { name: 'Finances', icon: FiDollarSign, href: '/admin/finances' },
    { name: 'Modérateurs', icon: FiUsers, href: '/admin/moderateurs' },
    { name: 'Paramètres', icon: FiSettings, href: '/admin/settings' }
  ];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p>Chargement du dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`fixed inset-0 z-40 lg:static lg:z-auto bg-black/10 backdrop-blur-sm lg:bg-transparent transition-all ${sidebarOpen ? 'block' : 'hidden lg:block'}`}>
        <div className="lg:ml-0 h-full bg-white shadow-lg">
          <div className="p-6 border-b border-gray-100">
            <h1 className="text-2xl font-bold text-gray-900">Admin Panel</h1>
          </div>
          <nav className="p-4">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center p-3 rounded-xl mb-2 transition-all ${
                  location.pathname === item.href || (item.exact && location.pathname === item.href)
                    ? 'bg-blue-50 text-blue-600 border-2 border-blue-200'
                    : 'text-gray-700 hover:bg-gray-100 hover:text-gray-900'
                }`}
              >
                <item.icon className="h-5 w-5 mr-3" />
                {item.name}
              </Link>
            ))}
          </nav>
        </div>
      </div>

      {/* Mobile overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-30 bg-black/50 lg:hidden" 
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main content */}
      <div className="lg:ml-64">
        <header className="bg-white shadow-sm border-b border-gray-200 p-4 lg:p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                className="lg:hidden p-2 rounded-lg text-gray-500 hover:bg-gray-100 mr-4"
              >
                <FiMenu className="h-6 w-6" />
              </button>
              <h2 className="text-xl font-semibold text-gray-900">
                {location.pathname === '/admin' ? 'Dashboard' : navigation.find(n => location.pathname.includes(n.href.split('/')[2]))?.name || 'Tableau de bord'}
              </h2>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center bg-gray-100 px-3 py-1 rounded-full text-sm">
                <FiBell className="h-4 w-4 mr-1" />
                <span>3</span>
              </div>
              <button
                onClick={logout}
                className="flex items-center text-gray-700 hover:text-gray-900 p-2 rounded-lg hover:bg-gray-100"
              >
                <FiLogOut className="h-5 w-5" />
              </button>
            </div>
          </div>
        </header>

        <main style={{ padding: '20px', minHeight: 'calc(100vh - 64px)' }}>
          <Routes>
            <Route path="/" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
            <Route path="/patients" element={<Patients />} />
            <Route path="/medecins" element={<Medecins />} />
            <Route path="/rdv" element={<Rdv />} />
            <Route path="/services" element={<Services />} />
            <Route path="/finances" element={<Finances />} />
            <Route path="/moderateurs" element={<Moderateurs />} />
            <Route path="/consultations" element={<Consultations />} />
            <Route path="/ordonnances" element={<Ordonnances />} />
            <Route path="/journal" element={<Journal />} />
            <Route path="*" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
