// PatientDashboard.js
import React, { useEffect, useState } from "react";
import { getData } from "../api/endpoint";

// Mock API pour tester le frontend
const fetchDossiers = async () => {
  return [
    { id: 1, nom: "Dossier médical général", dateCreation: "2025-12-01", consultations: 3 },
    { id: 2, nom: "Dossier cardiologie", dateCreation: "2025-12-05", consultations: 1 },
  ];
};

const fetchConsultations = async () => {
  return [
    { id: 1, medecin: "Dr. Diallo", date: "2026-02-05", motif: "Consultation générale" },
    { id: 2, medecin: "Dr. Ba", date: "2026-02-07", motif: "Suivi tension" },
  ];
};

const fetchRendezVous = async () => {
  return [
    { id: 1, medecin: "Dr. Diallo", date: "2026-02-10 09:00", statut: "EN_ATTENTE", rang: 5 },
    { id: 2, medecin: "Dr. Ba", date: "2026-02-12 14:00", statut: "EN_ATTENTE", rang: 3 },
  ];
};

const PatientDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [consultations, setConsultations] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const d = await fetchDossiers();
      const c = await fetchConsultations();
      const r = await fetchRendezVous();

      setDossiers(d);
      setConsultations(c);
      setRendezVous(r);
    };

    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Tableau de bord patient</h1>

      <section style={{ marginBottom: "30px" }}>
        <h2>Mes dossiers</h2>
        {dossiers.length === 0 ? (
          <p>Aucun dossier disponible</p>
        ) : (
          <ul>
            {dossiers.map((d) => (
              <li key={d.id}>
                <strong>{d.nom}</strong> - Créé le {d.dateCreation} - {d.consultations} consultations
              </li>
            ))}
          </ul>
        )}
      </section>

      <section style={{ marginBottom: "30px" }}>
        <h2>Mes consultations</h2>
        {consultations.length === 0 ? (
          <p>Aucune consultation</p>
        ) : (
          <ul>
            {consultations.map((c) => (
              <li key={c.id}>
                {c.date} - {c.medecin} - Motif: {c.motif}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h2>Mes rendez-vous</h2>
        {rendezVous.length === 0 ? (
          <p>Aucun rendez-vous</p>
        ) : (
          <ul>
            {rendezVous.map((r) => (
              <li key={r.id}>
                {r.date} - {r.medecin} - Statut: {r.statut} - Rang: {r.rang}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default PatientDashboard;
