import React, { useState, useEffect } from 'react';
import getData from '../../api/endpoint';
import { FiFileText, FiCalendar } from 'react-icons/fi';

const MesOrdonnances = () => {
  const [ordonnances, setOrdonnances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadOrdonnances();
  }, []);

  const loadOrdonnances = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      if (token) {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        const data = await getData('/ordonnances');
        const patientOrdonnances = data.filter(ord => ord.patientId === decoded.userId);
        setOrdonnances(patientOrdonnances);
      }
    } catch (err) {
      console.error('Erreur lors du chargement des ordonnances:', err);
      setError('Impossible de charger les ordonnances.');
      setOrdonnances([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-600">Chargement...</div>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Mes Ordonnances</h2>
        <p className="text-gray-600 mt-2">Consultez vos ordonnances médicales</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {ordonnances.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
          <FiFileText size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">Aucune ordonnance</h3>
          <p className="text-gray-600">Vous n'avez pas encore d'ordonnances médicales.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6">
          {ordonnances.map((ordonnance) => (
            <div key={ordonnance._id} className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100 hover:shadow-xl transition-all">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FiFileText className="text-blue-500 mr-2" size={24} />
                  <h3 className="text-xl font-bold text-gray-900">Ordonnance #{ordonnance._id.slice(-6)}</h3>
                </div>
                <div className="flex items-center text-gray-600">
                  <FiCalendar className="mr-2" size={16} />
                  <span>{new Date(ordonnance.dateOrdonnance || ordonnance.createdAt).toLocaleDateString('fr-FR')}</span>
                </div>
              </div>
              
              {ordonnance.medicaments && ordonnance.medicaments.length > 0 && (
                <div className="mt-4">
                  <h4 className="font-semibold text-gray-900 mb-2">Médicaments prescrits:</h4>
                  <ul className="space-y-2">
                    {ordonnance.medicaments.map((med, index) => (
                      <li key={index} className="text-gray-700 flex items-start">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-2"></span>
                        <div>
                          <span className="font-medium">{med.nom || med.medicament}</span>
                          {med.posologie && <span className="text-gray-600 ml-2">- {med.posologie}</span>}
                        </div>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
              
              {ordonnance.instructions && (
                <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                  <h4 className="font-semibold text-gray-900 mb-2">Instructions:</h4>
                  <p className="text-gray-700">{ordonnance.instructions}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesOrdonnances;
