import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api/endpoint';
import { FiFileText, FiUser, FiAlertCircle, FiHeart, FiUserCheck } from 'react-icons/fi';

const CreerDossier = () => {
  const navigate = useNavigate();
  const [patients, setPatients] = useState([]);
  const [medecins, setMedecins] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [formData, setFormData] = useState({
    patientId: '',
    medecinId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: { nom: '', telephone: '', lien: '' },
    statut: 'ACTIF'
  });
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');

  useEffect(() => { loadData(); }, []);

  const loadData = async () => {
    try {
      const [usersData, dossiersData] = await Promise.all([
        api.getData('/users'),
        api.getData('/dossiers')
      ]);
      
      const patientsList = usersData.filter(user => user.role === 'Patient');
      const patientsAvecDossier = dossiersData
        .filter(d => d.statut === 'ACTIF')
        .map(d => d.patientId);
      
      setPatients(patientsList.filter(p => !patientsAvecDossier.includes(p._id)));
      setMedecins(usersData.filter(user => user.role === 'Medecin'));
    } catch (err) {
      setError('Impossible de charger les données.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        contactUrgence: { ...prev.contactUrgence, [field]: value }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
    setError(null);
  };

  const addAntecedent = () => {
    if (newAntecedent.trim()) {
      setFormData(prev => ({
        ...prev,
        antecedents: [...prev.antecedents, { antecedent: newAntecedent.trim() }]
      }));
      setNewAntecedent('');
    }
  };

  const removeAntecedent = (index) => {
    setFormData(prev => ({
      ...prev,
      antecedents: prev.antecedents.filter((_, i) => i !== index)
    }));
  };

  const addAllergie = () => {
    if (newAllergie.trim()) {
      setFormData(prev => ({
        ...prev,
        allergies: [...prev.allergies, { allergie: newAllergie.trim() }]
      }));
      setNewAllergie('');
    }
  };

  const removeAllergie = (index) => {
    setFormData(prev => ({
      ...prev,
      allergies: prev.allergies.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); setSuccess(false);

    if (!formData.patientId || !formData.medecinId) {
      setError('Patient et médecin obligatoires');
      return;
    }

    try {
      setLoading(true);
      await api.postData('/dossiers', formData);
      setSuccess(true);
      setTimeout(() => navigate('/admin/patients'), 2000);
    } catch (err) {
      setError(err.message || 'Erreur création dossier');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Créer Dossier Médical</h1>
        <p className="text-gray-600 mt-2">Nouveau dossier patient</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded-xl mb-6">
          {error}
        </div>
      )}
      {success && (
        <div className="bg-green-50 border border-green-200 text-green-800 px-4 py-3 rounded-xl mb-6">
          ✅ Dossier créé avec succès !
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Patient *</label>
            <select
              name="patientId"
              value={formData.patientId}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
            >
              <option value="">Sélectionner un patient</option>
              {patients.map(patient => (
                <option key={patient._id} value={patient._id}>
                  {patient.fullName} ({patient.email})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Médecin Responsable *</label>
            <select
              name="medecinId"
              value={formData.medecinId}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
            >
              <option value="">Sélectionner un médecin</option>
              {medecins.map(medecin => (
                <option key={medecin._id} value={medecin._id}>
                  Dr. {medecin.fullName}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Groupe Sanguin</label>
            <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleChange} className="w-full p-3 border border-gray-300 rounded-xl">
              <option value="">Non spécifié</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Résumé Médical</label>
          <textarea
            name="resumeMedical"
            value={formData.resumeMedical}
            onChange={handleChange}
            rows="4"
            className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Antécédents principaux, traitements en cours..."
          />
        </div>

        {/* ANTECEDENTS */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-4">Antécédents</label>
          <div className="flex gap-3 mb-4">
            <input
              value={newAntecedent}
              onChange={(e) => setNewAntecedent(e.target.value)}
              className="flex-1 p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              placeholder="Ex: Diabète type 2..."
            />
            <button type="button" onClick={addAntecedent} className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700">
              Ajouter
            </button>
          </div>
          <div className="space-y-2">
            {formData.antecedents.map((ant, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-xl">
                <span>{ant.antecedent}</span>
                <button type="button" onClick={() => removeAntecedent(index)} className="text-red-500 hover:text-red-700">
                  ×
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* ALLERGIES */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-4">Allergies</label>
          <div className="flex gap-3 mb-4">
            <input
              value={newAllergie}
              onChange={(e) => setNewAllergie(e.target.value)}
              className="flex-1 p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
              placeholder="Ex: Pénicilline, arachides..."
            />
            <button type="button" onClick={addAllergie} className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700">
              Ajouter
            </button>
          </div>
          <div className="space-y-2">
            {formData.allergies.map((all, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-rose-50 border border-rose-200 rounded-xl">
                <span className="font-medium text-rose-800">{all.allergie}</span>
                <button type="button" onClick={() => removeAllergie(index)} className="text-red-500 hover:text-red-700">
                  ×
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* CONTACT URGENCE */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-4">Contact d'Urgence</label>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <input
              name="contactUrgence.nom"
              value={formData.contactUrgence.nom}
              onChange={handleChange}
              placeholder="Nom"
              className="p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
            />
            <input
              name="contactUrgence.telephone"
              value={formData.contactUrgence.telephone}
              onChange={handleChange}
              placeholder="Téléphone"
              className="p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
            />
            <input
              name="contactUrgence.lien"
              value={formData.contactUrgence.lien}
              onChange={handleChange}
              placeholder="Lien (Père, Mère...)"
              className="p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>

        <div className="flex gap-4 pt-6">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 bg-gradient-to-r from-blue-600 to-blue-700 text-white py-4 px-8 rounded-2xl font-semibold hover:shadow-lg transform hover:scale-[1.02] transition-all disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Création...' : 'Créer Dossier'}
          </button>
          <button
            type="button"
            onClick={() => navigate('/admin/patients')}
            className="px-8 py-4 border-2 border-gray-300 text-gray-700 rounded-2xl font-semibold hover:bg-gray-50 hover:shadow-md transition-all"
          >
            Annuler
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreerDossier;
