import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation, useParams } from 'react-router-dom';

const MedecinDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [patients, setPatients] = useState([]);
  const [rendezVous, setRendezVous] = useState([]);
  const [ordonnances, setOrdonnances] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [resultatsExamens, setResultatsExamens] = useState([]); // ðŸ†• NOUVEAU
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState({ open: false, type: null, patientId: null });

  useEffect(() => {
    setTimeout(() => {
      setProfile({
        nom: 'NDIAYE', prenom: 'Moustapha', email: 'moustapha.ndiaye@govsanitaire.sn',
        telephone: '+221 78 987 6543', specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001', etablissement: 'CHU de Fann', statut: 'ValidÃ©'
      });
      
      const mockPatients = [
        { id: '1', nom: 'DIOP', prenom: 'Aminata', age: 35, sexe: 'F', telephone: '+221 78 123 4567', 
          email: 'aminata.diop@email.com', groupeSanguin: 'O+', allergies: 'Aucune', derniereConsult: '08/02/2026' },
        { id: '2', nom: 'SOW', prenom: 'Fatou', age: 42, sexe: 'F', telephone: '+221 77 456 7890', 
          email: 'fatou.sow@email.com', groupeSanguin: 'A+', allergies: 'PÃ©nicilline', derniereConsult: '07/02/2026' }
      ];
      
      setPatients(mockPatients);
      
      // ðŸ†• Rendez-vous avec dates proches pour notifications
      const mockRdv = [
        { id: '1', patientId: '1', patient: 'Aminata DIOP', date: '10/02/2026 10:00', statut: 'ConfirmÃ©', type: 'consultation' },
        { id: '2', patientId: '2', patient: 'Fatou SOW', date: '09/02/2026 14:00', statut: 'ConfirmÃ©', type: 'urgence' }, // RDV demain
        { id: '3', patientId: '1', patient: 'Aminata DIOP', date: '08/02/2026 16:00', statut: 'ConfirmÃ©', type: 'reunion' } // RDV aujourd'hui
      ];
      setRendezVous(mockRdv);
      
      setDossiers([
        { patientId: '1', antecedents: 'HTA lÃ©gÃ¨re', diagnostics: 'Palpitations', notes: 'Suivi rÃ©gulier', analyses: 'ECG normal', id: 'd1' },
        { patientId: '2', antecedents: 'DiabÃ¨te type 2', diagnostics: 'Hypertension', notes: 'Bonne observance', analyses: 'Bilan sanguin OK', id: 'd2' }
      ]);
      
      // ðŸ†• RÃ©sultats d'examens mock
      setResultatsExamens([
        { id: 'r1', patientId: '1', type: 'scanner', titre: 'Scanner thoracique', date: '07/02/2026', resultat: 'Poumon sain', fichier: 'scanner_aminata.pdf' },
        { id: 'r2', patientId: '2', type: 'analyse', titre: 'Bilan sanguin', date: '06/02/2026', resultat: 'HbA1c: 7.2%', fichier: 'bilan_fatou.pdf' }
      ]);
      
      setLoading(false);
      
      // ðŸ†• Notifications automatiques pour urgences/RDV proches
      setTimeout(() => {
        const today = new Date('2026-02-08');
        const tomorrow = new Date('2026-02-09');
        
        mockRdv.forEach(rdv => {
          const rdvDate = new Date(rdv.date.split('/').reverse().join('-'));
          if (rdvDate <= tomorrow && rdv.type === 'urgence') {
            setNotifications(prev => [...prev, {
              id: Date.now(),
              message: `ðŸš¨ URGENCE: ${rdv.patient} - ${rdv.date}`,
              type: 'emergency',
              date: new Date().toLocaleDateString('fr-FR')
            }]);
          } else if (rdvDate <= today && rdvDate.getHours() < 18) {
            setNotifications(prev => [...prev, {
              id: Date.now(),
              message: `ðŸ“… RDV proche: ${rdv.patient} Ã  ${rdv.date}`,
              type: 'warning',
              date: new Date().toLocaleDateString('fr-FR')
            }]);
          }
        });
      }, 2000);
    }, 1000);
  }, []);

  const createDossier = (patientId, data) => {
    const nouveauDossier = {
      id: Date.now().toString(),
      patientId,
      ...data,
      dateCreation: new Date().toLocaleDateString('fr-FR'),
      statut: 'Actif'
    };
    setDossiers(prev => [...prev, nouveauDossier]);
    setNotifications(prev => [...prev, {
