                ðŸ“„ Dossier
              </button>
              <button onClick={() => setShowModal({ open: true, type: 'ordonnance', patientId: patient.id })} 
                className="bg-purple-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-purple-700 transition-all shadow-lg">
                ðŸ’Š Ordonnance
              </button>
              <button onClick={() => setShowModal({ open: true, type: 'rdv', patientId: patient.id })} 
                className="bg-green-600 text-white font-bold px-6 py-3 rounded-xl hover:bg-green-700 transition-all shadow-lg">
