        <div className="grid lg:grid-cols-2 gap-8">
          <div>
            <DossierResume dossier={patientDossier} />
            <ResultatsExamensResume resultats={patientResultats} />
          </div>
          <div>
            <RdvResume rdv={patientRdv} />
            <OrdonnancesResume ordonnances={patientOrdonnances.slice(0, 3)} />
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
