        <input type="text" value={formData.dosage} onChange={(e) => setFormData({...formData, dosage: e.target.value})}
          placeholder="Dosage ex: 1cp 3x/jour" className="w-full p-4 border rounded-xl" required />
        <input type="text" value={formData.duree} onChange={(e) => setFormData({...formData, duree: e.target.value})}
          placeholder="DurÃ©e ex: 7 jours" className="w-full p-4 border rounded-xl" required />
        <button type="submit" className="w-full bg-purple-600 text-white py-4 rounded-xl font-bold">
          âš•ï¸ Ã‰mettre ordonnance
        </button>
      </form>
    </div>
  );
};

