// src/components/PatientDashboard.js
import React, { useEffect, useState } from "react";
import { getData } from "../api/endpoint";

const PatientDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fonction pour récupérer les dossiers du patient
  const fetchPatientDossiers = async () => {
    setLoading(true);
    setError("");

    try {
      // Remplace "/mes-dossiers-patient" par le vrai endpoint côté serveur
      const data = await getData("/mes-dossiers-patient");
      setDossiers(data);
    } catch (err) {
      // Gestion spécifique des erreurs 403 / 404
      if (err.message.includes("403")) {
        setError("Accès refusé : vous n'êtes pas autorisé à voir ces dossiers.");
      } else if (err.message.includes("404")) {
        setError("Aucun dossier trouvé pour ce patient.");
      } else {
        setError(err.message || "Erreur inconnue lors du chargement des dossiers.");
      }
    } finally {
      setLoading(false);
    }
  };

  // useEffect pour charger les dossiers au montage du composant
  useEffect(() => {
    fetchPatientDossiers();
  }, []);

  return (
    <div className="patient-dashboard">
      <h1>Tableau de bord patient</h1>

      {loading && <p>Chargement des dossiers...</p>}

      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && !error && dossiers.length === 0 && (
        <p>Aucun dossier disponible pour ce patient.</p>
      )}

      {!loading && !error && dossiers.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Date</th>
              <th>Type de dossier</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {dossiers.map((dossier) => (
              <tr key={dossier._id || dossier.id}>
                <td>{dossier._id || dossier.id}</td>
                <td>{new Date(dossier.date).toLocaleDateString()}</td>
                <td>{dossier.type}</td>
                <td>
                  <button onClick={() => alert(`Voir le dossier ${dossier._id}`)}>
                    Voir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PatientDashboard;
