import React, { useEffect, useState } from "react";
import { getData } from "./endpoint";

const PatientDashboard = () => {
  const [patient, setPatient] = useState(null);
  const [medecin, setMedecin] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Récupérer les infos du patient connecté
        const patientData = await getData("/api/patient/me");
        setPatient(patientData);

        // Récupérer les infos du médecin référent
        if (patientData.medecinId) {
          const medecinData = await getData(`/api/medecin/${patientData.medecinId}`);
          setMedecin(medecinData);
        }

      } catch (error) {
        console.error("Erreur chargement patient :", error);
      }
    };

    fetchData();
  }, []);

  if (!patient) return <p>Chargement...</p>;

  return (
    <div>
      <h1>Bienvenue, {patient.nom} {patient.prenom} !</h1>

      <div className="patient-details">
        <h2>Vos informations :</h2>
        <p>Email : {patient.email}</p>
        <p>Age : {new Date().getFullYear() - new Date(patient.dateNaissance).getFullYear()}</p>
        <p>Téléphone : {patient.telephone}</p>
      </div>

      {medecin && (
        <div className="medecin-details">
          <h2>Votre médecin :</h2>
          <p>Nom : {medecin.nom} {medecin.prenom}</p>
          <p>Spécialité : {medecin.specialite}</p>
          <p>Contact : {medecin.telephone}</p>
        </div>
      )}
    </div>
  );
};

export default PatientDashboard;
