import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../api/endpoint';
import { FiUsers, FiSearch, FiFileText, FiUserCheck } from 'react-icons/fi';

const MesPatients = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await api.getData('/patients/medecin');
      setPatients(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Erreur patients:', err);
      setError(err.message || 'Impossible de charger vos patients.');
      setPatients([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredPatients = patients.filter(patient =>
    patient.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    patient.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px] p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-lg text-gray-600">Chargement de vos patients...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* HEADER */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-12 gap-6">
        <div>
          <h1 className="text-4xl font-bold text-gray-900 flex items-center gap-4 mb-2">
            <FiUsers size={48} className="text-blue-600" />
            Mes Patients
          </h1>
          <p className="text-xl text-gray-600">({filteredPatients.length} patients)</p>
        </div>
        
        <div className="flex gap-4">
          <div className="relative flex-1 max-w-md">
            <FiSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Rechercher un patient..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-6 py-3 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500"
            />
          </div>
        </div>
      </div>

      {/* ERROR */}
      {error && (
        <div className="bg-red-50 border-2 border-red-200 text-red-800 px-6 py-4 rounded-2xl mb-8 shadow-lg">
          {error}
        </div>
      )}

      {/* TABLEAU PATIENTS */}
      {filteredPatients.length === 0 ? (
        <div className="text-center py-20 bg-white rounded-3xl shadow-lg">
          <FiUsers size={64} className="mx-auto mb-6 text-gray-400" />
          <h3 className="text-2xl font-bold text-gray-900 mb-2">Aucun patient trouvé</h3>
          <p className="text-lg text-gray-600">
            {searchTerm ? 'Aucun patient ne correspond à votre recherche.' : 'Vos patients apparaîtront ici.'}
          </p>
        </div>
      ) : (
        <div className="bg-white rounded-3xl shadow-2xl border border-gray-100 overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gradient-to-r from-gray-50 to-gray-100">
                <tr>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Patient</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Email</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Dossier Médical</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Statut</th>
                  <th className="px-8 py-6 text-left text-sm font-bold text-gray-900 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredPatients.map((patient) => (
                  <tr key={patient._id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-8 py-6 font-semibold text-gray-900">{patient.fullName}</td>
                    <td className="px-8 py-6 text-gray-700">{patient.email}</td>
                    <td className="px-8 py-6">
                      {patient.dossierMedicalId ? (
                        <span className="px-4 py-2 bg-green-100 text-green-800 rounded-xl text-sm font-semibold">
                          <FiFileText className="inline mr-1" size={14} />
                          Dossier actif
                        </span>
                      ) : (
                        <span className="px-4 py-2 bg-gray-100 text-gray-600 rounded-xl text-sm font-semibold">
                          Aucun dossier
                        </span>
                      )}
                    </td>
                    <td className="px-8 py-6">
                      <span className={`px-4 py-2 rounded-full text-sm font-semibold ${
                        patient.statut === 'ACTIF' 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-red-100 text-red-800'
                      }`}>
                        <FiUserCheck className="inline mr-1" size={16} />
                        {patient.statut}
                      </span>
                    </td>
                    <td className="px-8 py-6">
                      <div className="flex gap-2">
                        {patient.dossierMedicalId && (
                          <Link
                            to={`/medecin/dossier/${patient.dossierMedicalId}`}
                            className="p-3 text-blue-600 hover:bg-blue-50 rounded-xl transition-all"
                            title="Voir dossier"
                          >
                            <FiFileText size={18} />
                          </Link>
                        )}
                        <Link
                          to={`/medecin/patient/${patient._id}`}
                          className="p-3 text-indigo-600 hover:bg-indigo-50 rounded-xl transition-all"
                          title="Détails"
                        >
                          <FiEye size={18} />
                        </Link>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default MesPatients;
