import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';

const PatientDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [rendezVous, setRendezVous] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showFormRDV, setShowFormRDV] = useState(false);

  // âœ… PERSISTANCE LOCALSTORAGE
  useEffect(() => {
    const savedRDV = JSON.parse(localStorage.getItem('patientRDV') || '[]');
    const savedNotifs = JSON.parse(localStorage.getItem('patientNotifications') || '[]');
    
    setTimeout(() => {
      setProfile({
        nom: 'Aminata DIOP', prenom: 'Aminata', email: 'aminata.diop@email.com',
        telephone: '+221 78 123 4567', age: 35, sexe: 'FÃ©minin', 
        groupeSanguin: 'O+', allergies: 'Aucune connue'
      });
      setRendezVous(savedRDV);
      setNotifications(savedNotifs);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    localStorage.setItem('patientRDV', JSON.stringify(rendezVous));
    localStorage.setItem('patientNotifications', JSON.stringify(notifications));
  }, [rendezVous, notifications]);

  if (loading) return <LoadingScreen />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 relative">
      {/* ðŸ”” Notifications Toast */}
      <NotificationsToast notifications={notifications} />
      
      {/* Sidebar */}
      <aside className="hidden lg:block lg:w-72 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white/95 backdrop-blur-xl shadow-2xl border-r-4 border-blue-200/60">
        <ProfileSidebar profile={profile} />
        <Navigation activePath={location.pathname} rdvCount={rendezVous.length} notifCount={notifications.length} />
      </aside>

      {/* Contenu principal */}
      <main className={`lg:ml-72 p-6 lg:p-8 pb-24 transition-all duration-300 ${showFormRDV ? 'lg:ml-72 overflow-hidden' : ''}`}>
        <Routes>
          <Route path="/" element={<DashboardHome profile={profile} rdv={rendezVous} notifications={notifications} />} />
          <Route path="/rdv" element={<RDVPage rdv={rendezVous} />} />
          <Route path="/rdv/nouveau" element={<NouveauRDVForm rdv={rendezVous} setRdv={setRendezVous} setNotifications={setNotifications} setShowForm={setShowFormRDV} />} />
          <Route path="/dossier" element={<DossierPage />} />
          <Route path="/ordonnances" element={<OrdonnancesPage />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>

      {/* Modal Formulaire Mobile */}
      {showFormRDV && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-md z-50 lg:hidden flex items-center justify-center p-4">
          <NouveauRDVFormMobile rdv={rendezVous} setRdv={setRendezVous} setNotifications={setNotifications} setShowForm={setShowFormRDV} />
        </div>
      )}
    </div>
  );
};

// ðŸ”¥ TOUS LES COMPOSANTS DÃ‰FINIS âœ…
const NouveauRDVForm = ({ rdv, setRdv, setNotifications, setShowForm }) => {
  const [formData, setFormData] = useState({
    medecin: '', specialite: '', date: '', heure: '', motif: '',
    telephone: '', email: ''
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const medecins = [
    { nom: 'Dr. Moustapha NDIAYE', specialite: 'Cardiologue' },
    { nom: 'Dr. Awa SOW', specialite: 'GynÃ©cologue' },
    { nom: 'Dr. Fatou DIA', specialite: 'PÃ©diatre' },
    { nom: 'Dr. Ibrahima BA', specialite: 'MÃ©decin gÃ©nÃ©raliste' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    setTimeout(() => {
      const nouveauRDV = {
        _id: Date.now().toString(),
        medecin: { nom: formData.medecin },
        specialite: formData.specialite,
        date: `${formData.date} ${formData.heure}`,
        statut: 'ConfirmÃ©',
        motif: formData.motif,
        createdAt: new Date().toISOString()
      };

      setRdv(prev => [nouveauRDV, ...prev]);
      const notification = {
        id: Date.now(),
        title: 'âœ… Rendez-vous confirmÃ© !',
        message: `Votre consultation avec ${formData.medecin} le ${formData.date} Ã  ${formData.heure} est confirmÃ©e.`,
        type: 'success',
        date: new Date().toLocaleString('fr-FR'),
        read: false
      };
      setNotifications(prev => [notification, ...prev]);

      setSuccess(true);
      setLoading(false);

      setTimeout(() => {
        setShowForm(false);
        setSuccess(false);
      }, 3000);
    }, 1500);
  };

  if (success) {
    return (
      <div className="w-full max-w-2xl bg-white/95 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200/50 p-12">
        <div className="text-center">
          <div className="w-32 h-32 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-full flex items-center justify-center mx-auto mb-8 shadow-2xl text-5xl text-white">
            âœ…
          </div>
          <h3 className="text-4xl font-black text-emerald-700 mb-6">Rendez-vous CONFIRMÃ‰ !</h3>
          <div className="bg-emerald-50 p-8 rounded-3xl border-4 border-emerald-200 mb-8">
            <p className="text-xl font-bold text-emerald-800 mb-2">{formData.medecin}</p>
            <p className="text-2xl font-black text-gray-900">{formData.date} Ã  {formData.heure}</p>
          </div>
          <Link to="/patient/rdv" className="w-full block bg-blue-600 text-white font-bold text-xl py-6 px-8 rounded-3xl text-center hover:bg-blue-700 shadow-2xl mb-4">
            Voir mon agenda
          </Link>
          <button onClick={() => setShowForm(false)} className="w-full bg-gray-500 text-white font-bold text-xl py-6 px-8 rounded-3xl hover:bg-gray-600 shadow-2xl">
            Fermer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full max-w-2xl bg-white/95 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200/50 p-8 lg:p-12 max-h-[90vh] overflow-y-auto">
      <div className="flex items-center justify-between mb-12">
        <h2 className="text-4xl font-black text-gray-900 flex items-center gap-4">ðŸ“… Demander un rendez-vous</h2>
        <button onClick={() => setShowForm(false)} className="text-4xl hover:scale-110 transition-all">âœ•</button>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-xl font-bold text-gray-700 mb-3">ðŸ‘¨â€âš•ï¸ MÃ©decin</label>
          <select value={formData.medecin} onChange={(e) => {
            const medecin = medecins.find(m => m.nom === e.target.value);
            setFormData({ ...formData, medecin: e.target.value, specialite: medecin?.specialite || '' });
          }} className="w-full p-6 border-2 border-gray-200 rounded-3xl focus:border-emerald-500 focus:ring-4 ring-emerald-100/50 text-xl transition-all bg-white shadow-lg" required>
            <option value="">Choisir un mÃ©decin</option>
            {medecins.map(m => <option key={m.nom} value={m.nom}>{m.nom}</option>)}
          </select>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-xl font-bold text-gray-700 mb-3">ðŸ“… Date</label>
            <input type="date" value={formData.date} onChange={(e) => setFormData({ ...formData, date: e.target.value })} 
              min={new Date(Date.now() + 24*60*60*1000).toISOString().split('T')[0]} 
              className="w-full p-6 border-2 border-gray-200 rounded-3xl focus:border-emerald-500 focus:ring-4 ring-emerald-100/50 text-xl transition-all bg-white shadow-lg" required />
          </div>
          <div>
            <label className="block text-xl font-bold text-gray-700 mb-3">ðŸ• Heure</label>
            <select value={formData.heure} onChange={(e) => setFormData({ ...formData, heure: e.target.value })} 
              className="w-full p-6 border-2 border-gray-200 rounded-3xl focus:border-emerald-500 focus:ring-4 ring-emerald-100/50 text-xl transition-all bg-white shadow-lg" required>
              <option value="">SÃ©lectionner</option>
              {['09:00', '10:00', '11:00', '14:00', '15:00', '16:00', '17:00'].map(h => <option key={h} value={h}>{h}</option>)}
            </select>
          </div>
        </div>

        <div>
          <label className="block text-xl font-bold text-gray-700 mb-3">ðŸ“ Motif</label>
          <textarea rows="3" value={formData.motif} onChange={(e) => setFormData({ ...formData, motif: e.target.value })} 
            className="w-full p-6 border-2 border-gray-200 rounded-3xl focus:border-emerald-500 focus:ring-4 ring-emerald-100/50 text-xl transition-all bg-white shadow-lg resize-vertical" 
            placeholder="DÃ©crivez votre motif de consultation..." required />
        </div>

        <button type="submit" disabled={loading} className="w-full p-8 bg-gradient-to-r from-emerald-500 to-emerald-600 text-white font-black text-2xl rounded-4xl shadow-3xl hover:shadow-4xl hover:scale-[1.02] transition-all duration-300 disabled:opacity-50 flex items-center justify-center gap-3">
          {loading ? (<><div className="w-8 h-8 border-2 border-white/30 border-t-white rounded-full animate-spin"></div> Confirmation...</>) : 'âœ… Confirmer mon rendez-vous'}
        </button>
      </form>
    </div>
  );
};

const NouveauRDVFormMobile = NouveauRDVForm;

const NotificationsToast = ({ notifications }) => {
  if (!notifications.length) return null;
  return (
    <div className="fixed top-24 right-6 space-y-3 z-50 max-w-sm w-full">
      {notifications.slice(0, 3).map(notif => (
        <div key={notif.id} className={`p-6 rounded-3xl shadow-2xl backdrop-blur-xl border-2 animate-slideInRight ${
          notif.type === 'success' ? 'bg-emerald-500/95 border-emerald-300 text-white' : 'bg-blue-500/95 border-blue-300 text-white'
        }`}>
          <div className="flex items-start gap-4">
            <span className="flex-shrink-0 mt-1 text-2xl">{notif.type === 'success' ? 'âœ…' : 'ðŸ””'}</span>
            <div className="flex-1 min-w-0">
              <h4 className="font-bold text-xl mb-1">{notif.title}</h4>
              <p className="text-lg opacity-95">{notif.message}</p>
              <p className="text-sm opacity-75 mt-2">{notif.date}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

const ProfileSidebar = ({ profile }) => (
  <div className="p-8 sticky top-0 bg-gradient-to-b from-blue-50/90 to-white/80 border-b-2 border-blue-100/50">
    <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-xl ring-4 ring-white/50">
      <span className="text-3xl text-white">ðŸ‘¤</span>
    </div>
    <h2 className="text-3xl font-black text-gray-900 text-center mb-4 truncate">{profile?.nom}</h2>
    <div className="text-center text-xl space-y-1 bg-blue-50/80 p-4 rounded-xl border border-blue-200/50">
      <p className="font-bold text-blue-700">{profile?.age} ans â€¢ {profile?.sexe}</p>
      <p className="text-blue-600 text-sm">{profile?.groupeSanguin} â€¢ {profile?.allergies}</p>
    </div>
  </div>
);

const Navigation = ({ activePath, rdvCount, notifCount }) => (
  <nav className="p-6 mt-8 space-y-3 h-[calc(100vh-300px)] overflow-y-auto">
    <NavLink href="/patient" icon="ðŸ " label="Dashboard" active={activePath === '/patient'} color="blue" badge={null} />
    <NavLink href="/patient/rdv" icon="ðŸ“…" label={`Rendez-vous (${rdvCount})`} active={activePath === '/patient/rdv'} color="emerald" badge={notifCount > 0 ? notifCount : null} />
    <NavLink href="/patient/dossier" icon="ðŸ“„" label="Dossier" active={activePath === '/patient/dossier'} color="purple" badge={null} />
    <NavLink href="/patient/ordonnances" icon="ðŸ’Š" label="Ordonnances" active={activePath === '/patient/ordonnances'} color="orange" badge={null} />
  </nav>
);

const NavLink = ({ href, icon, label, active, color, badge }) => (
  <Link to={href} className={`flex items-center p-6 rounded-3xl w-full font-bold text-xl transition-all duration-300 group hover:shadow-xl hover:-translate-x-2 border-2 relative overflow-hidden ${
    active 
      ? `bg-gradient-to-r from-${color}-500 to-${color}-600 text-white shadow-2xl shadow-${color}-200 ring-2 ring-${color}-400/30` 
      : `text-gray-700 hover:bg-${color}-50 hover:border-${color}-200 hover:shadow-lg`
  }`}>
    <span className={`mr-6 text-3xl transition-transform group-hover:scale-110 ${active ? 'text-white' : ''}`}>{icon}</span>
    <span className="flex-1 min-w-0 truncate">{label}</span>
    {badge && (
      <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs font-bold w-8 h-8 rounded-full flex items-center justify-center animate-pulse shadow-lg">
        {badge}
      </span>
    )}
  </Link>
);

const DashboardHome = ({ profile, rdv, notifications }) => (
  <div className="max-w-7xl mx-auto space-y-16">
    <section className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-blue-200/50 p-12 lg:p-16 text-center">
      <h1 className="text-6xl lg:text-7xl font-black bg-gradient-to-r from-gray-900 to-blue-900 bg-clip-text text-transparent mb-8">
        Bienvenue {profile?.nom} ðŸ‘‹
      </h1>
      <div className="bg-blue-50/80 p-8 rounded-3xl backdrop-blur-sm border-2 border-blue-200/50 inline-block max-w-2xl mx-auto">
        <p className="text-2xl font-semibold text-blue-800">{profile?.email}</p>
      </div>
    </section>
    
    <section className="grid lg:grid-cols-2 gap-12 items-start">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <InfoCard icon="ðŸ“…" title={rdv.length} label="RDV confirmÃ©s" color="emerald" />
        <InfoCard icon="ðŸ””" title={notifications.length} label="Notifications" color="blue" />
      </div>
      <ActionButtons />
    </section>
    
    <RDVPreview rdv={rdv.slice(0, 2)} />
  </div>
);

const RDVPreview = ({ rdv }) => (
  <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200 p-12 lg:p-16">
    <h3 className="text-4xl font-black text-gray-900 mb-10">ðŸ“… Prochains RDV ({rdv.length})</h3>
    <div className="space-y-6">
      {rdv.map(rdvItem => (
        <div key={rdvItem._id} className="p-8 bg-emerald-50 rounded-3xl border-l-8 border-emerald-400 hover:shadow-xl transition-all">
          <div className="flex items-center justify-between">
            <h4 className="text-2xl font-bold text-gray-900">{rdvItem.medecin?.nom}</h4>
            <span className="px-6 py-3 bg-emerald-500 text-white font-bold rounded-2xl text-lg">{rdvItem.statut}</span>
          </div>
          <p className="text-xl text-emerald-700 mt-2">{rdvItem.specialite} â€¢ {rdvItem.date}</p>
        </div>
      ))}
    </div>
  </div>
);

const RDVPage = ({ rdv }) => (
  <div className="max-w-6xl mx-auto space-y-12">
    <div className="flex items-center justify-between p-12 bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200/50">
      <h2 className="text-6xl font-black text-gray-900 flex items-center gap-6">
        ðŸ“… Mes rendez-vous ({rdv.length})
      </h2>
      <Link to="/patient/rdv/nouveau" className="px-12 py-8 bg-gradient-to-r from-emerald-500 to-teal-600 text-white font-black text-2xl rounded-4xl hover:shadow-4xl hover:scale-105 shadow-3xl transition-all">
        + Nouveau RDV
      </Link>
    </div>
    
    <div className="grid gap-8">
      {rdv.map(rdvItem => (
        <RDVCard key={rdvItem._id} rdv={rdvItem} />
      ))}
    </div>
  </div>
);

const RDVCard = ({ rdv }) => (
  <div className="group p-12 bg-gradient-to-r from-emerald-50 to-blue-50 rounded-4xl border-4 border-emerald-200 shadow-2xl hover:shadow-4xl hover:-translate-y-2 transition-all backdrop-blur-xl cursor-pointer">
    <div className="flex flex-col lg:flex-row items-start lg:items-center gap-8">
      <div className="w-32 h-32 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-3xl flex flex-col items-center justify-center text-white font-bold shadow-2xl flex-shrink-0">
        <span className="text-2xl">{rdv.date?.split(' ')[1]}</span>
        <span className="text-lg">{rdv.date?.split(' ')[0]}</span>
      </div>
      <div className="flex-1">
        <h3 className="text-3xl font-black text-gray-900 mb-4">{rdv.medecin?.nom}</h3>
        <p className="text-2xl text-emerald-700 font-bold mb-4">{rdv.specialite}</p>
        <p className="text-xl text-gray-700 mb-4">ðŸ“ {rdv.motif}</p>
        <span className={`px-6 py-3 bg-emerald-500 text-white font-bold text-xl rounded-3xl shadow-xl`}>
          {rdv.statut}
        </span>
      </div>
    </div>
  </div>
);

const DossierPage = () => (
  <div className="max-w-6xl mx-auto p-20 bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-purple-200/50">
    <h1 className="text-6xl font-black text-gray-900 text-center mb-12">ðŸ“„ Dossier mÃ©dical</h1>
    <p className="text-3xl text-gray-600 text-center">Votre dossier mÃ©dical complet (lecture seule)</p>
  </div>
);

const OrdonnancesPage = () => (
  <div className="max-w-6xl mx-auto p-20 bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-orange-200/50">
    <h1 className="text-6xl font-black text-gray-900 text-center mb-12">ðŸ’Š Mes ordonnances</h1>
    <p className="text-3xl text-gray-600 text-center">Liste de vos ordonnances actives</p>
  </div>
);

const NotFoundPage = () => (
  <div className="max-w-4xl mx-auto p-32 text-center bg-white/70 backdrop-blur-xl rounded-4xl shadow-2xl border-4 border-gray-200/50">
    <h1 className="text-7xl font-black text-gray-400 mb-12">404 - Page introuvable</h1>
    <Link to="/patient" className="inline-block px-20 py-12 bg-blue-600 text-white font-black text-3xl rounded-4xl hover:bg-blue-700 shadow-3xl hover:shadow-4xl transition-all duration-300">
      â† Retour Dashboard
    </Link>
  </div>
);

const InfoCard = ({ icon, title, label, color }) => (
  <div className={`group p-12 bg-gradient-to-br from-${color}-50 to-${color}-100 rounded-4xl border-4 border-${color}-100 hover:shadow-3xl hover:-translate-y-3 transition-all backdrop-blur-xl cursor-pointer`}>
    <div className="text-6xl mb-6 group-hover:scale-110 transition-transform">{icon}</div>
    <p className="text-5xl font-black text-gray-900 mb-4 leading-tight">{title}</p>
    <p className="text-2xl font-bold text-gray-700">{label}</p>
  </div>
);

const ActionButtons = () => (
  <div className="grid grid-cols-1 gap-8">
    <ActionButton to="/patient/rdv/nouveau" icon="ðŸ“…" title="Nouveau RDV" subtitle="Demander rendez-vous" color="emerald" />
    <ActionButton to="/patient/rdv" icon="ðŸ“‹" title="Mon Agenda" subtitle="GÃ©rer mes RDV" color="blue" />
  </div>
);

const ActionButton = ({ to, icon, title, subtitle, color }) => (
  <Link to={to} className={`group relative p-16 bg-gradient-to-br from-${color}-500 to-${color}-600 text-white rounded-4xl shadow-3xl hover:shadow-4xl hover:scale-[1.05] transition-all duration-500 overflow-hidden`}>
    <span className="text-8xl block mb-8 group-hover:scale-125 transition-transform opacity-90">{icon}</span>
    <h3 className="text-4xl font-black mb-4">{title}</h3>
    <p className="text-2xl opacity-90 font-semibold">{subtitle}</p>
  </Link>
);

const LoadingScreen = () => (
  <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-8">
    <div className="text-center max-w-md">
      <div className="w-28 h-28 border-4 border-blue-200 border-t-blue-500 rounded-full animate-spin mx-auto mb-12 shadow-2xl"></div>
      <h1 className="text-5xl font-black text-gray-900 mb-6">Chargement Dashboard</h1>
      <p className="text-2xl text-blue-600 font-semibold">PrÃ©paration de vos donnÃ©es mÃ©dicales...</p>
    </div>
  </div>
);

export default PatientDashboard;
