import React, { useEffect, useState } from "react";
import { getData, deleteData, putData } from "../api";

const DashboardAdmin = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editUser, setEditUser] = useState(null);
  const [formData, setFormData] = useState({ username: "", email: "", role: "" });
  const [error, setError] = useState("");

  // ðŸ”¹ Charger les utilisateurs depuis le backend
  const fetchUsers = async () => {
    setLoading(true);
    try {
      const data = await getData("/users");
      setUsers(data);
    } catch (err) {
      console.error("Erreur fetch users:", err.message);
      setError(err.message);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // ðŸ”¹ Supprimer un utilisateur
  const handleDelete = async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer cet utilisateur ?")) return;
    try {
      await deleteData(`/user/${id}`);
      setUsers(users.filter(u => u._id !== id));
    } catch (err) {
      console.error("Erreur delete user:", err.message);
      setError(err.message);
    }
  };

  // ðŸ”¹ Ouvrir modal d'Ã©dition
  const handleEdit = (user) => {
    setEditUser(user);
    setFormData({ username: user.username, email: user.email, role: user.role });
  };

  // ðŸ”¹ Soumettre l'Ã©dition
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const updated = await putData(`/user/${editUser._id}`, formData);
      setUsers(users.map(u => u._id === updated._id ? updated : u));
      setEditUser(null);
    } catch (err) {
      console.error("Erreur update user:", err.message);
      setError(err.message);
    }
  };

  // ðŸ”¹ Gestion des inputs
  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Dashboard Admin - Utilisateurs</h1>

      {error && <p className="text-red-500 mb-2">{error}</p>}

      {loading ? (
        <p>Chargement des utilisateurs...</p>
      ) : (
        <table className="min-w-full border">
          <thead>
            <tr className="bg-gray-200">
              <th className="px-4 py-2 border">#</th>
              <th className="px-4 py-2 border">Nom d'utilisateur</th>
              <th className="px-4 py-2 border">Email</th>
              <th className="px-4 py-2 border">Role</th>
              <th className="px-4 py-2 border">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user._id} className="text-center">
                <td className="px-4 py-2 border">{index + 1}</td>
                <td className="px-4 py-2 border">{user.username}</td>
                <td className="px-4 py-2 border">{user.email}</td>
                <td className="px-4 py-2 border">{user.role}</td>
                <td className="px-4 py-2 border space-x-2">
                  <button
                    className="bg-blue-500 text-white px-2 py-1 rounded"
                    onClick={() => handleEdit(user)}
                  >
                    Edit
                  </button>
                  <button
                    className="bg-red-500 text-white px-2 py-1 rounded"
                    onClick={() => handleDelete(user._id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Modal Ã©dition */}
      {editUser && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded w-96">
            <h2 className="text-xl font-bold mb-4">Modifier l'utilisateur</h2>
            <form onSubmit={handleUpdate} className="space-y-2">
              <input
                type="text"
                name="username"
                placeholder="Nom d'utilisateur"
                value={formData.username}
                onChange={handleChange}
                className="w-full border px-2 py-1 rounded"
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                className="w-full border px-2 py-1 rounded"
                required
              />
              <select
                name="role"
                value={formData.role}
                onChange={handleChange}
                className="w-full border px-2 py-1 rounded"
                required
              >
                <option value="Admin">Admin</option>
                <option value="SuperAdmin">SuperAdmin</option>
                <option value="Medecin">Medecin</option>
                <option value="Assistant">Assistant</option>
                <option value="Patient">Patient</option>
                <option value="MediateurNumerique">MediateurNumerique</option>
              </select>
              <div className="flex justify-end space-x-2 mt-2">
                <button
                  type="button"
                  className="bg-gray-500 text-white px-4 py-1 rounded"
                  onClick={() => setEditUser(null)}
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  className="bg-green-500 text-white px-4 py-1 rounded"
                >
                  Sauvegarder
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default DashboardAdmin;
