import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import getData from '../../api/endpoint';
import { FiFileText, FiUser, FiSearch } from 'react-icons/fi';

const MesDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadDossiers();
  }, []);

  const loadDossiers = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      if (token) {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        const data = await getData('/dossiers');
        const mesDossiers = data.filter(d => d.medecinId === decoded.userId);
        setDossiers(mesDossiers);
      }
    } catch (err) {
      console.error('Erreur lors du chargement des dossiers:', err);
      setError('Impossible de charger les dossiers.');
      setDossiers([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredDossiers = dossiers.filter(dossier => {
    const patientName = dossier.patientId?.fullName || '';
    return patientName.toLowerCase().includes(searchTerm.toLowerCase());
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-600">Chargement...</div>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Mes Dossiers Médicaux</h2>
          <p className="text-gray-600 mt-2">Gérez les dossiers médicaux de vos patients</p>
        </div>
        <Link
          to="/medecin/dossiers/creer"
          className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-all font-medium"
        >
          + Créer un dossier
        </Link>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {/* Barre de recherche */}
      <div className="mb-6">
        <div className="relative">
          <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher un dossier..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>
      </div>

      {filteredDossiers.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
          <FiFileText size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">
            {searchTerm ? 'Aucun dossier trouvé' : 'Aucun dossier'}
          </h3>
          <p className="text-gray-600 mb-4">
            {searchTerm 
              ? 'Aucun dossier ne correspond à votre recherche.' 
              : "Vous n'avez pas encore créé de dossiers médicaux."}
          </p>
          {!searchTerm && (
            <Link
              to="/medecin/dossiers/creer"
              className="inline-block bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-all font-medium"
            >
              Créer votre premier dossier
            </Link>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredDossiers.map((dossier) => (
            <div key={dossier._id} className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100 hover:shadow-xl transition-all">
              <div className="flex items-center mb-4">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center mr-4">
                  <FiFileText className="text-white" size={24} />
                </div>
                <div>
                  <h3 className="text-lg font-bold text-gray-900">
                    {dossier.patientId?.fullName || 'Patient'}
                  </h3>
                  <p className="text-sm text-gray-600">
                    {dossier.groupeSanguin || 'Groupe sanguin non spécifié'}
                  </p>
                </div>
              </div>
              
              <div className="space-y-2 text-sm text-gray-600 mb-4">
                <p>Statut: <span className={`font-semibold ${dossier.statut === 'ACTIF' ? 'text-green-600' : 'text-gray-600'}`}>
                  {dossier.statut || 'ACTIF'}
                </span></p>
                {dossier.antecedents && dossier.antecedents.length > 0 && (
                  <p>Antécédents: {dossier.antecedents.length}</p>
                )}
                {dossier.allergies && dossier.allergies.length > 0 && (
                  <p>Allergies: {dossier.allergies.length}</p>
                )}
              </div>

              <Link
                to={`/medecin/dossiers/${dossier._id}`}
                className="block w-full bg-blue-500 text-white text-center py-2 rounded-lg hover:bg-blue-600 transition-all font-medium"
              >
                Voir le dossier
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesDossiers;
