import React, { useState } from 'react';
import { motion, AnimatePresence } from "framer-motion";
import { Plus, Trash2, FileText, Smartphone, UserPlus, Calendar, Users, Activity, Stethoscope, CheckCircle, XCircle } from "lucide-react";
import { toast } from "sonner";

const MedecinDashboard = () => {
  const [activeTab, setActiveTab] = useState('ordonnances');
  
  const [patients, setPatients] = useState([
    { id: 1, name: 'Diop Aminata', age: 32, lastVisit: '08/02/2026', status: 'actif' },
    { id: 2, name: 'Ndiaye Fatou', age: 45, lastVisit: '07/02/2026', status: 'actif' },
    { id: 3, name: 'Sow Mamadou', age: 28, lastVisit: '06/02/2026', status: 'en attente' }
  ]);
  
  const [ordonnance, setOrdonnance] = useState({
    patient: '',
    medicaments: [
      { nom: 'ParacÃ©tamol 1g', dose: '1cp', frequence: '3x/jour', duree: '5 jours' },
      { nom: 'Amoxicilline 500mg', dose: '1cp', frequence: '2x/jour', duree: '7 jours' }
    ]
  });

  const [rdvs, setRdvs] = useState([
    { id: 1, patient: "Aminata Diop", heure: "09:00", motif: "Consultation gÃ©nÃ©rale", status: "confirmÃ©" },
    { id: 2, patient: "Fatou Ndiaye", heure: "10:30", motif: "Suivi diabÃ¨te", status: "en attente" },
  ]);

  // âœ… FONCTIONS DYNAMIQUES
  const addMedicament = () => {
    setOrdonnance({
      ...ordonnance,
      medicaments: [...ordonnance.medicaments, { nom: '', dose: '', frequence: '', duree: '' }]
    });
    toast.success("ğŸ’Š MÃ©dicament ajoutÃ©");
  };

  const updateMedicament = (index, field, value) => {
    const newMeds = [...ordonnance.medicaments];
    newMeds[index] = { ...newMeds[index], [field]: value };
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const removeMedicament = (index) => {
    setOrdonnance({
      ...ordonnance,
      medicaments: ordonnance.medicaments.filter((_, i) => i !== index)
    });
    toast("ğŸ—‘ï¸ MÃ©dicament supprimÃ©");
  };

  const selectPatient = (value) => {
    setOrdonnance({ ...ordonnance, patient: value });
  };

  const saveOrdonnance = () => {
    if (!ordonnance.patient) {
      toast.error("âŒ Veuillez sÃ©lectionner un patient !");
      return;
    }
    const filledMeds = ordonnance.medicaments.filter(m => m.nom.trim());
    if (filledMeds.length === 0) {
      toast.error("âŒ Ajoutez au moins un mÃ©dicament !");
      return;
    }
    toast.success("âœ… Ordonnance gÃ©nÃ©rÃ©e et envoyÃ©e !", {
      description: `${ordonnance.patient} - ${filledMeds.length} mÃ©dicament(s)`
    });
  };

  const stats = {
    patients: patients.length,
    ordonnances: 47,
    rdvsToday: 8,
    revenus: 1250000
  };

  const tabs = [
    { id: 'ordonnances', label: `ğŸ’Š Ordonnances`, icon: Stethoscope },
    { id: 'patients', label: `ğŸ‘¥ Patients (${stats.patients})`, icon: Users },
    { id: 'rdv', label: `ğŸ“… RDV`, icon: Calendar },
    { id: 'stats', label: `ğŸ“Š Stats`, icon: Activity }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-500 via-emerald-600 to-teal-700 p-5 font-sans text-white overflow-x-hidden">
      <div className="max-w-7xl mx-auto space-y-8">
        
        {/* HEADER */}
        <motion.div
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          className="backdrop-blur-xl bg-white/15 border-4 border-white/30 rounded-3xl shadow-2xl shadow-black/30 p-8 md:p-12 mb-8 hover:shadow-3xl transition-all duration-500"
        >
          <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-8">
            <div className="flex flex-col gap-6">
              <motion.h1 
                initial={{ scale: 0.9 }}
                animate={{ scale: 1 }}
                className="text-4xl md:text-6xl font-black bg-gradient-to-r from-white to-emerald-100 bg-clip-text text-transparent leading-tight"
              >
                ğŸ©º DOCTORAT - GOVAIMED
              </motion.h1>
              
              <div className="backdrop-blur-xl bg-white/20 border border-white/40 rounded-2xl p-6 shadow-xl shadow-black/20">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 text-lg">
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 bg-white/20 rounded-2xl flex items-center justify-center">
                      ğŸ‘¨â€âš•ï¸
                    </div>
                    <div>
                      <div>Dr Ndiaye Mamadou</div>
                      <div className="text-emerald-100">ğŸ¥ Clinique Dakar</div>
                    </div>
                  </div>
                  <div className="flex flex-wrap gap-4 items-center text-sm">
                    <span>ğŸ“ +221 77 123 4567</span>
                    <span>ğŸ©º MÃ©decine GÃ©nÃ©rale</span>
                    <span className="px-4 py-2 bg-white/30 rounded-xl font-bold">âœ… En ligne</span>
                  </div>
                </div>
              </div>
            </div>
            
            <motion.div 
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              className="text-right"
            >
              <div className="text-5xl md:text-6xl font-black text-emerald-100 drop-shadow-lg">
                {stats.revenus.toLocaleString()} FCFA
              </div>
              <div className="text-xl opacity-90">Consultations ce mois</div>
            </motion.div>
          </div>
        </motion.div>

        {/* NAVIGATION TABS */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="backdrop-blur-xl bg-white/15 border-4 border-white/30 rounded-3xl shadow-2xl shadow-black/30 flex flex-wrap gap-4 p-6 justify-center hover:shadow-3xl transition-all duration-500"
        >
          <AnimatePresence mode="wait">
            {tabs.map((tab, index) => (
              <TabButton
                key={tab.id}
                label={tab.label}
                icon={tab.icon}
                active={activeTab === tab.id}
                onClick={() => setActiveTab(tab.id)}
                layout
                initial={{ opacity: 0, scale: 0.8, y: 20 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{ delay: index * 0.05 }}
              />
            ))}
          </AnimatePresence>
        </motion.div>

        {/* STATS CARDS */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6"
        >
          <StatBox title="ğŸ‘¥ Mes Patients" value={stats.patients.toString()} color="from-emerald-400 to-green-400" />
          <StatBox title="ğŸ’Š Ordonnances" value={stats.ordonnances.toString()} color="from-blue-400 to-indigo-500" />
          <StatBox title="ğŸ“… RDV Aujourd'hui" value={stats.rdvsToday.toString()} color="from-amber-400 to-orange-500" />
          <StatBox title="ğŸ’° Revenus" value={stats.revenus.toLocaleString()} color="from-teal-400 to-emerald-500" />
        </motion.div>

        {/* DYNAMIC CONTENT */}
        <AnimatePresence mode="wait">
          <motion.div
            key={activeTab}
            initial={{ opacity: 0, scale: 0.95, y: 30 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.95, y: -30 }}
            transition={{ duration: 0.4 }}
            className="backdrop-blur-xl bg-white/15 border-4 border-white/30 rounded-3xl shadow-2xl shadow-black/30 p-8 md:p-12 hover:shadow-3xl transition-all duration-500"
          >
            {activeTab === 'ordonnances' && (
              <OrdonnancesSection
                ordonnance={ordonnance}
                setOrdonnance={setOrdonnance}
                addMedicament={addMedicament}
                updateMedicament={updateMedicament}
                removeMedicament={removeMedicament}
                selectPatient={selectPatient}
                saveOrdonnance={saveOrdonnance}
              />
            )}
            {activeTab === 'patients' && <PatientsSection patients={patients} />}
            {activeTab === 'rdv' && <RdvSection rdvs={rdvs} />}
            {activeTab === 'stats' && <StatsSection />}
          </motion.div>
        </AnimatePresence>
      </div>
    </div>
  );
};

// âœ… TOUS LES COMPOSANTS SANS DOUBLONS
const TabButton = ({ label, icon: Icon, active, onClick }) => (
  <motion.button
    onClick={onClick}
    whileHover={{ scale: 1.05 }}
