import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    patientEmail: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatientsList, setShowPatientsList] = useState(false);

  // ‚úÖ CHARGEMENT PATIENTS EXISTANTS
  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      const data = await getData('/patients');
      setPatients(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Erreur chargement patients:', err);
    }
  };

  // ‚úÖ S√âLECTION PATIENT EXISTANT (infos affich√©es seulement)
  const selectPatient = (patient) => {
    setFormData(prev => ({
      ...prev,
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom || '',
      patientEmail: patient.email || '',
      sexe: patient.sexe || 'M'
    }));
    setShowPatientsList(false);
  };

  // ‚úÖ CHAMP M√âDICAUX UNIQUEMENT
  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ ENVOI - SEULEMENT les champs backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId) {
      alert('‚ùå S√©lectionnez un patient existant');
      return;
    }

    setLoading(true);
    try {
      // ‚úÖ UNIQUEMENT ce que le backend attend
      const dossierData = {
        patientId: formData.patientId,
        groupeSanguin: formData.groupeSanguin || '',
        resumeMedical: formData.resumeMedical || '',
        allergies: formData.allergies || ''
      };

      console.log('üì§ Envoi backend:', dossierData);
      await postData('/dossier', dossierData);
      alert('‚úÖ Dossier cr√©√© avec succ√®s !');
      onSuccess();
    } catch (err) {
      console.error('Erreur cr√©ation:', err);
      alert('‚ùå Erreur: ' + (err.message || 'Probl√®me cr√©ation dossier'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center',
      zIndex: 10000
    }}>
      <div style={{
        background: 'white', padding: '2.5rem', borderRadius: '16px', maxWidth: '700px',
        width: '90%', maxHeight: '90vh', overflowY: 'auto', boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier Patient</h2>
          <button onClick={onClose} style={{
            background: 'none', border: 'none', fontSize: '2rem', cursor: 'pointer', color: '#666'
          }}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* 1. S√âLECTION PATIENT OBLIGATOIRE */}
          <div style={{ marginBottom: '1.5rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientsList(!showPatientsList)}
              style={{
                width: '100%', padding: '1rem 1.2rem', background: '#2196f3', color: 'white',
                border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer',
                display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.8rem'
              }}
            >
              üë§ {formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'S√©lectionner un patient *'}
            </button>
          </div>

          {/* LISTE PATIENTS */}
          {showPatientsList && (
            <div style={{ 
              maxHeight: '200px', overflowY: 'auto', 
              border: '2px solid #e0e0e0', borderRadius: '8px', marginBottom: '1.5rem',
              background: '#f8f9fa'
            }}>
              {patients.length === 0 ? (
                <div style={{ padding: '1.5rem', textAlign: 'center', color: '#666' }}>
                  üë§ Aucun patient trouv√©
                  <br /><small>Cr√©ez d'abord un compte patient</small>
                </div>
              ) : (
                patients.map(patient => (
                  <div 
                    key={patient._id}
                    onClick={() => selectPatient(patient)}
                    style={{
                      padding: '1.2rem', cursor: 'pointer', borderBottom: '1px solid #eee',
                      display: 'flex', alignItems: 'center', gap: '1rem',
                      background: formData.patientId === patient._id ? '#e3f2fd' : 'transparent',
                      transition: 'background 0.2s'
                    }}
                  >
                    <div style={{ 
                      fontWeight: 'bold', fontSize: '1.1rem', 
                      color: formData.patientId === patient._id ? '#1976d2' : '#333'
                    }}>
                      {patient.fullName || patient.nom || 'N/A'}
                    </div>
                    <div style={{ color: '#666', fontSize: '0.9rem', flex: 1 }}>
                      {patient.email}
                    </div>
                  </div>
                ))
              )}
            </div>
          )}

          {/* PATIENT S√âLECTIONN√â */}
          {formData.patientId && (
            <div style={{ 
              padding: '1.2rem', background: '#e8f5e8', borderRadius: '8px', marginBottom: '1.5rem',
              borderLeft: '4px solid #4caf50', fontSize: '1rem'
            }}>
              ‚úÖ <strong>Patient s√©lectionn√©:</strong> {formData.patientFullName} 
              <span style={{ color: '#666' }}> | {formData.patientEmail}</span>
            </div>
          )}

          {/* 2. CHAMPS M√âDICAUX (optionnels) */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem', marginBottom: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
                ü©∏ Groupe sanguin
              </label>
              <select 
                name="groupeSanguin" 
                value={formData.groupeSanguin} 
                onChange={handleInputChange}
                style={inputStyle}
              >
                <option value="">S√©lectionner...</option>
                <option value="A+">A+</option>
                <option value="A-">A-</option>
                <option value="B+">B+</option>
                <option value="B-">B-</option>
                <option value="AB+">AB+</option>
                <option value="AB-">AB-</option>
                <option value="O+">O+</option>
                <option value="O-">O-</option>
              </select>
            </div>
            
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
                ‚öïÔ∏è Sexe
              </label>
              <input 
                value={formData.sexe === 'M' ? 'Masculin' : 'F√©minin'} 
                readOnly 
                style={{ ...inputStyle, background: '#f5f5f5', color: '#666' }}
              />
            </div>
          </div>

          {/* 3. R√âSUM√â M√âDICAL */}
          <div style={{ marginBottom: '1.5rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
              üìù R√©sum√© m√©dical
            </label>
            <textarea
              name="resumeMedical"
              placeholder="Ant√©c√©dents m√©dicaux, diagnostics actuels, traitements en cours..."
              value={formData.resumeMedical}
              onChange={handleInputChange}
              rows={4}
              style={{ ...inputStyle, width: '100%', resize: 'vertical', fontFamily: 'inherit' }}
            />
          </div>

          {/* 4. ALLERGIES */}
          <div style={{ marginBottom: '2rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#333' }}>
              ‚ö†Ô∏è Allergies / Intol√©rances
            </label>
            <textarea
              name="allergies"
              placeholder="Aucun connu, ou liste: p√©nicilline, arachides, fruits de mer..."
              value={formData.allergies}
              onChange={handleInputChange}
              rows={2}
              style={{ ...inputStyle, width: '100%', resize: 'vertical', fontFamily: 'inherit' }}
            />
          </div>

          {/* BOUTONS */}
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button 
              type="submit" 
              disabled={!formData.patientId || loading}
              style={{
                flex: 1, padding: '1.2rem 2rem', 
                background: formData.patientId ? 
                  'linear-gradient(135deg, #4caf50, #45a049)' : '#ccc',
                color: 'white', border: 'none', borderRadius: '12px', 
                fontSize: '1.1rem', fontWeight: 'bold', 
                cursor: formData.patientId ? 'pointer' : 'not-allowed',
                boxShadow: formData.patientId ? '0 4px 15px rgba(76,175,80,0.3)' : 'none'
              }}
            >
              {loading ? '‚è≥ Cr√©ation en cours...' : 'üíæ Cr√©er le Dossier'}
            </button>
            <button 
              type="button"
              onClick={onClose}
              style={{
                flex: 1, padding: '1.2rem 2rem', 
                background: 'linear-gradient(135deg, #f44336, #d32f2f)',
                color: 'white', border: 'none', borderRadius: '12px', 
                fontSize: '1.1rem', fontWeight: 'bold', cursor: 'pointer',
                boxShadow: '0 4px 15px rgba(244,67,54,0.3)'
              }}
            >
              ‚ùå Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  padding: '1rem 1.2rem', 
  border: '2px solid #e0e0e0', 
  borderRadius: '8px',
  fontSize: '1rem', 
  transition: 'border-color 0.3s, box-shadow 0.3s', 
  width: '100%',
  fontFamily: 'inherit'
};

export default CreateDossierModal;
